import{_ as n,c as a,o as t,ah as o}from"./chunks/framework.MgE-7MWY.js";const b=JSON.parse('{"title":"Анацит","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Анацит.md","filePath":"content/Бестиарий/Анацит.md"}'),s={name:"content/Бестиарий/Анацит.md"};function e(g,r,h,i,l,p){return t(),a("div",null,[...r[0]||(r[0]=[o('<hr><h1 id="анацит" tabindex="-1">Анацит <a class="header-anchor" href="#анацит" aria-label="Permalink to “Анацит”">​</a></h1><h2 id="анацит-крылобот-воитель" tabindex="-1">Анацит-крылобот [Воитель] <a class="header-anchor" href="#анацит-крылобот-воитель" aria-label="Permalink to “Анацит-крылобот [Воитель]”">​</a></h2><p><strong>КО ½</strong><br> Опыт 200</p><p>Нейтральная небольшая конструкция (технологический).<br><strong>Инициатива:</strong> +3; <strong>Восприятие:</strong> ночное зрение 60 футов, сумеречное зрение; <strong>Внимание</strong> +4.</p><h3 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 13<br> ЭКБ: 10; ККБ: 12<br><strong>Стойкость</strong> +0; <strong>Реакция</strong> +0; <strong>Воля</strong> –2.<br><strong>Невосприимчивость:</strong> невосприимчивости конструкций.<br><strong>Слабые стороны:</strong> зависимость от солнечного света.</p><h3 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 40 футов, полёт 40 футов (Экс, средняя манёвренность).<br><strong>В ближнем бою:</strong> укус +4 (1d6 + 2 КО).<br><strong>Дистанционная:</strong> лазерный луч +7 (1d4 О, шаг дистанции 60 футов; крит: поджигание 1d4).</p><p><strong>Атакующие способности:</strong> дребезг.</p><h3 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +2; <strong>Лов:</strong> +3; <strong>Вын:</strong> —; <strong>Инт:</strong> –5; <strong>Мдр:</strong> +1; <strong>Хар:</strong> –2.<br><strong>Навыки:</strong> Атлетика +9 (+17 при лазании), Скрытность +4.<br><strong>Языки:</strong> всеобщий (не может говорить); радиоволны 100 футов.<br><strong>Прочие способности:</strong> неживой.</p><h3 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая (Абаллон).<br><strong>Численность:</strong> одиночка или стайка (2–6).</p><h3 id="особые-способности" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности" aria-label="Permalink to “Особые способности”">​</a></h3><p><strong>Дребезг (Экс):</strong> в качестве основного действия анацит-крылобот может издать резкий дребезжащий звук. Все существа в радиусе 30 футов, кроме анацитов-крылоботов, должны пройти испытание Стойкости со СЛ 9 и при провале становятся дезориентированными на 1d3 раунда. Существо, прошедшее испытание (вне зависимости от успешности), невосприимчиво к дребезгу этого анацита-крылобота на следующие 24 часа.</p><p><strong>Зависимость от солнечного света (Экс):</strong> анациты — конструкции, работающие от солнечных батарей, хотя у них есть и резервное питание, обеспечивающее ограниченную функциональность не на свету. Анацит становится дезориентированным, когда находится в темноте.</p><p><strong>Радиоволны (Экс):</strong> анацитам доступна беспроводная связь, аналогичная телепатии, но позволяющая общаться только с другими существами, обладающими этой способностью, или с конструкциями с подтипом «технологический».</p><hr><h2 id="анацит-рабочии-специалист" tabindex="-1">Анацит-рабочий [Специалист] <a class="header-anchor" href="#анацит-рабочии-специалист" aria-label="Permalink to “Анацит-рабочий [Специалист]”">​</a></h2><p><strong>КО 7</strong><br> Опыт 3200</p><p>Принципиально нейтральная средняя конструкция (технологический).<br><strong>Инициатива:</strong> +2; <strong>Восприятие:</strong> ночное зрение 60 футов, сумеречное зрение; <strong>Внимание</strong> +19.</p><h3 id="защита-1" tabindex="-1">Защита <a class="header-anchor" href="#защита-1" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 100<br> ЭКБ: 19; ККБ: 20<br><strong>Стойкость</strong> +4; <strong>Реакция</strong> +4; <strong>Воля</strong> +8.<br><strong>Невосприимчивость:</strong> невосприимчивости конструкций.<br><strong>Слабые стороны:</strong> зависимость от солнечного света.</p><h3 id="атака-1" tabindex="-1">Атака <a class="header-anchor" href="#атака-1" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 40 футов.<br><strong>В ближнем бою:</strong> плазменный резак +16 (1d8 + 12 О).<br><strong>Дистанционная:</strong> электрический всплеск +14 (1d8 + 7 Э, шаг дистанции 60 футов).</p><h3 id="параметры-1" tabindex="-1">Параметры <a class="header-anchor" href="#параметры-1" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +5; <strong>Лов:</strong> +2; <strong>Вын:</strong> —; <strong>Инт:</strong> +4; <strong>Мдр:</strong> +0; <strong>Хар:</strong> +0.<br><strong>Навыки:</strong> Инженерное дело +19, Компьютеры +19, Пилотирование +14, Скрытность +14.<br><strong>Языки:</strong> всеобщий; радиоволны 100 футов.<br><strong>Прочие способности:</strong> неживой, реконфигурация.</p><h3 id="экология-1" tabindex="-1">Экология <a class="header-anchor" href="#экология-1" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая (Абаллон).<br><strong>Численность:</strong> одиночка, пара или бригада (3–8).</p><h3 id="особые-способности-1" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности-1" aria-label="Permalink to “Особые способности”">​</a></h3><p><strong>Зависимость от солнечного света (Экс):</strong> см. выше.<br><strong>Радиоволны (Экс):</strong> см. выше.</p><p><strong>Реконфигурация (Экс):</strong> анациты-рабочие способны адаптироваться и улучшать свою конструкцию. Каждый рабочий обладает встроенными способностями в количестве, равном его КО / 3 (минимум 1), выбираемыми из приведённого списка. Анацит-рабочий может заменить эти способности, потратив 1 час непрерывной работы для каждой замены, но для этого ему необходим доступ к подходящему рабочему месту. Каждая способность может быть выбрана только один раз, если не указано иное. Кроме перечисленных модификаций, могут существовать и другие.</p><ul><li>Улучшенные гусеницы, увеличивающие базовую скорость до 60 футов.</li><li>Сенсоры, дающие слепозрение (вибрации) на расстоянии до 120 футов.</li><li>Удлинённые конечности, увеличивающие зону досягаемости на 5 футов.</li><li>Модифицированное шасси, дающее скорость лазания, плавания или рыта, равную базовой. Эту способность можно взять несколько раз, при этом её эффекты не складываются: каждый раз вы выбираете новый тип перемещения.</li><li>Защищённые системы, дающие устойчивость 10 к одному типу энергии (звуку, кислоте, огню, холоду или электричеству). Эту способность можно взять несколько раз, при этом её эффекты не складываются: каждый раз вы выбираете новый тип энергии.</li><li>Бронепластины, увеличивающие КБ на 2.</li></ul><hr><p>Анациты населяют Абаллон, ближайший к Солнцу мир Соглашения. Это раса машин, тысячи лет назад оставленная исчезнувшими хозяевами и с тех пор развившая в себе способности к эволюции и самосовершенствованию, создав целую механическую экосистему.</p><p>Внешне большинство анацитов напоминают членистоногих, сделанных из серебристого металла: у них множество ног для более эффективного перемещения и клешни или манипуляторы для выполнения полученных задач. Впрочем, внешность зависит от роли: анацит-шахтёр размером примерно с бульдозер и выглядит соответствующе, а парящий в воздухе электронный мозг предназначен для решения сложных задач. Даже у насекомоподобных анацитов обычно есть пара разных модификаций, и практически каждый анацит способен частично реконфигурироваться, чтобы адаптироваться к обстоятельствам.</p><p>С момента отбытия так называемых «Первенцев» минули неисчислимые тысячелетия, но анациты не сидели без дела. Две основные группы этих существ, Ждущие и Становящиеся, по-разному видят своё предназначение, но сходятся между ними гораздо больше, чем различий. Пока они ждут возвращения Первенцев или стремятся пойти по их стопам, все анациты ни на секунду не прекращают увеличивать своё богатство и влияние на пути к достижению цели. Несмотря на то, что «анаци́тами» являются только разумные абаллонские машины, способные обучаться и принимать участие в общественной жизни, многие выходцы с иных миров называют так всё многообразие механической жизни планеты. Стереозолободные крылоботы служат ярким примером абаллонской биотехнологии. У этих искусственных существ отсутствует самосознание, но они размножаются и занимают собственную экологическую нишу. Четырёхфутовые крылоботы перелетают с уступа на уступ, расправив покрытые солнечными батареями крылья, чтобы напитаться солнечной энергией.</p><p>Крылоботы периодически нападают на инопланетян и других анацитов, защищая свою территорию.</p><hr><h2 id="аугментации-анацитов" tabindex="-1">Аугментации анацитов <a class="header-anchor" href="#аугментации-анацитов" aria-label="Permalink to “Аугментации анацитов”">​</a></h2><p>Многие анациты считают свои тела вершиной эволюции и стремятся помочь остальным достичь такого же совершенства, продавая аугментации, что приносит им внушительный доход.</p><h3 id="антитоксиновая-мембрана" tabindex="-1">Антитоксиновая мембрана <a class="header-anchor" href="#антитоксиновая-мембрана" aria-label="Permalink to “Антитоксиновая мембрана”">​</a></h3><p><strong>АУГМЕНТАЦИЯ: КИБЕРИМПЛАНТ</strong><br><strong>ЦЕНА:</strong> 3 000<br><strong>УРОВЕНЬ:</strong> 5</p><p>Антитоксиновая мембрана представляет собой колонию нанороботов, способную нейтрализовать некоторые токсины на молекулярном уровне. Мембрана даёт бонус +4 (ситуативный) к испытаниям против ядов и болезней типов «контакт» или «вдыхание».</p><hr><h3 id="голосовои-усилитель" tabindex="-1">Голосовой усилитель <a class="header-anchor" href="#голосовои-усилитель" aria-label="Permalink to “Голосовой усилитель”">​</a></h3><p><strong>АУГМЕНТАЦИЯ: КИБЕРИМПЛАНТ</strong><br><strong>ЦЕНА:</strong> 125<br><strong>УРОВЕНЬ:</strong> 1</p><p>Голосовой усилитель многократно повышает громкость вашего голоса. Вы можете включить или выключить усилитель в качестве быстрого действия. При включении усилитель даёт бонус +2 (ситуативный) к проверкам Запугивания и на 5 уменьшает СЛ проверок Внимания других существ, пытающихся услышать ваш голос.</p><hr><h3 id="коротковолновыи-трансивер" tabindex="-1">Коротковолновый трансивер <a class="header-anchor" href="#коротковолновыи-трансивер" aria-label="Permalink to “Коротковолновый трансивер”">​</a></h3><p><strong>АУГМЕНТАЦИЯ: КИБЕРИМПЛАНТ</strong><br><strong>ЦЕНА:</strong> 6 500<br><strong>УРОВЕНЬ:</strong> 7</p><p>Коротковолновый трансивер даёт вам способность радиоволны (см. стр. 6).</p>',53)])])}const c=n(s,[["render",e]]);export{b as __pageData,c as default};
