import{_ as a,c as n,o as t,ah as o}from"./chunks/framework.MgE-7MWY.js";const c=JSON.parse('{"title":"Апари [Воитель]","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Апари.md","filePath":"content/Бестиарий/Апари.md"}'),e={name:"content/Бестиарий/Апари.md"};function s(h,r,g,i,l,d){return t(),n("div",null,[...r[0]||(r[0]=[o('<h1 id="апари-воитель" tabindex="-1">Апари [Воитель] <a class="header-anchor" href="#апари-воитель" aria-label="Permalink to “Апари [Воитель]”">​</a></h1><p><strong>КО 7</strong><br> Опыт 3200</p><p>Нейтральный крупный вредитель.<br><strong>Инициатива:</strong> +2; <strong>Восприятие:</strong> ночное зрение 60 футов; <strong>Внимание</strong> +14.</p><h2 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h2><p>ПЗ: 105; ПР: 4<br> ЭКБ: 19; ККБ: 21<br><strong>Стойкость</strong> +11; <strong>Реакция</strong> +6; <strong>Воля</strong> +9.<br><strong>Защитные способности:</strong> изменчивость; <strong>Невосприимчивость:</strong> критические удары.</p><h2 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h2><p><strong>Скорость:</strong> 30 футов.<br><strong>В ближнем бою:</strong> удар когтями +17 (2d6 + 11 Л).<br><strong>Дистанционная:</strong> шип +14 (2d8 + 7 Кб).<br><strong>Занимаемое пространство:</strong> 10 футов.<br><strong>Зона досягаемости:</strong> 10 футов.<br><strong>Атакующие способности:</strong> порождение составляющих.</p><h2 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h2><p><strong>Сил:</strong> +4; <strong>Лов:</strong> +2; <strong>Вын:</strong> +5; <strong>Инт:</strong> —; <strong>Мдр:</strong> +0; <strong>Хар:</strong> +0.<br><strong>Навыки:</strong> Атлетика +19, Выживание +14, Запугивание +14.<br><strong>Прочие способности:</strong> неразумный.</p><h2 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h2><p><strong>Среда обитания:</strong> равнины с умеренным или тёплым климатом.<br><strong>Численность:</strong> одиночка.</p><h2 id="особые-способности" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности" aria-label="Permalink to “Особые способности”">​</a></h2><h3 id="изменчивость-экс" tabindex="-1">Изменчивость (Экс) <a class="header-anchor" href="#изменчивость-экс" aria-label="Permalink to “Изменчивость (Экс)”">​</a></h3><p>Практически каждый внутренний орган апари может быть в считаные мгновения восстановлен или заменён одной из многих составляющих, стремящихся занять требуемую роль.</p><h3 id="порождение-составляющих-экс" tabindex="-1">Порождение составляющих (Экс) <a class="header-anchor" href="#порождение-составляющих-экс" aria-label="Permalink to “Порождение составляющих (Экс)”">​</a></h3><p>Большинство апари держат несколько составляющих в полной готовности, чтобы они могли при необходимости защитить улей или, в крайнем случае, пожертвовать собой, чтобы дать апари возможность скрыться. В качестве сопутствующего действия апари может потратить 1 ПР и потерять 20 ПЗ, чтобы породить составляющую в одной из свободных соседних клеток. Апари может применять эту способность, только если у него осталось 40 или более ПЗ.</p><h3 id="шип-экс" tabindex="-1">Шип (Экс) <a class="header-anchor" href="#шип-экс" aria-label="Permalink to “Шип (Экс)”">​</a></h3><p>Шаг дистанции этой дистанционной атаки апари составляет 30 футов.</p><hr><h2 id="составляющая-апари" tabindex="-1">Составляющая Апари <a class="header-anchor" href="#составляющая-апари" aria-label="Permalink to “Составляющая Апари”">​</a></h2><p><strong>КО –</strong><br> Опыт —</p><p>Нейтральный маленький вредитель.<br><strong>Инициатива:</strong> +4; <strong>Восприятие:</strong> ночное зрение 60 футов; <strong>Внимание</strong> +7.</p><h3 id="защита-1" tabindex="-1">Защита <a class="header-anchor" href="#защита-1" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 20<br> ЭКБ: 13; ККБ: 15<br><strong>Стойкость</strong> +6; <strong>Реакция</strong> +4; <strong>Воля</strong> +1.<br><strong>Слабые стороны:</strong> зависимость от улья.</p><h3 id="атака-1" tabindex="-1">Атака <a class="header-anchor" href="#атака-1" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> полёт 30 футов (Экс, безупречная манёвренность).<br><strong>В ближнем бою:</strong> удар когтями +10 (1d6 + 4 Л).<br><strong>Атакующие способности:</strong> взаимозаменяемость.</p><h3 id="параметры-1" tabindex="-1">Параметры <a class="header-anchor" href="#параметры-1" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +2; <strong>Лов:</strong> +4; <strong>Вын:</strong> +1; <strong>Инт:</strong> —; <strong>Мдр:</strong> +0; <strong>Хар:</strong> +0.<br><strong>Навыки:</strong> Акробатика +12 (+20 при полёте), Выживание +7, Запугивание +7.<br><strong>Прочие способности:</strong> воссоединение, неразумный.</p><h3 id="экология-1" tabindex="-1">Экология <a class="header-anchor" href="#экология-1" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая суша.<br><strong>Численность:</strong> колония (10+ и 1 апари).</p><h3 id="особые-способности-1" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности-1" aria-label="Permalink to “Особые способности”">​</a></h3><h4 id="взаимозаменяемость-экс" tabindex="-1">Взаимозаменяемость (Экс) <a class="header-anchor" href="#взаимозаменяемость-экс" aria-label="Permalink to “Взаимозаменяемость (Экс)”">​</a></h4><p>Составляющая апари может изменять физиологию, подстраиваясь под слабые стороны противника. В качестве сопутствующего действия она может изменить тип кинетического урона, который наносит её удар когтями (дробящий, колющий или режущий).</p><h4 id="воссоединение-экс" tabindex="-1">Воссоединение (Экс) <a class="header-anchor" href="#воссоединение-экс" aria-label="Permalink to “Воссоединение (Экс)”">​</a></h4><p>В качестве основного действия составляющая, находящаяся рядом с апари, может вновь стать частью улья. Текущие ПЗ составляющей добавляются к ПЗ апари, и составляющая исчезает.</p><h4 id="зависимость-от-улья-экс" tabindex="-1">Зависимость от улья (Экс) <a class="header-anchor" href="#зависимость-от-улья-экс" aria-label="Permalink to “Зависимость от улья (Экс)”">​</a></h4><p>Составляющая апари не может добровольно отдалиться от породившего её апари более чем на 200 футов. Если она не по своей воле оказывается на большем расстоянии, то становится дезориентированной и одержимой единственной целью: вернуться к своему апари. После смерти апари составляющая погибает через 1 час (если за это время не найдёт другого апари).</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Апари — живой улей. Внешне он напоминает жука, но приводится в движение полчищами мелких насекомых, которые он приходится и королевкой, и жилищем. В каждом экзоскеле апари копошатся миллионы извивающихся серых личинок величиной с рисовое зёрнышко. Живая нервная система постоянно истощает потоки химических сигналов, регулирующих развитие личинок, которые принимают мириады форм, чтобы поддерживать как биологические функции самого улья, так и численность узкоспециализированных популяций обитающих в нём жуков. Апари встречаются по всей галактике, в мирах с самыми разными климатическими условиями, но пока что ни одному энтомологу миров Соглашения не удалось установить, на какой планете эти существа зародились впервые. Ископаемые остатки свидетельствуют, что их расселение произошло ещё до Разрыва. Согласно некоторым маргинальным теориям, апари — прародители Роя, но это крайне спорное предположение. Вопрос о том, как не разумным существам удалось переместиться из одной солнечной системы в другую, также остаётся открытым: некоторые учёные полагают, что их специально расселила раса космополитов, использовавшая их как скот, другие думают, что в перемещениях им помогли путешественники между планами (скорее всего — насекомоподобные заклинатели, живущие в Стержне), третьи же придерживаются мнения, что это потомки намеренно выродившейся космической расы, которая предпочла деградировать и стать неразумной, нежели столкнуться с экзистенциальной дилеммой или грозящей полным истреблением напастью.</p><p>Когда апари попадают в новую экосистему, они очень быстро становятся крайне опасны. Пока сам улей охотится на зверей, его составляющие могут искать иное пропитание (обычно перегной или падаль). Сочетание обоих методов — достаточно неприятное зрелище: апари раздирает добычу, а его составляющие устремляются в раны жертвы, чтобы пожрать её изнутри. Кроме этого, изменчивость апари позволяет уберечься от угроз, представляющих опасность для более оседлых общественных насекомых, например на водяной или попыток истребления со стороны разумного соперника.</p><p>Когда доступные ресурсы позволяют апари породить больше составляющих, чем его организм способен поддерживать, он перемещается в центр кормовой территории и некоторое время не двигается с места. Часть составляющих зарывается под ним в землю и переносит туда долю важнейших биологических систем апари-родителя, включая и половину его личиночного ядра, а остальные составляющие продолжают добывать пищу, чтобы снабдить зарождающийся улей необходимыми питательными веществами. Когда новый улей готов, оба апари делают текущие охотничьи угодья поровну между собой и принимаются расширять их в стороны от общего центра. При этом они не работают сообща, но и не конфликтуют напрямую. Более того, они поддерживают химическую связь, так что, если одного из них постигнет печальная участь, его уцелевшее составляющее вполне сможет вернуться во второй улей и продолжить жить там. Именно так суперколонии апари заполонили континенты и захватывали целые планеты. Несмотря на то, что апари можно найти на самых разных планетах, в том числе на Триаксе и населённой насекомыми луне Нчак, современные апари неспособны колонизировать новые миры без содействия разумных существ. К счастью для них, жареные личинки апари — излюбленное лакомство на многих мирах Соглашения, поэтому охота на апари и их разведение — дело хоть и опасное, но прибыльное.</p><hr><h2 id="встречи-с-составляющими" tabindex="-1">Встречи с составляющими <a class="header-anchor" href="#встречи-с-составляющими" aria-label="Permalink to “Встречи с составляющими”">​</a></h2><p>Герои могут повстречать отдельные составляющие апари, отделившиеся от улья в поисках пищи. Составляющие, порождённые апари в бою, не приносят персонажам опыта, но каждая самостоятельная составляющая — существо с КО 2 (600 опыта), которое может встретиться как в одиночку, так и группами от 2 до 4 особей.</p>',45)])])}const p=a(e,[["render",s]]);export{c as __pageData,p as default};
