import{_ as a,c as t,o as n,ah as o}from"./chunks/framework.MgE-7MWY.js";const c=JSON.parse('{"title":"Астерай [Заклинатель]","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Астерай.md","filePath":"content/Бестиарий/Астерай.md"}'),e={name:"content/Бестиарий/Астерай.md"};function s(i,r,l,h,g,d){return n(),t("div",null,[...r[0]||(r[0]=[o('<h1 id="астераи-заклинатель" tabindex="-1">Астерай [Заклинатель] <a class="header-anchor" href="#астераи-заклинатель" aria-label="Permalink to “Астерай [Заклинатель]”">​</a></h1><p><strong>КО 12</strong><br> Опыт 19 200</p><p>Хаотичная нейтральная средняя фея.<br><strong>Инициатива:</strong> +5; <strong>Восприятие:</strong> сумеречное зрение; <strong>Внимание</strong> +22 (+30 в космосе).</p><h2 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h2><p>ПЗ: 170<br> ЭКБ: 25; ККБ: 26<br><strong>Стойкость</strong> +13; <strong>Реакция</strong> +13; <strong>Воля</strong> +15.<br><strong>Невосприимчивость:</strong> вакуум, огонь, холод.</p><h2 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h2><p><strong>Скорость:</strong> 30 футов, полёт 60 футов (Св, безупречная манёвренность).<br><strong>В ближнем бою:</strong> удар хвостом +20 (2d12 + 13 Р).<br><strong>Дистанционная:</strong> выброс электричества +18 (2d8 + 12 Э).<br><strong>Занимаемое пространство:</strong> 5 футов.<br><strong>Зона досягаемости:</strong> 5 футов (10 футов для удара хвостом).<br><strong>Атакующие способности:</strong> сенсорная пень. <strong>Псевдозаклинания</strong> (УЗ 12; атака в ближнем бою +20):</p><ul><li>1 в день — замешательство (СЛ 23), системная перегрузка (СЛ 23);</li><li>3 в день — волшебное зрение, необнаружимость, приворот (чудовище) (СЛ 22), разрядка (СЛ 22);</li><li>Неограниченно — голограмма (2 круг, СЛ 21), паучьи лапы.</li></ul><h2 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h2><p><strong>Сил:</strong> –1; <strong>Лов:</strong> +5; <strong>Вын:</strong> +4; <strong>Инт:</strong> +2; <strong>Мдр:</strong> +3; <strong>Хар:</strong> +8.<br><strong>Навыки:</strong> Блеф +27, Культура +27, Скрытность +22.<br><strong>Языки:</strong> всеобщий, телепатия 300 футов.<br><strong>Прочие способности:</strong> недышащий, прилипала.</p><h2 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h2><p><strong>Среда обитания:</strong> любой вакуум.<br><strong>Численность:</strong> одиночка, пара или хор (3–12).</p><h2 id="особые-способности" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности" aria-label="Permalink to “Особые способности”">​</a></h2><h3 id="выброс-электричества-экс" tabindex="-1">Выброс электричества (Экс) <a class="header-anchor" href="#выброс-электричества-экс" aria-label="Permalink to “Выброс электричества (Экс)”">​</a></h3><p>В качестве атаки астерай может направить в одиночную цель выброс электричества (шаг дистанции 70 футов).</p><h3 id="прилипала-св" tabindex="-1">Прилипала (Св) <a class="header-anchor" href="#прилипала-св" aria-label="Permalink to “Прилипала (Св)”">​</a></h3><p>Коснувшись звездолёта, астерай может привязать себя к энергетическому следу, который корабль оставляет за собой. Это позволяет фее, находящейся в пределах 100 футов от корабля, перемещаться вместе с ним на аналогичной скорости, как если бы она находилась на его поверхности. Если звездолёт перемещается в Поток, астерай может либо последовать за ним, либо в качестве ответного действия отделиться и остаться позади.</p><h3 id="сенсорная-пень-экс" tabindex="-1">Сенсорная пень (Экс) <a class="header-anchor" href="#сенсорная-пень-экс" aria-label="Permalink to “Сенсорная пень (Экс)”">​</a></h3><p>Астерай может «напевать» электронные сигналы, способные маскировать или подменять получаемые сенсорами данные. В качестве основного действия астерай может создать поддельное изображение предмета, как если бы он сотворил голограмму 4 круга (УЗ 12). Подделка способна ввести в заблуждение только электронные сенсоры. Несколько астерая могут петь хором, при этом УЗ для определения области действия и дистанции заклинания увеличивается на 1 за каждого астерая после первого.</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Когда гуманоиды впервые заглянули в глубины галаронских морей и океанов, им повстречалось множество прекрасных и опасных созданий, обитавших среди волн и заманивших корабли на скалы прекрасным пением. Со временем галаронцы научились отличать игривых мерфолков от жестоких русалок, кровожадных сцилл и других подобных созданий и, благодаря этим знаниям, наносимый ущерб уменьшился. Отправившись в космос, гуманоиды вновь столкнулись с разнообразными загадочными космическими обитателями, угрожающими их звездолётам. Самой существенной из подобных угроз являются коварные астерай.</p><p>Астерай — раса космических фей, стройных ангелоподобных существ, парящих в солнечных ветрах среди космических обломков, астероидов и планетарных колец, игриво забавляющихся с пылью, танцующих в невесомости и жаждущих повидать новые красивые места. Тела этих существ состоят в основном из лёгких и гибких костей и мощных сухожилий, удерживающих их воедино. Выше пояса астерай отдалённо похожи на гуманоидов, а ниже пояса у них находится особый «хвост». Этот полупрозрачный «хвост», придающий фее изящный и безобидный вид, способен улавливать космическое излучение и позволяет перемещаться в безвоздушном пространстве. На лицах астерая отсутствуют какие-либо черты за исключением длинной и тонкой прорези рта и широких синих или зелёных глаз, выражающих весь спектр эмоций существа. Эти создания прекрасно приспособлены к жизни в космосе и обладают острым зрением, но без малейших зазрений совести съедят всё, что осталось на звездолёте после спешной эвакуации экипажа, включая тела погибших.</p><p>Первые астерай появились в наполненных магией звездолётных системах, где соприкасались космос и Первый мир. Тысячелетиями они были заперты в этих отдалённых уголках Вселенной, будучи не в состоянии добраться до населённых систем в отведённый им срок, но с появлением первых звездолётов ситуация изменилась. Астерай могут плыть по энергетическим следам, оставляемым звездолётами, прилепляясь к кораблям подобно прилипалам и преследуя схожие цели. В наше время колонии этих своенравных фей можно найти в большинстве обитаемых систем. Как правило, территория астерая расположена недалеко от основных космических маршрутов и включает в себя крупные, полные пещер астероиды, где астерай гнездятся и хранят сокровища. Здесь же находится своеобразное кладбище звездолётов, служащее угрозу столкновения, но феи либо маскируют их своими песнями, либо используют остовы в качестве приманки для жадных до сокровищ исследователей.</p><p>Астерай не злые, но их образ мысли и действия чужеродны для гуманоидов. Астерай прекрасно понимают, что большинство существ нуждается в воздухе, воде и пище, но собственный голод и тяга к развлечениям для них куда важнее. Их жестокость и жадность проистекают из чуждого нам менталитета и скуки, поэтому те, кому удалось развлечь астерая или найти с ними какой-никакой общий язык, могут рассчитывать на поддержку этих могущественных космических существ.</p><p>Длина астерая от макушки до кончика хвоста составляет 7,5 фута, но если он подожмёт или изогнёт хвост, то может показаться куда короче. Вес астерая в среднем составляет около 75 футов.</p>',26)])])}const b=a(e,[["render",s]]);export{c as __pageData,b as default};
