import{_ as r,c as d,o as a,ah as n}from"./chunks/framework.MgE-7MWY.js";const p=JSON.parse('{"title":"Вечная Гвардия [Воитель]","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Вечная гвардия.md","filePath":"content/Бестиарий/Вечная гвардия.md"}'),o={name:"content/Бестиарий/Вечная гвардия.md"};function s(e,t,h,g,l,i){return a(),d("div",null,[...t[0]||(t[0]=[n('<h1 id="вечная-гвардия-воитель" tabindex="-1">Вечная Гвардия [Воитель] <a class="header-anchor" href="#вечная-гвардия-воитель" aria-label="Permalink to “Вечная Гвардия [Воитель]”">​</a></h1><h2 id="солдат-вечнои-гвардии" tabindex="-1">Солдат Вечной Гвардии <a class="header-anchor" href="#солдат-вечнои-гвардии" aria-label="Permalink to “Солдат Вечной Гвардии”">​</a></h2><p><strong>КО 3</strong><br> Опыт 800</p><p>Алланти, солдат.<br> Принципиальный злой средний гуманоид (человек).<br><strong>Инициатива:</strong> +5; <strong>Восприятие:</strong> ночное зрение 60 футов; <strong>Внимание</strong> +8.</p><h3 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 48<br> ЭКБ: 19; ККБ: 22<br><strong>Стойкость</strong> +5; <strong>Реакция</strong> +3; <strong>Воля</strong> +4.</p><h3 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 20 футов.<br><strong>В ближнем бою:</strong> импульсная перчатка «Гром» +8 (1d6 + 5 Д, крит: нокдаун).<br><strong>Дистанционная:</strong> штурмовая винтовка ВГ +11 (1d8 + 3 Кб), или зажигательная граната I +11 (взрыв 1d6 О, поджигание 1d4, 5 футов, СЛ 14), или осколочная граната II +11 (взрыв 2d6 Кб, 15 футов, СЛ 14).</p><p><strong>Атакующие способности:</strong> боевой стиль (меткий стрелок), снайперская точность.</p><h3 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +2; <strong>Лов:</strong> +4; <strong>Вын:</strong> +1; <strong>Инт:</strong> +1; <strong>Мдр:</strong> +1; <strong>Хар:</strong> –1.<br><strong>Навыки:</strong> Атлетика +8, Запугивание +8, Профессия (солдат) +8, Скрытность +10.<br><strong>Языки:</strong> алланти.<br><strong>Снаряжение:</strong> боевой костюм рядового ВГ (камень вечных [хрустальное веретено], прыжковые двигатели), штурмовая винтовка ВГ с 4 магазинами (100 патронов для длинноствольного оружия), импульсная перчатка «Гром» с 2 аккумуляторами, зажигательная граната I, осколочная граната II.</p><h3 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая (Звёздная империя Алланти).<br><strong>Численность:</strong> огневая группа (3–6), отделение (7–12), штурмовое отделение (7–12 солдат Вечной гвардии и 1 специалист Вечной гвардии) или взвод (21–48 солдат Вечной гвардии и 1 специалист Вечной гвардии).</p><hr><h2 id="специалист-вечнои-гвардии-специалист" tabindex="-1">Специалист Вечной Гвардии [Специалист] <a class="header-anchor" href="#специалист-вечнои-гвардии-специалист" aria-label="Permalink to “Специалист Вечной Гвардии  [Специалист]”">​</a></h2><p><strong>КО 7</strong><br> Опыт 3 200</p><p>Алланти, оперативник.<br> Принципиальный злой средний гуманоид (человек).<br><strong>Инициатива:</strong> +8; <strong>Восприятие:</strong> ночное зрение 60 футов, слепое чутьё (эмоции) 60 футов, сумеречное зрение; <strong>Внимание</strong> +15.</p><h3 id="защита-1" tabindex="-1">Защита <a class="header-anchor" href="#защита-1" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 102<br> ЭКБ: 22; ККБ: 23<br><strong>Стойкость</strong> +9; <strong>Реакция</strong> +9; <strong>Воля</strong> +10; +2 против болезней и ядов.<br><strong>Защитные способности:</strong> невероятное проворство, уклонение, СУ 5/–.<br><strong>Устойчивость:</strong> кислота 5.</p><h3 id="атака-1" tabindex="-1">Атака <a class="header-anchor" href="#атака-1" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 40 футов, полёт 30 футов (реактивный ранец, средняя манёвренность).<br><strong>В ближнем бою:</strong> тактический нож +13 (2d4 + 9 Л).<br><strong>Дистанционная:</strong> акселераторная винтовка ВГ +15 (3d4 + 7 Кб) или лазерный пистолет «Корона» +15 (2d4 + 7 О, крит: поджигание 1d4).</p><p><strong>Атакующие способности:</strong> калечащий приём, обманная атака +4d8.</p><h3 id="параметры-1" tabindex="-1">Параметры <a class="header-anchor" href="#параметры-1" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +1; <strong>Лов:</strong> +5; <strong>Вын:</strong> +1; <strong>Инт:</strong> +4; <strong>Мдр:</strong> +2; <strong>Хар:</strong> +1.<br><strong>Навыки:</strong> Акробатика +20, Атлетика +15, Компьютеры +20, Пилотирование +15, Профессия (солдат) +15, Скрытность +20.<br><strong>Языки:</strong> алланти, всеобщий, 4 дополнительных языка.<br><strong>Прочие способности:</strong> оперативная уловка (маскировочное поле), специализация оперативника (призрак).</p><h3 id="снаряжение" tabindex="-1">Снаряжение <a class="header-anchor" href="#снаряжение" aria-label="Permalink to “Снаряжение”">​</a></h3><p>Броня спецназа ВГ (камни вечных [фиолетовая сфера, хрустальное веретено], отражающее покрытие, реактивный ранец, фильтрующий дыхательный аппарат), акселераторная винтовка ВГ, лазерный пистолет «Корона», тактический нож, удобный патронташ (200 патронов для длинноствольного оружия и 10 аккумуляторов).</p><h3 id="экология-1" tabindex="-1">Экология <a class="header-anchor" href="#экология-1" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая (Звёздная империя Алланти).<br><strong>Численность:</strong> одиночка, пара, штурмовое отделение (1 специалист Вечной гвардии и 7–12 солдат Вечной гвардии) или взвод (1 специалист Вечной гвардии и 21–48 солдат Вечной гвардии).</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Могущественная Звёздная империя Алланти удерживает подвластные системы при помощи вооружённых сил, которые делятся на два вида: Имперский флот и Вечную гвардию. Вечная гвардия — элитные пехотные войска Звёздной империи: они выступают в качестве десантных подразделений на звездолётах Имперского флота, защищают правительственные и военные объекты, подавляют беспорядки и восстания, а также идут в авангарде при захвате и оккупации новых планет Звёздной империи.</p><p>Солдаты Вечной гвардии приносят присягу верности Вечному трону и освобождаются от данной ими клятвы, лишь умерев во славу империи. В ряды гвардейцев принимают только людей, каждый из которых должен являть собой образцового представителя расы алланти.</p><p>Гвардейца легко узнать по броне, в которой используется магия легендарных имперских камней вечных, а также по кибернетическим и биотехнологическим аугментациям, которые многие солдаты получают в дополнение к обычному снаряжению. Блок параметров солдата Вечной гвардии относится к рядовым бойцам, которых можно повстречать на планетах Звёздной империи или её кораблях. Солдаты-специалисты Вечной гвардии способны без какой-либо поддержки действовать самостоятельно на протяжении недель или даже месяцев, выполняя секретные задания, включающие шпионаж, проникновение в ряды противника, разведку или саботаж.</p><hr><h2 id="броня-вечнои-гвардии" tabindex="-1">Броня Вечной Гвардии <a class="header-anchor" href="#броня-вечнои-гвардии" aria-label="Permalink to “Броня Вечной Гвардии”">​</a></h2><p>Стандартная броня элитных солдат Звёздной империи Алланти называется боевым костюмом Вечной гвардии и представляет собой комплект тяжёлой брони, состоящий из плотно подогнанных керамических и поликарбонатных пластин, покрытых энергоотражающим молекулярным составом. Броня спецназа, используемая специалистами Вечной гвардии, выглядит схоже, но при этом облегчена и не стесняет движений. Помимо мест под обычные улучшения, в каждом боевом костюме присутствуют специальные места для установки камней вечных. Размещение в них камня вечных позволяет носителю брони пользоваться способностями камня, как если бы он парил вокруг его головы. Гражданским лицам запрещается владеть боевыми костюмами Вечной гвардии — это преступление, карающееся смертной казнью.</p><p>Броня Вечной гвардии крайне практична, но её внешний вид предназначен для устрашения врага. Самый узнаваемый элемент брони — шлем с гладкой непрозрачной маской, скрывающий всё лицо. Благодаря им все гвардейцы похожи на жутковатых роботов и неотличимы друг от друга. Последнее идёт на пользу и самим солдатам: невозможность опознания товарищей не позволяет привязываться к ним, что делает гвардейца верным служителем трона. В масках шлемов рядовых бойцов часто бывает установлен один камень вечных, но у офицеров их может быть больше, хотя располагаться они могут и иным образом.</p><h2 id="броня-вечнои-гвардии-1" tabindex="-1">Броня Вечной Гвардии <a class="header-anchor" href="#броня-вечнои-гвардии-1" aria-label="Permalink to “Броня Вечной Гвардии”">​</a></h2><table tabindex="0"><thead><tr><th>МОДЕЛЬ БРОНИ</th><th>УРОВЕНЬ</th><th>ЦЕНА</th><th>ЭКБ</th><th>ККБ</th><th>МАКС.ЛВК.</th><th>ШТРАФ ЗА БРОНЮ</th><th>СКОРОСТЬ</th><th>УЛУЧШЕНИЯ</th><th>ВЕС</th></tr></thead><tbody><tr><td><strong>Лёгкая броня</strong></td><td></td><td></td><td></td><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td>Броня спецназа ВГ</td><td>7</td><td>7 500</td><td>+7</td><td>+8</td><td>+5</td><td>–1</td><td>—</td><td>3+2*</td><td>1</td></tr><tr><td><strong>Тяжёлая броня</strong></td><td></td><td></td><td></td><td></td><td></td><td></td><td></td><td></td><td></td></tr><tr><td>Боевой костюм рядового ВГ</td><td>3</td><td>1 650</td><td>+5</td><td>+7</td><td>+2</td><td>–3</td><td>–10 футов</td><td>1+1*</td><td>3</td></tr><tr><td>Боевой костюм командира ВГ</td><td>10</td><td>18 750</td><td>+15</td><td>+17</td><td>+3</td><td>–3</td><td>–10 футов</td><td>2+3*</td><td>3</td></tr></tbody></table><blockquote><p>*Места под улучшение, отмеченные звёздочкой, могут использоваться только для установки камней вечных.</p></blockquote><hr><h2 id="винтовки-вечнои-гвардии" tabindex="-1">Винтовки Вечной Гвардии <a class="header-anchor" href="#винтовки-вечнои-гвардии" aria-label="Permalink to “Винтовки Вечной Гвардии”">​</a></h2><p>Серия длинноствольного оружия «ВГ» производится на государственных предприятиях и предназначена исключительно для использования бойцами Вечной гвардии. Конструкторы оружия лишены свободы выбора благодаря пожизненным «контрактам», став окружёнными вниманием пленниками Вечного трона. Штурмовая винтовка ВГ — автоматическая винтовка с выбором режима огня, стандартное оснащение рядовых пехотинцев. В серию также входит акселераторная винтовка ВГ, подобно магнитной винтовке использующая для разгона металлического снаряда магнитные поля, а также реактивная винтовка (РВ, прозванная гвардейцами «ревуном»), которая стреляет мини-ракетами, аналогичными тем, что применяются в гирореактивных винтовках. Разведслужбы миров Соглашения полагают, что некоторые модели оружия из этой линейки могут быть оснащены камнями вечных, но неизвестно, как именно работает эта гибридная технология и какой эффект она оказывает. Гражданским лицам запрещается владеть любым подобным оружием — это преступление, за которое предусмотрена смертная казнь.</p><h2 id="винтовки-вечнои-гвардии-двуручное-длинноствольное-оружие" tabindex="-1">Винтовки Вечной Гвардии (Двуручное длинноствольное оружие) <a class="header-anchor" href="#винтовки-вечнои-гвардии-двуручное-длинноствольное-оружие" aria-label="Permalink to “Винтовки Вечной Гвардии (Двуручное длинноствольное оружие)”">​</a></h2><table tabindex="0"><thead><tr><th>ОГНЕСТРЕЛЬНОЕ ОРУЖИЕ</th><th>УРОВЕНЬ</th><th>ЦЕНА</th><th>УРОН</th><th>ДИСТАНЦИЯ</th><th>КРИТ.</th><th>ЁМКОСТЬ</th><th>РАСХОД</th><th>ВЕС</th><th>ОСОБЫЕ СВОЙСТВА</th></tr></thead><tbody><tr><td>Штурмовая винтовка ВГ</td><td>3</td><td>1 400</td><td>1d8 Ко</td><td>80 футов</td><td>—</td><td>12 патронов</td><td>1</td><td>1</td><td>Автоматическое</td></tr><tr><td>Акселераторная винтовка ВГ</td><td>7</td><td>7 500</td><td>3d4 Ко</td><td>60 футов</td><td>—</td><td>16 патронов</td><td>1</td><td>2</td><td>Автоматическое</td></tr><tr><td>РВ ВГ</td><td>10</td><td>21 000</td><td>2d12 Д</td><td>100 футов</td><td>Нокдаун</td><td>12 мини-ракет</td><td>1</td><td>2</td><td>—</td></tr></tbody></table><hr><h2 id="камни-вечных" tabindex="-1">Камни Вечных <a class="header-anchor" href="#камни-вечных" aria-label="Permalink to “Камни Вечных”">​</a></h2><p>Алланти часто используют камни вечных, а солдаты Вечной гвардии и вовсе устанавливают эти магические самоцветы на своё оружие и броню. Одним из самых часто применяемых камней является фиолетовая сфера, описанная ниже. Полные правила использования камней вечных приведены на стр. 222–223 «Основной книги правил».</p><h3 id="фиолетовая-сфера" tabindex="-1">Фиолетовая сфера <a class="header-anchor" href="#фиолетовая-сфера" aria-label="Permalink to “Фиолетовая сфера”">​</a></h3><p><strong>МАГИЧЕСКИЙ ПРЕДМЕТ</strong><br><strong>ЦЕНА:</strong> 7 500<br><strong>ВЕС:</strong> —<br><strong>УРОВЕНЬ:</strong> 7</p><p>Этот камень вечных создаёт вокруг носителя невидимое силовое поле, аналогичное фиолетовому силовому полю («Основная книга правил», стр. 205). У камня 10 зарядов, которые полностью восстанавливаются каждые 24 часа.</p>',51)])])}const c=r(o,[["render",s]]);export{p as __pageData,c as default};
