import{_ as n,c as t,o as a,ah as o}from"./chunks/framework.MgE-7MWY.js";const b=JSON.parse('{"title":"Дроу","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Дроу.md","filePath":"content/Бестиарий/Дроу.md"}'),s={name:"content/Бестиарий/Дроу.md"};function e(g,r,h,l,i,p){return a(),t("div",null,[...r[0]||(r[0]=[o('<h1 id="дроу" tabindex="-1">Дроу <a class="header-anchor" href="#дроу" aria-label="Permalink to “Дроу”">​</a></h1><h2 id="дроу-головорез" tabindex="-1">Дроу-Головорез <a class="header-anchor" href="#дроу-головорез" aria-label="Permalink to “Дроу-Головорез”">​</a></h2><p>[Воитель] <strong>КО 1</strong><br> Опыт 400</p><p>Дроу, солдат.<br> Хаотичный злой средний гуманоид (эльф).<br><strong>Инициатива:</strong> +4; <strong>Восприятие:</strong> ночное зрение 60 футов; <strong>Внимание</strong> +10.</p><h3 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 20<br> ЭКБ: 16; ККБ: 18<br><strong>Стойкость</strong> +3; <strong>Реакция</strong> +1; <strong>Воля</strong> +3; +2 против очарования.<br><strong>Невосприимчивость:</strong> сон; УхМ: 7.<br><strong>Слабые стороны:</strong> световая слепота.</p><h3 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 25 футов.<br><strong>В ближнем бою:</strong> стандартный боевой кнут +5 (1d4 + 2 Л).<br><strong>Дистанционная:</strong> лазерная винтовка «Азимут» +8 (1d8 + 1 О, крит: поджигание 1d6) или электрическая граната I +8 (взрыв 1d8 Э, 3,15 футов, СЛ 10).</p><p><strong>Атакующие способности:</strong> боевой стиль (рунический воин), руна рыцаря-колдуна, сотворение тьмы.<br><strong>Псевдозаклинания (УЗ 1):</strong></p><ul><li>Неограниченно — обнаружение магии, пляшущие огоньки.</li></ul><h3 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> –1; <strong>Лов:</strong> +4; <strong>Вын:</strong> –1; <strong>Инт:</strong> –1; <strong>Мдр:</strong> +0; <strong>Хар:</strong> +1.<br><strong>Навыки:</strong> Акробатика +10, Запугивание +5, Скрытность +5.<br><strong>Языки:</strong> всеобщий, дроу.<br><strong>Снаряжение:</strong> лащунтская «Кольчуга», лазерная винтовка «Азимут» с 4 аккумуляторами (20 зарядов в каждом), 2 электрические гранаты I, стандартный боевой кнут.</p><h3 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая (Апост).<br><strong>Численность:</strong> пара, патруль (3–4), делегация (5–8 и 1 высокорожденный дроу-оружейник) или батальон (10–40).</p><hr><h2 id="высокорожденныи-дроу-оружеиник" tabindex="-1">Высокорожденный Дроу-Оружейник <a class="header-anchor" href="#высокорожденныи-дроу-оружеиник" aria-label="Permalink to “Высокорожденный Дроу-Оружейник”">​</a></h2><p>[Специалист] <strong>КО 11</strong><br> Опыт 12 800</p><p>Дроу, посланник.<br> Хаотичный злой средний гуманоид (эльф).<br><strong>Инициатива:</strong> +3; <strong>Восприятие:</strong> ночное зрение 60 футов; <strong>Внимание</strong> +26.</p><h3 id="защита-1" tabindex="-1">Защита <a class="header-anchor" href="#защита-1" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 170; ПР: 6<br> ЭКБ: 26; ККБ: 28<br><strong>Стойкость</strong> +10; <strong>Реакция</strong> +12; <strong>Воля</strong> +14; +2 против очарования.<br><strong>Невосприимчивость:</strong> сон; УхМ: 22.<br><strong>Слабые стороны:</strong> световая слепота.</p><h3 id="атака-1" tabindex="-1">Атака <a class="header-anchor" href="#атака-1" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 30 футов.<br><strong>В ближнем бою:</strong> ультратонкий длинный меч +19 (4d8 + 12 Л).</p><h3 id="параметры-1" tabindex="-1">Параметры <a class="header-anchor" href="#параметры-1" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> –1; <strong>Лов:</strong> +3; <strong>Вын:</strong> –1; <strong>Инт:</strong> +5; <strong>Мдр:</strong> +2; <strong>Хар:</strong> +8.<br><strong>Навыки:</strong> Блеф +21, Дипломатия +26, Запугивание +26, Проницательность +26, Скрытность +21.<br><strong>Языки:</strong> акло, всеобщий, демонический, дроу, эоксийский.<br><strong>Прочие способности:</strong> импровизация посланника (быстрая обескураживающая провокация, в атаку!+, тайный умысел, шевелься!).<br><strong>Снаряжение:</strong> касатский микрокордовый костюм IV (белое силовое поле [15 ПЗ], электростатическое поле [тип 2]), низкочастотный звуковой пистолет с 4 аккумуляторами повышенной ёмкости (40 зарядов в каждом), ультратонкий длинный меч.</p><h3 id="экология-1" tabindex="-1">Экология <a class="header-anchor" href="#экология-1" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая (Апост).<br><strong>Численность:</strong> одиночка, пара или делегация (1 и 5–8 дроу-головорезов).</p><h3 id="особые-способности" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности" aria-label="Permalink to “Особые способности”">​</a></h3><h4 id="подсвечивающее-сияние-св" tabindex="-1">Подсвечивающее сияние (Св) <a class="header-anchor" href="#подсвечивающее-сияние-св" aria-label="Permalink to “Подсвечивающее сияние (Св)”">​</a></h4><p>В качестве основного действия высокорожденный дроу может заставить существо и предметы в области действия (взрыв радиусом 5 футов) излучать бледное свечение, подчёркивающее их силуэты. Все существа с линией видимости до него определяют его местоположение («Основная книга правил», стр. 260). Длительность эффекта равна 1 минуте за 1 КО высокорожденного дроу (11 минут для оружейника). Дистанция применения способности — 100 футов.</p><p><em>Если на такое существо действует невидимость или аналогичный эффект, все существа с линией видимости до него определяют его местоположение («Основная книга правил», стр. 260). Длительность эффекта равна 1 минуте за 1 КО высокорожденного дроу (11 минут для оружейника). Дистанция применения способности — 100 футов.</em></p><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Дроу белокуры, фиолетовожоки, красивы и абсолютно безжалостны. Обычные дроу составляют основу общества и вооружённых сил, а высокорожденные дроу правят ими. Общество дроу матриархально, поэтому идея мужчин незавиден, работа на один из благородных домов в качестве головореза — один из немногих вариантов, позволяющих вести пусть необязательно долгую, но более-менее комфортную жизнь.</p><p>В сражении дроу беспощадны и не брезгуют любой возможностью застать противника врасплох или заманить его туда, где у них будет преимущество. Кодекс чести и доблесть для дроу лишь пустой звук, а всё, что верит в подобные идеалы, — низшие существа, заслуживающие своей участи. Эти эльфы без каких-либо сожалений порабощают представителей других рас, заставляя их служить себе, используют их в качестве пушечного мяса при исследовании опасной местности и не брезгуют жертвовать ими, если это необходимо для отступления.</p><p>Экономика дроу основана преимущественно на добыче образцов различного оружия из недр планеты-корабля, которую они объявили своей, их изучении и последующей продаже. В мирах Соглашения дроу известны как поставщики лучшего и новейшего вооружения. Солдаты дроу специализируются на дистанционном оружии и обычно работают командами, чтобы прорвать оборону противника, но даже самый преданный снайпер без малейшего промедления воспользуется возможностью осуществить личную месть и в пылу сражения пристрелить соперника.</p><p>Некоторые дроу рождаются с особыми дарами и гораздо могущественнее обычных сородичей. Таких дроу называют высокорожденными. Они обычно появляются в семьях других высокорожденных дроу, принадлежащих к одному из правящих домов, благодаря использованию технологий генетической селекции, секреты которой тщательно охраняются. Тем не менее иногда высокорожденные дроу рождаются и в обычных семьях без какого-либо генетического вмешательства. Как только подобный дроу осознаёт свою исключительность, он пытается продвинуться по социальной лестнице, либо войдя в один из благородных домов, либо снискав личную славу. Благодаря своему могуществу высокорожденные дроу быстро растут в иерархии и получают признание за свои деяния. Не беда, если при этом общество недосчитается нескольких обычных дроу.</p><p>Наследница могущественного дома может стоять во главе одного из подразделений семейного предприятия по торговле оружием и путешествовать по мирам Соглашения и новым колониям в поисках покупателей, заинтересованных в приобретении новых технологий, которые её дом разрабатывает на основе реликвий, добытых из недр Апост. Некоторые наследницы укрепляют связь дома с демоническими покровителями и становятся могущественными мистиками, а другие обучаются искусству техномантов в надежде разгадать тайну портала в центре Врат Ночи («Основная книга правил», стр. 458–459).</p><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +2 Лов, +2 Хар, –2 Вын<br><strong>ПЗ:</strong> 4</p><p><strong>Размер и тип:</strong> дроу — гуманоиды среднего размера с подтипом «эльф».<br><strong>Ночное зрение:</strong> дроу видят в темноте на расстоянии до 60 футов.</p><p><strong>Магия дроу:</strong> дроу получают следующие псевдозаклинания:</p><ul><li>Неограниченно — обнаружение магии, пляшущие огоньки.<br> УЗ для этих эффектов равен уровню персонажа. Кроме этого, при определении соответствия требованиям считается, что дроу обладают чертой Малая экстрасенсорная сила; если дроу берёт черту Экстрасенсорная сила, он может добавить сверхъестественную способность подсвечивающее сияние к списку доступных псевдозаклинаний.</li></ul><p><strong>Невосприимчивость дроу:</strong> дроу невосприимчивы к эффектам магического сна и получают бонус +2 (раса) к испытаниям против заклинаний и эффектов очарования.</p><p><strong>Острые чувства:</strong> дроу получают +2 (раса) к проверкам Внимания.</p><p><strong>Световая слепота:</strong> дроу ослеплен на 1 раунд, когда попадает под действие яркого света, и становится растворенным до тех пор, пока не покинет область яркого света.</p>',44)])])}const c=n(s,[["render",e]]);export{b as __pageData,c as default};
