import{_ as a,c as n,o as t,ah as o}from"./chunks/framework.MgE-7MWY.js";const b=JSON.parse('{"title":"Кало","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Кало.md","filePath":"content/Бестиарий/Кало.md"}'),s={name:"content/Бестиарий/Кало.md"};function e(h,r,g,l,i,d){return t(),n("div",null,[...r[0]||(r[0]=[o('<h1 id="кало" tabindex="-1">Кало <a class="header-anchor" href="#кало" aria-label="Permalink to “Кало”">​</a></h1><h2 id="охотник-на-акул-воитель" tabindex="-1">Охотник на Акул [Воитель] <a class="header-anchor" href="#охотник-на-акул-воитель" aria-label="Permalink to “Охотник на Акул  [Воитель]”">​</a></h2><p><strong>КО 2</strong><br> Опыт 600</p><p>Кало, солдат.<br> Нейтральное доброе среднее гуманоидное чудовище (подводный).<br><strong>Инициатива:</strong> +4; <strong>Восприятие:</strong> слепозрение (звук) 60 футов, сумеречное зрение; <strong>Внимание</strong> +7.</p><h3 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 22<br> ЭКБ: 10; ККБ: 17<br><strong>Стойкость</strong> +1; <strong>Реакция</strong> +3; <strong>Воля</strong> +5.<br><strong>Устойчивость:</strong> холод 10.</p><h3 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 20 футов, плавание 50 футов.<br><strong>В ближнем бою:</strong> подводный тактический нож-звезда +8 (1d4 + 3 Кб).<br><strong>Дистанционная:</strong> подводная автоматическая винтовка +10 (1d6 + 2 Кб) или осколочная граната I +10 (взрыв 1d6 Кб, 15 футов, СЛ 11).</p><h3 id="атакующие-способности" tabindex="-1">Атакующие способности <a class="header-anchor" href="#атакующие-способности" aria-label="Permalink to “Атакующие способности”">​</a></h3><p>боевой стиль (коммандо), трёхмерная тактика.</p><h3 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> –1; <strong>Лов:</strong> +4; <strong>Вын:</strong> –1; <strong>Инт:</strong> +0; <strong>Мдр:</strong> +2; <strong>Хар:</strong> +0.<br><strong>Навыки:</strong> Атлетика +12 (+20 при плавании), Профессия (поэт) +7, Скрытность +12 (+16 в воде).<br><strong>Языки:</strong> всеобщий, кало.<br><strong>Черты:</strong> Уредительный выстрел.<br><strong>Снаряжение:</strong> броня «Флибустер» I, 2 осколочные гранаты I, подводная автоматическая винтовка с 50 патронами для длинноствольного оружия, подводный тактический нож-звезда.</p><h3 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая подводная (Кало-Махой).<br><strong>Численность:</strong> одиночка, пара или отряд (3–10).</p><h3 id="особые-способности" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности" aria-label="Permalink to “Особые способности”">​</a></h3><h4 id="трехмерная-тактика-экс" tabindex="-1">Трёхмерная тактика (Экс) <a class="header-anchor" href="#трехмерная-тактика-экс" aria-label="Permalink to “Трёхмерная тактика (Экс)”">​</a></h4><p>Охотники на акул обучены вести бой в трёх измерениях. Когда охотник на акул сражается под водой, в невесомости, в полёте и в любой другой ситуации, где может передвигаться не только в одной плоскости, он получает бонус +1 к проверкам атаки в каждом раунде, в котором перемещался, даже если это был осторожный шаг.</p><hr><h2 id="говорящии-с-глубинои-заклинатель" tabindex="-1">Говорящий-с-Глубиной [Заклинатель] <a class="header-anchor" href="#говорящии-с-глубинои-заклинатель" aria-label="Permalink to “Говорящий-с-Глубиной  [Заклинатель]”">​</a></h2><p><strong>КО 5</strong><br> Опыт 1 600</p><p>Кало, мистик.<br> Нейтральное доброе среднее гуманоидное чудовище (подводный).<br><strong>Инициатива:</strong> +3; <strong>Восприятие:</strong> слепозрение (звук) 60 футов, сумеречное зрение; <strong>Внимание</strong> +11.</p><h3 id="защита-1" tabindex="-1">Защита <a class="header-anchor" href="#защита-1" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 57<br> ЭКБ: 17; ККБ: 17<br><strong>Стойкость</strong> +3; <strong>Реакция</strong> +4; <strong>Воля</strong> +9.<br><strong>Устойчивость:</strong> холод 10.</p><h3 id="атака-1" tabindex="-1">Атака <a class="header-anchor" href="#атака-1" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 20 футов, плавание 50 футов.<br><strong>В ближнем бою:</strong> подводное тактическое копьё +8 (1d6 + 5 Кб).<br><strong>Дистанционная:</strong> подводный зеро-пистолет класса «Изморозь» +10 (1d6 + 5 Х, крит: ошеломление (СЛ 15)).</p><h3 id="атакующие-способности-1" tabindex="-1">Атакующие способности <a class="header-anchor" href="#атакующие-способности-1" aria-label="Permalink to “Атакующие способности”">​</a></h3><p>ползушие льяны (СЛ 15).</p><h3 id="псевдозаклинания-мистика-уз-5" tabindex="-1">Псевдозаклинания мистика (УЗ 5): <a class="header-anchor" href="#псевдозаклинания-мистика-уз-5" aria-label="Permalink to “Псевдозаклинания мистика (УЗ 5):”">​</a></h3><ul><li>Неограниченно — связь разумов.</li></ul><h3 id="известные-заклинания-мистика-уз-5" tabindex="-1">Известные заклинания мистика (УЗ 5): <a class="header-anchor" href="#известные-заклинания-мистика-уз-5" aria-label="Permalink to “Известные заклинания мистика (УЗ 5):”">​</a></h3><ul><li>2 круг (3 в день) — облако тумана, призыв существа.</li><li>1 круг (6 в день) — защитная оболочка, приказ (СЛ 16), удар по разуму (СЛ 16).</li><li>0 круг (неограниченно) — ступор (СЛ 15), телепатическое сообщение.</li></ul><h3 id="дарование-ксенодруид" tabindex="-1">Дарование: ксенодруид. <a class="header-anchor" href="#дарование-ксенодруид" aria-label="Permalink to “Дарование: ксенодруид.”">​</a></h3><h3 id="параметры-1" tabindex="-1">Параметры <a class="header-anchor" href="#параметры-1" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +0; <strong>Лов:</strong> +3; <strong>Вын:</strong> +1; <strong>Инт:</strong> +0; <strong>Мдр:</strong> +5; <strong>Хар:</strong> +2.<br><strong>Навыки:</strong> Выживание +11, Дипломатия +10, Мистицизм +10, Профессия (судья) +11, Скрытность +11 (+15 в воде).<br><strong>Языки:</strong> всеобщий, кало, разговор с животными.<br><strong>Снаряжение:</strong> простой лащунтский термокостюм, подводное тактическое копьё, подводный зеро-пистолет класса «Изморозь» с 3 аккумуляторами (20 зарядов в каждом).</p><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Люди обычно сравнивают кало с летучими мышами из-за того, что те имеют тонкие перепонки между руками и ногами. На самом же деле эти крылоподобные образования являются плавниками, благодаря которым кало изящно и легко мчатся сквозь ледяные воды Кало-Махой, одной из лун Бреседы.</p><p>Когда кало имеет сине-зелёный оттенок и места прозрачна, а их выпуклые светящиеся глаза могут двигаться и фокусироваться независимо друг от друга.</p><p>Кало крайне цивилизованные и в целом мирные, поэтому они стали первыми из жителей лун, получившими независимость в рамках миров Соглашения, и теперь многие из их подводных городов, окружённых кораллами, оборудованы атмосферными отсеками для сухопутных сановников и экспатриантов, а на поверхности льда, покрывающего мировой океан, расположено множество торговых поселений. Кало призраками скользят сквозь тёмные океанские глубины и ориентируются в основном за счёт эхолокации, однако их города освещены множеством разноцветных огней, символизирующим художественные традиции этой расы.</p><p>У большинства кало есть хобби, связанное с наукой или изящными искусствами. Они не очень привязаны к традициям, но глубоко уважают собственную историю и называют детей и организации в честь событий и деятелей древности. Члены военных подразделений, к примеру, могут называться «охотниками на акул» несмотря на то, что они занимаются совсем другими вещами и название давно устарело. Некоторые кало по-прежнему следуют традициям, в частности, говорящие-с-глубиной могут общаться с обитателями глубин и даже управлять ими. В современном обществе мудрецы играют не такую важную роль, как в древности, однако к ним обращаются как к арбитрам или посредникам в случае возникновения споров общественного и государственно-го характера.</p><p>На суше кало не столь быстры, но их воины славятся спокойствием и точностью, особенно в условиях нулевой гравитации и под водой. Они используют криоружие против других рас, полагаясь на то, что естественные устойчивости защитят их от шальных выстрелов союзников.</p><p>Рост типичного кало 5,5 фута, а вес — 100 футов.</p><h2 id="подводное-оружие" tabindex="-1">Подводное оружие <a class="header-anchor" href="#подводное-оружие" aria-label="Permalink to “Подводное оружие”">​</a></h2><p>Как и прочие жители водных миров, кало разработали собственные аналоги привычного нам оружия, предназначенные для использования под водой. Такое оружие обладает описанным ниже особым свойством и, как правило, стоит на 10% дороже.</p><p><strong>Подводное:</strong> при использовании под водой оружие с этим свойством игнорирует штраф –2 к проверкам атаки и наносит полный урон.</p><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +2 Лов, +2 Мдр, –2 Вын<br><strong>ПЗ:</strong> 2</p><p><strong>Размер и тип:</strong> кало — гуманоидные чудовища среднего размера с подтипом «подводный», поэтому могут дышать только в воде.</p><p><strong>Зрение кало:</strong> благодаря врождённым способностям к экологии и жизни в условиях недостаточного освещения кало получают слепозрение (звук) на расстоянии до 60 футов и сумеречное зрение.</p><p><strong>Незаметные пловцы:</strong> кало получают бонус +4 к проверкам Скрытности, пока находятся в воде.</p><p><strong>Перемещение кало:</strong> базовая скорость кало равна 20 футам, они получают скорость плавания 50 футов.</p><p><strong>Устойчивость к холоду:</strong> кало обитают в холодной воде и получают устойчивость к холоду 5.</p>',51)])])}const c=a(s,[["render",e]]);export{b as __pageData,c as default};
