import{_ as a,c as n,o as t,ah as o}from"./chunks/framework.MgE-7MWY.js";const b=JSON.parse('{"title":"Космогоблин","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Космогоблин.md","filePath":"content/Бестиарий/Космогоблин.md"}'),s={name:"content/Бестиарий/Космогоблин.md"};function e(h,r,g,i,l,d){return t(),n("div",null,[...r[0]||(r[0]=[o('<h1 id="космогоблин" tabindex="-1">Космогоблин <a class="header-anchor" href="#космогоблин" aria-label="Permalink to “Космогоблин”">​</a></h1><h2 id="космогоблин-убиватор-специалист" tabindex="-1">Космогоблин-Убиватор [Специалист] <a class="header-anchor" href="#космогоблин-убиватор-специалист" aria-label="Permalink to “Космогоблин-Убиватор  [Специалист]”">​</a></h2><p><strong>КО ⅓</strong><br> Опыт 135</p><p>Нейтральный злой небольшой гуманоид (гоблиноид).<br><strong>Инициатива:</strong> +3; <strong>Восприятие:</strong> ночное зрение 60 футов; <strong>Внимание</strong> +3.</p><h3 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 6<br> ЭКБ: 11; ККБ: 12<br><strong>Стойкость</strong> +0; <strong>Реакция</strong> +2; <strong>Воля</strong> +2.</p><h3 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 35 футов.<br><strong>В ближнем бою:</strong> собачий тесак +0 (1d4 Л).<br><strong>Дистанционная:</strong> нестабильный хламолазер +3 (1d4 О, крит: поджигание 1d4).</p><h3 id="атакующие-способности" tabindex="-1">Атакующие способности <a class="header-anchor" href="#атакующие-способности" aria-label="Permalink to “Атакующие способности”">​</a></h3><p>ломастер.</p><h3 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +0; <strong>Лов:</strong> +3; <strong>Вын:</strong> +0; <strong>Инт:</strong> +1; <strong>Мдр:</strong> +0; <strong>Хар:</strong> +0.<br><strong>Навыки:</strong> Выживание +3, Инженерное дело +7, Компьютеры +7, Скрытность +7.<br><strong>Языки:</strong> всеобщий, гоблинский.<br><strong>Снаряжение:</strong> порванный эстекский комбинезон, собачий тесак, хламолазер с 1 аккумулятором (20 зарядов).</p><h3 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая.<br><strong>Численность:</strong> банда (4–12) или племя (13+ и 13+ не участвующих в бою), 1 головарь с КО 2–3 и 5–6 инопланетных питомцев.</p><h3 id="особые-способности" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности" aria-label="Permalink to “Особые способности”">​</a></h3><h4 id="ломастер-экс" tabindex="-1">Ломастер (Экс) <a class="header-anchor" href="#ломастер-экс" aria-label="Permalink to “Ломастер (Экс)”">​</a></h4><p>В качестве сопутствующего действия космогоблин может до начала своего следующего хода снять все штрафы, полученные одним предметом снаряжения из-за поломки. После этого предмет нельзя использовать в течение 10 минут (и он остаётся сломанным до тех пор, пока не будет починен).</p><h4 id="нестабильныи-хламолазер-экс" tabindex="-1">Нестабильный хламолазер (Экс) <a class="header-anchor" href="#нестабильныи-хламолазер-экс" aria-label="Permalink to “Нестабильный хламолазер (Экс)”">​</a></h4><p>Лазерный пистолет космогоблина представляет собой мешанину кусков пластика, подтекающих энергоячеек и тому подобного мусора. В остальном хламолазер аналогичен лазерному пистолету «Азимут», но его шаг дистанции всего 60 футов. Если при проверке атаки хламолазером выпадает натуральная 1, атакующий должен немедленно совершить проверку Инженерного дела со СЛ 18. При успехе хламолазер ломается. При провале оружие взрывается через 1d3 – 1 раунд, аналогично фугасной гранате I (взрыв 1d6 Д, 20 футов, СЛ 10); результат 0 раундов означает, что хламолазер взрывается сразу же, — случайным образом определите угол клетки владельца, который станет центром взрыва. При метании шаг дистанции хламолазера аналогичен таковому у гранаты.</p><hr><h2 id="космогоблин-головарь" tabindex="-1">Космогоблин-Головарь <a class="header-anchor" href="#космогоблин-головарь" aria-label="Permalink to “Космогоблин-Головарь”">​</a></h2><p><strong>КО 2</strong><br> Опыт 600</p><p>Нейтральный злой небольшой гуманоид (гоблиноид).<br><strong>Инициатива:</strong> +4; <strong>Восприятие:</strong> ночное зрение 60 футов; <strong>Внимание</strong> +7.</p><h3 id="защита-1" tabindex="-1">Защита <a class="header-anchor" href="#защита-1" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 24<br> ЭКБ: 14; ККБ: 15<br><strong>Стойкость</strong> +1; <strong>Реакция</strong> +3; <strong>Воля</strong> +5.</p><h3 id="атака-1" tabindex="-1">Атака <a class="header-anchor" href="#атака-1" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 35 футов.<br><strong>В ближнем бою:</strong> собачий тесак +6 (1d4 + 2 Л).<br><strong>Дистанционная:</strong> нестабильный хламолазер +9 (1d4 + 2 О, крит: поджигание 1d4).</p><h3 id="атакующие-способности-1" tabindex="-1">Атакующие способности <a class="header-anchor" href="#атакующие-способности-1" aria-label="Permalink to “Атакующие способности”">​</a></h3><p>ломастер, раздражающий визг.</p><h3 id="параметры-1" tabindex="-1">Параметры <a class="header-anchor" href="#параметры-1" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +0; <strong>Лов:</strong> +4; <strong>Вын:</strong> +1; <strong>Инт:</strong> +2; <strong>Мдр:</strong> +0; <strong>Хар:</strong> +0.<br><strong>Навыки:</strong> Запугивание +12, Компьютеры +12, Пилотирование +7, Скрытность +12.<br><strong>Языки:</strong> всеобщий, гоблинский.<br><strong>Снаряжение:</strong> порванный эстекский комбинезон, собачий тесак, хламолазер с 2 аккумуляторами (20 зарядов в каждом).</p><h3 id="экология-1" tabindex="-1">Экология <a class="header-anchor" href="#экология-1" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая.<br><strong>Численность:</strong> племя (1 и 13+ убиваторов, 13+ не участвующих в бою, 5–6 инопланетных питомцев).</p><h3 id="особые-способности-1" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности-1" aria-label="Permalink to “Особые способности”">​</a></h3><h4 id="раздражающии-визг-экс" tabindex="-1">Раздражающий визг (Экс) <a class="header-anchor" href="#раздражающии-визг-экс" aria-label="Permalink to “Раздражающий визг (Экс)”">​</a></h4><p>В качестве основного действия космогоблин-головарь может издать мерзкий визг, нервирующий всех в радиусе 30 футов, кроме гоблиноидов. Каждое существо, провалившее испытание Воли со СЛ 13, становится сбитым с цели на 1d4 раунда. Существо, прошедшее испытание, становится невосприимчивым к этому эффекту на 24 часа. Этот эффект с дескрипторами «восприятие» и «разум».</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>В гоблинских легендах говорится, что когда-то давным-давно племя очень смышлённых гоблинов пробралось на улетавший с Голарио-на звездолёт и попало на станцию «Авессалом». Оказавшись на космической станции, гоблины быстро проникли в самые захудалые её районы и поселились в инженерных тоннелях. Со временем гоблины научились делать оружие и броню из найденных запчастей, но результаты их экспериментов имеют обыкновение взрываться при первом удобном случае.</p><p>Космогоблины время от времени угнояли звездолёты и в результате расселились по галактике, но нигде их нет столько, сколько на «Авессаломе», к великому облегчению других миров, знакомых с ними не понаслышке.</p><p>Из-за крайней плодовитости этих существ с момента прибытия первых гоблинов на «Авессалом» сменилось уже множество поколений. Из-за гравитационных флуктуаций, совершенно нового рациона и периодических утечек радиации космогоблины существенно отличаются от гоблинов Голарио. Они чуть умнее и способны инстинктивно разбирать технологические устройства и собирать нечто соответствующее их странным представлениям о прекрасном. Кроме этого, они стали пошустрее и, ставну оставленный без присмотра лазерный пистолет или планшет, юрко скрываются в ближайшей вентиляционной шахте.</p><p>Некоторые космогоблины продолжают поклоняться древним гоблинским героям-богам, частично приспособив своё вероучение к текущему положению дел, но большинство почитает Триединого, бога машин. Из-за врождённой склонности к использованию технологий (без какого-либо понимания принципов их действия) они пришли к выводу, что у Триединого есть для них особое предназначение. Некоторые даже верят в то, что он приведёт их на райскую свалку, где они каждый день будут находить что-то новенькое, что можно разобрать на части. Пока что ни Триединый, ни его церковь официально не признают фанатичных космогоблинов последователями.</p><p>Голарионские гоблины славились прожорливостью, каждый день съедая столько же, сколько весят сами, притом в представлении гоблина едой могло быть вообще всё мало-малски съедобное. Космогоблинам на «Авессаломе» подобная роскошь могла только сниться, первое время они питались объедками и прочим мусором и в результате умерили аппетит, но по-прежнему тянут в рот всё, что не приколочено, — встретив незнакомый предмет, космогоблин с равной вероятностью попытается его либо съесть, либо разобрать. Если небольшой прибор сломался, то космогоблин вполне может искать поломку, засунув его в рот и хорошенько обмусолив.</p><p>Инстинктивная гоблинская ненависть и страх в отношении лошадей и собак также претерпели некоторые изменения. Теперь космогоблины называют любое неприятное им четвероногое существо (да и вообще что угодно похожей формы) «собакой» или «лошадью», в зависимости от размера. По этой же причине они называют своё грубое оружие ближнего боя собачьими тесаками; они схожи с охотничьими ножами, но некоторые предприимчивые племена научились делать микро- и ультрасеребраторные клинки. Ряд других племён предпочитает огненные винтовки и пистолеты, которые они называют «коженогами».</p><p>В анатомическом плане космогоблины несильно отличаются от голарионских сородичей (которых никто не видел уже несколько сотен лет). Космогоблины на несколько дюймов выше, а их красные глаза чуть темнее. Рост гоблина в среднем 3,5 фута, а вес — около 35 футов, причём основную его часть составляет голова.</p><hr><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +4 Лов, –2 Хар<br><strong>ПЗ:</strong> 2</p><p><strong>Размер и тип:</strong> космогоблины — гуманоиды небольшого размера с подтипом «гоблиноид».<br><strong>Ночное зрение:</strong> космогоблины видят в темноте на расстоянии до 60 футов.</p><p><strong>Быстрый:</strong> невзирая на размер, космогоблины очень быстры, их базовая скорость — 35 футов.</p><p><strong>Ломастер:</strong> см. стр. 46.</p><p><strong>Пронзья:</strong> космогоблины получают бонус +2 (раса) к проверкам Выживания, Инженерного дела и Скрытности.</p>',52)])])}const c=a(s,[["render",e]]);export{b as __pageData,c as default};
