import{_ as a,c as r,o as d,ah as e}from"./chunks/framework.MgE-7MWY.js";const b=JSON.parse('{"title":"Костоед [Воитель]","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Костоед.md","filePath":"content/Бестиарий/Костоед.md"}'),n={name:"content/Бестиарий/Костоед.md"};function o(s,t,h,i,l,g){return d(),r("div",null,[...t[0]||(t[0]=[e('<h1 id="костоед-воитель" tabindex="-1">Костоед [Воитель] <a class="header-anchor" href="#костоед-воитель" aria-label="Permalink to “Костоед [Воитель]”">​</a></h1><p><strong>КО 4</strong><br> Опыт 1 200</p><p>Нейтральный крупный волшебный зверь.<br><strong>Инициатива:</strong> +1; <strong>Восприятие:</strong> ночное зрение 60 футов, нюх на кости, сумеречное зрение; <strong>Внимание</strong> +10.</p><h2 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h2><p>ПЗ: 54<br> ЭКБ: 16; ККБ: 19<br><strong>Стойкость</strong> +8; <strong>Реакция</strong> +8; <strong>Воля</strong> +3.</p><h2 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h2><p><strong>Скорость:</strong> 60 футов.<br><strong>В ближнем бою:</strong> укус +13 (1d6 + 7 Ко и 1 Вын).<br><strong>Дистанционная:</strong> растворяющий плевок +10 (1 Вын).<br><strong>Занимаемое пространство:</strong> 10 футов.<br><strong>Зона досягаемости:</strong> 10 футов.<br><strong>Атакующие способности:</strong> растворение костей.</p><h2 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h2><p><strong>Сил:</strong> +3; <strong>Лов:</strong> +1; <strong>Вын:</strong> +3; <strong>Инт:</strong> –2; <strong>Мдр:</strong> +1; <strong>Хар:</strong> –1.<br><strong>Навыки:</strong> Выживание +10, Скрытность +15.<br><strong>Языки:</strong> касатский (не может говорить ни на одном языке).</p><h2 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h2><p><strong>Среда обитания:</strong> тёплые пустыни (Касат).<br><strong>Численность:</strong> одиночка, пара или прайд (3–6).</p><h2 id="особые-способности" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности" aria-label="Permalink to “Особые способности”">​</a></h2><h3 id="нюх-на-кости-экс" tabindex="-1">Нюх на кости (Экс) <a class="header-anchor" href="#нюх-на-кости-экс" aria-label="Permalink to “Нюх на кости (Экс)”">​</a></h3><p>Обоняние костоеда улавливает запахи необходимых ему минералов. Он может унюхать природные залежи кальция и у него слепое чутьё (запах) против существ, которые в течение последнего часа получили урон Выносливости от его атак.</p><h3 id="растворение-костеи-св" tabindex="-1">Растворение костей (Св) <a class="header-anchor" href="#растворение-костеи-св" aria-label="Permalink to “Растворение костей (Св)”">​</a></h3><p>При укусе костоед впрыскивает в жертву магический секрет, растворяющий минералы в её теле и разжижающий кости. Если атака укусом нанесла урон, она дополнительно наносит 1 урон Выносливости. Успешное испытание Стойкости со СЛ 13 нейтрализует эффект.</p><h3 id="растворяющии-плевок-св" tabindex="-1">Растворяющий плевок (Св) <a class="header-anchor" href="#растворяющии-плевок-св" aria-label="Permalink to “Растворяющий плевок (Св)”">​</a></h3><p>В качестве основного действия костоед может плюнуть в жертву растворяющим кости секретом, совершив дистанционную атаку против ККБ одиночной цели, находящейся в пределах 30 футов. При попадании жертва должна пройти испытание Стойкости со СЛ 13 и при провале получает 1 урон Выносливости.</p><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Костоеды, обитающие в пустынях и каменистых каньонах Касата, названы так неспроста. Основной рацион этих опасных хищников составляют кости, а при нападении на касат они, как правило, отрывают им голову и жадно пожирают кости черепа. Поглощая минералы, костоед отращивает на голове и спине крупные образования — нечто среднее между рогами и панцирем. Эти наросты состоят не только из кости и содержат кусочки отражающих материалов, образующие тысячи крошечных солнечных батарей, которые поставляют существу все остальные необходимые питательные вещества.</p><p>По мере взросления костоеда наросты приобретают всё более и более сложную форму, увеличиваясь в размерах и позволяя запасать минералы на чёрный день. Секрет, выделяемый костоедами, способен разжигать минералы и позволять зверю не только растворять кости добычи, но и добывать пропитание из окрестных скал.</p><p>У костоедов мускулистое тело и четыре толстые лапы с тупыми когтями, приспособленными для передвижения по камням и скалам. В задней части туловища расположена ещё одна пара конечностей, которые применяются не только для перемещения (совместно с передними лапами), что позволяет чудовищу свободно передвигаться в каменистой местности и лучше распределять вес на песчаных дюнах, но и для сдирания мяса с костей жертвы более острыми когтями. Благодаря особому строению зубов костоед может не только кусать добычу, но и впрыскивать в неё свой растворяющий секрет. Несмотря на внешнее сходство с рептилиями, эти существа во многом похожи на млекопитающих: они живородящи и обладают постоянной температурой тела.</p><p>С млекопитающими костоедов связывает не только схожая физиология, но и поведенческие особенности. Они крайне социальны и заботливы. Взрослые неродственные особи часто собираются в небольшие прайды, чтобы охотиться, выкапывать минералы и охранять друг друга во время отдыха. Нередки случаи, когда юный охотник, убивший одинокого костоеда, внезапно оказывался в окружении полудюжины его сородичей, дремавших до этого под песком, и погибал в неравной схватке. Самка костоеда обычно рожает одного или двух детёнышей и во время достаточно продолжительной беременности всё больше нуждается в пище и воде, поэтому целый прайд может охотиться и защищать источники воды, чтобы удовлетворить растущие потребности одной беременной самки. Костоеды могут расторгать кости с момента появления на свет, но в течение первого года жизни охотятся крайне осторожно, пока не начнут отращивать роговой панцирь.</p><p>Костоеды, выросшие рядом с другими существами, часто вступают с ними в симбиоз и потому нередко перемещаются в окружении небольшой толпы падальщиков, поддевающей остатки их пищи и очищающей щёки их наростов от паразитов. Особо крупные самцы становятся всё агрессивнее со временем и часто изгоняются из прайдов и теряют доступ к наиболее доступным источникам пищи. Несмотря на то, что расцвет сил этих одиноких уже миовал, они всё ещё крайне опасны, непредсказуемы и бесстрашно нападают на поселения в пустыне. Чтобы отогнать бушующее чудовище или победить его, иногда требуются совместные усилия сразу нескольких героев. Подобная схватка редко заканчивается без потерь и серьёзных ранений.</p><p>Древние касаты относились к костоедам со страхом и почтением, считая их ужасными посланниками смерти, в основном из-за привычки костоеда отрывать голову жертвы, не трогая тело. В наши дни костоедов по-прежнему боятся, но гораздо лучше изучили. Касатские учёные досконально исследовали уникальные кристаллы, выращиваемые этими существами: именно они легли в основу технологий касат, связанных с солнечной энергией. А некоторые другие расы разводили их в качестве охранных животных, экзотических питомцев или для охоты. Касаты, пустившиеся в долгое странствие на «Идарии», также взяли на борт популяцию этих существ, но они тщательно охраняются: слухи о том, что некоторым костоедам удалось сбежать и поселиться в вентиляционных шахтах корабля, считаются полным вымыслом.</p><p>Длина обычного костоеда до головы составляет 14 футов, высота в холке — 6 футов (без учёта высоты их ветвистых наростов), а вес — в среднем более 1000 футов.</p><h2 id="костяные-цестусы" tabindex="-1">Костяные Цестусы <a class="header-anchor" href="#костяные-цестусы" aria-label="Permalink to “Костяные Цестусы”">​</a></h2><p>Несколько столетий назад касаты создали из костяных наростов костоеда простое оружие — боевые перчатки-цестусы. Они служили знаком отличия для самых умелых касатских воинов, а в наши дни современные оружейники дали им вторую жизнь в качестве костяных цестусов, хотя теперь они изготавливаются из высококачественных полимеров, а не костей. Благодаря вкраплениям минералов костяные цестусы обычно достаточно тёплые на ощупь, особенно после того как побывают на солнечном свете. Это простое одноручное оружие ближнего боя способно не только пробить броню противника, но и повредить жизненно важные органы.</p><h2 id="костяные-цестусы-простое-одноручное-оружие-ближнего-боя" tabindex="-1">Костяные Цестусы (Простое одноручное оружие ближнего боя) <a class="header-anchor" href="#костяные-цестусы-простое-одноручное-оружие-ближнего-боя" aria-label="Permalink to “Костяные Цестусы (Простое одноручное оружие ближнего боя)”">​</a></h2><table tabindex="0"><thead><tr><th>ПРОЧЕЕ</th><th>УРОВЕНЬ</th><th>ЦЕНА</th><th>УРОН</th><th>КРИТ.</th><th>ВЕС</th><th>ОСОБЫЕ СВОЙСТВА</th></tr></thead><tbody><tr><td>Костяной цестус, простой</td><td>2</td><td>600</td><td>1d6 Ко</td><td>Кровотечение 1d4</td><td>Л</td><td>Аналоговое</td></tr><tr><td>Костяной цестус, обработанный</td><td>7</td><td>5 500</td><td>2d6 Ко</td><td>Кровотечение 1d6</td><td>Л</td><td>Аналоговое</td></tr><tr><td>Костяной цестус, внушительный</td><td>12</td><td>32 000</td><td>3d6 Ко</td><td>Кровотечение 2d6</td><td>Л</td><td>Аналоговое</td></tr><tr><td>Костяной цестус, грозный</td><td>17</td><td>230 000</td><td>8d6 Ко</td><td>Кровотечение 5d6</td><td>Л</td><td>Аналоговое</td></tr></tbody></table>',30)])])}const c=a(n,[["render",o]]);export{b as __pageData,c as default};
