import{_ as a,c as n,o as t,ah as o}from"./chunks/framework.MgE-7MWY.js";const p=JSON.parse('{"title":"Неразумная Нежить [Воитель]","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Неразумная Нежить.md","filePath":"content/Бестиарий/Неразумная Нежить.md"}'),s={name:"content/Бестиарий/Неразумная Нежить.md"};function e(g,r,h,i,l,b){return t(),n("div",null,[...r[0]||(r[0]=[o('<h1 id="неразумная-нежить-воитель" tabindex="-1">Неразумная Нежить [Воитель] <a class="header-anchor" href="#неразумная-нежить-воитель" aria-label="Permalink to “Неразумная Нежить [Воитель]”">​</a></h1><h2 id="скелет" tabindex="-1">Скелет <a class="header-anchor" href="#скелет" aria-label="Permalink to “Скелет”">​</a></h2><p><strong>КО ½</strong><br> Опыт 200</p><p>Нейтральная злая средняя нежить.<br><strong>Инициатива:</strong> +2; <strong>Восприятие:</strong> ночное зрение 60 футов; <strong>Внимание</strong> +4.</p><h3 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 13<br> ЭКБ: 10; ККБ: 12<br><strong>Стойкость</strong> +2; <strong>Реакция</strong> +2; <strong>Воля</strong> +2.</p><h3 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 30 футов.<br><strong>В ближнем бою:</strong> охотничий нож +6 (1d4 + 3 Л).<br><strong>Дистанционная:</strong> охотничья винтовка +3 (1d6 + 3 Кб).</p><h3 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +3; <strong>Лов:</strong> +2; <strong>Вын:</strong> –; <strong>Инт:</strong> –; <strong>Мдр:</strong> +1; <strong>Хар:</strong> +1.<br><strong>Навыки:</strong> Атлетика +9.<br><strong>Прочие способности:</strong> неживой, неразумный.<br><strong>Снаряжение:</strong> охотничья винтовка с 2 аккумуляторами (20 зарядов в каждом).</p><h3 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая.<br><strong>Численность:</strong> одиночка, пара или легион (9+).</p><hr><h2 id="оккультныи-зомби" tabindex="-1">Оккультный Зомби <a class="header-anchor" href="#оккультныи-зомби" aria-label="Permalink to “Оккультный Зомби”">​</a></h2><p><strong>КО 1</strong><br> Опыт 400</p><p>Нейтральная злая средняя нежить.<br><strong>Инициатива:</strong> +2; <strong>Восприятие:</strong> ночное зрение 60 футов; <strong>Внимание</strong> +5.</p><h3 id="защита-1" tabindex="-1">Защита <a class="header-anchor" href="#защита-1" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 24<br> ЭКБ: 11; ККБ: 13<br><strong>Стойкость</strong> +3; <strong>Реакция</strong> +3; <strong>Воля</strong> +3.<br><strong>Невосприимчивость:</strong> невосприимчивость нежити.<br><strong>Слабые стороны:</strong> шаткая походка.</p><h3 id="атака-1" tabindex="-1">Атака <a class="header-anchor" href="#атака-1" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 30 футов.<br><strong>В ближнем бою:</strong> крушащий удар +8 (1d6 + 5 Д).</p><h3 id="параметры-1" tabindex="-1">Параметры <a class="header-anchor" href="#параметры-1" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +4; <strong>Лов:</strong> +2; <strong>Вын:</strong> –; <strong>Инт:</strong> –; <strong>Мдр:</strong> +1; <strong>Хар:</strong> +0.<br><strong>Навыки:</strong> Атлетика +10.<br><strong>Прочие способности:</strong> неживой, неразумный.</p><h3 id="экология-1" tabindex="-1">Экология <a class="header-anchor" href="#экология-1" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая.<br><strong>Численность:</strong> одиночка, пара, стая (3–12) или орда (13+).</p><h3 id="особые-способности" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности" aria-label="Permalink to “Особые способности”">​</a></h3><h4 id="шаткая-походка-экс" tabindex="-1">Шаткая походка (Экс) <a class="header-anchor" href="#шаткая-походка-экс" aria-label="Permalink to “Шаткая походка (Экс)”">​</a></h4><p>Оккультный зомби считается постоянно ошеломлённым и в каждом раунде может совершить либо одно сопутствующее, либо одно основное действие. Действия полного хода такой зомби совершать не может.</p><hr><h2 id="кибернетическии-зомби" tabindex="-1">Кибернетический Зомби <a class="header-anchor" href="#кибернетическии-зомби" aria-label="Permalink to “Кибернетический Зомби”">​</a></h2><p><strong>КО 3</strong><br> Опыт 800</p><p>Нейтральная средняя нежить.<br><strong>Инициатива:</strong> +4; <strong>Восприятие:</strong> ночное зрение 60 футов; <strong>Внимание</strong> +8.</p><h3 id="защита-2" tabindex="-1">Защита <a class="header-anchor" href="#защита-2" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 40<br> ЭКБ: 14; ККБ: 16<br><strong>Стойкость</strong> +5; <strong>Реакция</strong> +5; <strong>Воля</strong> +4.<br><strong>Невосприимчивость:</strong> невосприимчивость нежити.<br><strong>Слабые стороны:</strong> уязвимость к электричеству.</p><h3 id="атака-2" tabindex="-1">Атака <a class="header-anchor" href="#атака-2" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 30 футов.<br><strong>В ближнем бою:</strong> крушащий удар +8 (1d6 + 5 Д).<br><strong>Дистанционная:</strong> встроенный дуговой пистолет «Искра» +11 (1d6 + 3; 3; крит: дуга 2).</p><h3 id="атакующие-способности" tabindex="-1">Атакующие способности <a class="header-anchor" href="#атакующие-способности" aria-label="Permalink to “Атакующие способности”">​</a></h3><p>самоликвидация (1d6 + 3, СЛ 12).</p><h3 id="параметры-2" tabindex="-1">Параметры <a class="header-anchor" href="#параметры-2" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +2; <strong>Лов:</strong> +4; <strong>Вын:</strong> –; <strong>Инт:</strong> –; <strong>Мдр:</strong> +1; <strong>Хар:</strong> +1.<br><strong>Навыки:</strong> Атлетика +13.<br><strong>Прочие способности:</strong> неживой, неразумный.</p><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Неразумную нежить можно создать из трупов любых существ, но в рассказах и легендах разных рас в роли нежити выступают как раз восставшие из мёртвых представители этой расы.</p><p>Гуманоиды рассказывают о захороненных трупах, поднимающихся из мест упокоения, а у аббераций, драконов и волшебных зверей свои собственные истории о представителях их вида, вернувшихся с того света, чтобы нести смерть живым.</p><p>Вне зависимости от первоначального вида нежити, она обычно сохраняет все естественные атаки и физические характеристики, бывшие у неё при жизни, но становится неразумной и теряет возможность действовать тактически и выполнять сложные или требующие скрупулёзности задачи, творить заклинания и совершать прочие осмысленные действия.</p><p>Из-за отсутствия разума мертвецам неведомы ни страх, ни жалость, что делает их лишь опаснее.</p><p>Ниже приведены шаблоны, которые можно использовать для создания неразумной нежити.</p><hr><h2 id="базовыи-шаблон-кибернетического-зомби" tabindex="-1">Базовый шаблон Кибернетического Зомби <a class="header-anchor" href="#базовыи-шаблон-кибернетического-зомби" aria-label="Permalink to “Базовый шаблон Кибернетического Зомби”">​</a></h2><p>Эта неразумная нежить оживлена не сверхъестественными силами и не магией, а встроенными в тело киберимпланта-ми, продолжающими работать даже после прекращения всех физиологических процессов.</p><p><strong>Требуемый тип существа:</strong> нежить.<br><strong>Требуемая роль:</strong> воитель.<br><strong>Особенности:</strong> неразумный, уязвимость к электричеству.<br><strong>Способности:</strong> самоликвидация (см. выше).<br><strong>Рекомендуемые модификаторы характеристик:</strong> Сила, Ловкость.</p><hr><h2 id="базовыи-шаблон-оккультного-зомби" tabindex="-1">Базовый шаблон Оккультного Зомби <a class="header-anchor" href="#базовыи-шаблон-оккультного-зомби" aria-label="Permalink to “Базовый шаблон Оккультного Зомби”">​</a></h2><p>Эта неразумная нежить оживлена сверхъестественными силами или некромантией и часто создаётся и управляется могущественным заклинателем или высшей нежитью.</p><p><strong>Требуемый тип существа:</strong> нежить.<br><strong>Требуемая роль:</strong> воитель.<br><strong>Особенности:</strong> на 20% больше ПЗ, чем предусмотрено КО существа; СУ 5/магия (увеличивается до СУ 10 при КО 8+ и СУ 15 при КО 14+); неразумный; шаткая походка (см. стр. 64).<br><strong>Рекомендуемые модификаторы характеристик:</strong> Сила, Ловкость.</p><hr><h2 id="базовыи-шаблон-скелета" tabindex="-1">Базовый шаблон Скелета <a class="header-anchor" href="#базовыи-шаблон-скелета" aria-label="Permalink to “Базовый шаблон Скелета”">​</a></h2><p>Скелеты оживляются сверхъестественными силами или некромантией. Они быстрее прочей неразумной нежити и часто используют оружие и броню, которыми умели пользоваться при жизни.</p><p><strong>Требуемый тип существа:</strong> нежить.<br><strong>Требуемая роль:</strong> воитель.<br><strong>Особенности:</strong> СУ 5/магия (увеличивается до СУ 10 при КО 8+ и СУ 15 при КО 14+); неразумный; невосприимчивость к холоду.<br><strong>Рекомендуемые модификаторы характеристик:</strong> Сила, Ловкость.</p>',57)])])}const c=a(s,[["render",e]]);export{p as __pageData,c as default};
