import{_ as a,c as n,o as t,ah as o}from"./chunks/framework.MgE-7MWY.js";const p=JSON.parse('{"title":"Рептоид [Заклинатель]","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Рептоид.md","filePath":"content/Бестиарий/Рептоид.md"}'),s={name:"content/Бестиарий/Рептоид.md"};function e(g,r,h,l,i,d){return t(),n("div",null,[...r[0]||(r[0]=[o('<h1 id="рептоид-заклинатель" tabindex="-1">Рептоид [Заклинатель] <a class="header-anchor" href="#рептоид-заклинатель" aria-label="Permalink to “Рептоид [Заклинатель]”">​</a></h1><h2 id="рептоид" tabindex="-1">Рептоид <a class="header-anchor" href="#рептоид" aria-label="Permalink to “Рептоид”">​</a></h2><p><strong>КО 1</strong><br> Опыт 400</p><p>Принципиальный злой средний гуманоид (перевёртыш, рептоид).<br><strong>Инициатива:</strong> +0; <strong>Восприятие:</strong> сумеречное зрение; <strong>Внимание</strong> +5.</p><h3 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 16<br> ЭКБ: 11; ККБ: 12<br><strong>Стойкость</strong> +1; <strong>Реакция</strong> +1; <strong>Воля</strong> +2 (+2 против эффектов с дескриптором «разум» и ядов).</p><h3 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 30 футов.<br><strong>В ближнем бою:</strong> удар когтями +4 (1d4 + 2 Р).<br><strong>Дистанционная:</strong> лазерный пистолет «Азимут» +2 (1d4 + 1 О, крит: поджигание 1d4).</p><h3 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +1; <strong>Лов:</strong> +0; <strong>Вын:</strong> –1; <strong>Инт:</strong> +2; <strong>Мдр:</strong> +1; <strong>Хар:</strong> +4.<br><strong>Навыки:</strong> Блеф +10, Дипломатия +10, Маскировка +5, Проницательность +5.<br><strong>Языки:</strong> верчитский, всеобщий, рептоидский.<br><strong>Прочие способности:</strong> смена формы.<br><strong>Снаряжение:</strong> «вторая кожа», лазерный пистолет «Азимут» с 2 аккумуляторами (20 зарядов в каждом).</p><h3 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая.<br><strong>Численность:</strong> одиночка, пара или клика (3–8 и 1 рептоид-владыка).</p><hr><h2 id="рептоид-владыка" tabindex="-1">Рептоид-Владыка <a class="header-anchor" href="#рептоид-владыка" aria-label="Permalink to “Рептоид-Владыка”">​</a></h2><p><strong>КО 6</strong><br> Опыт 2 400</p><p>Рептоид, мистик.<br> Принципиальный злой средний гуманоид (перевёртыш, рептоид).<br><strong>Инициатива:</strong> +0; <strong>Восприятие:</strong> сумеречное зрение; <strong>Внимание</strong> +13.</p><h3 id="защита-1" tabindex="-1">Защита <a class="header-anchor" href="#защита-1" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 77; ПР: 4<br> ЭКБ: 16; ККБ: 18<br><strong>Стойкость</strong> +5; <strong>Реакция</strong> +5; <strong>Воля</strong> +11; +2 против эффектов с дескриптором «разум» и ядов.</p><h3 id="атака-1" tabindex="-1">Атака <a class="header-anchor" href="#атака-1" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 30 футов.<br><strong>В ближнем бою:</strong> удар когтями +12 (1d6 + 8 Р).<br><strong>Дистанционная:</strong> лазерный пистолет «Корона» +10 (2d4 + 6 О, крит: поджигание 1d4).</p><h3 id="атакующие-способности" tabindex="-1">Атакующие способности <a class="header-anchor" href="#атакующие-способности" aria-label="Permalink to “Атакующие способности”">​</a></h3><p>неведомые приказания, отголоски власти, принудительная дружба (СЛ 16).</p><h3 id="псевдозаклинания-мистика-уз-6" tabindex="-1">Псевдозаклинания мистика (УЗ 6): <a class="header-anchor" href="#псевдозаклинания-мистика-уз-6" aria-label="Permalink to “Псевдозаклинания мистика (УЗ 6):”">​</a></h3><ul><li>Неограниченно — связь разумов.</li></ul><h3 id="известные-заклинания-мистика-уз-6" tabindex="-1">Известные заклинания мистика (УЗ 6): <a class="header-anchor" href="#известные-заклинания-мистика-уз-6" aria-label="Permalink to “Известные заклинания мистика (УЗ 6):”">​</a></h3><ul><li>2 круг (1 в день) — паралич (гуманоид) (СЛ 19), ступор (чудовище) (СЛ 19).</li><li>1 круг (3 в день) — низшее замешательство (СЛ 18), приворот (гуманоид) (СЛ 18), приказ (СЛ 18).</li><li>0 круг (неограниченно) — ступор (СЛ 17), телепатическое сообщение.</li></ul><h3 id="дарование-владыка" tabindex="-1">Дарование: владыка. <a class="header-anchor" href="#дарование-владыка" aria-label="Permalink to “Дарование: владыка.”">​</a></h3><h3 id="параметры-1" tabindex="-1">Параметры <a class="header-anchor" href="#параметры-1" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +2; <strong>Лов:</strong> +1; <strong>Вын:</strong> +1; <strong>Инт:</strong> +1; <strong>Мдр:</strong> +3; <strong>Хар:</strong> +5.<br><strong>Навыки:</strong> Блеф +18, Дипломатия +18, Маскировка +13, Проницательность +13.<br><strong>Языки:</strong> верчитский, всеобщий, рептоидский.<br><strong>Прочие способности:</strong> смена формы.<br><strong>Снаряжение:</strong> касатский микрокордовый костюм II, лазерный пистолет «Корона» с 4 аккумуляторами (20 зарядов в каждом).</p><h3 id="экология-1" tabindex="-1">Экология <a class="header-anchor" href="#экология-1" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая.<br><strong>Численность:</strong> одиночка, пара или клика (1 и 3–8 рептоидов).</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Рептоиды — мастера маскировки и обмана. Они используют способность изменять облик, чтобы внедряться в различные сообщества под видом влиятельных личностей и занимать в них высокое положение. Количество успешно внедрившихся рептоидов редко бывает точно известно: помимо превосходных шпионских навыков, эти существа обладают особой магией, при помощи которой заметают следы и склоняют врагов к сотрудничеству. Количество успешно внедрившихся рептоидов редко бывает точно известно: помимо превосходных шпионских навыков, эти существа обладают особой магией, при помощи которой заметают следы и склоняют врагов к сотрудничеству. Способность рептоидов к смене формы обладает рядом ограничений: обычно рептоид выбирает только одну личность и меняет её лишь тогда, когда она скомпрометирована. В некоторых случаях рептоиды организуют «несчастные случаи», чтобы избавиться от нежелательного облика и принять новый. Иногда конспирологи или работники служб контрразведки рассматривают смерти высокооплачиваемых лиц как организованные рептоидами заказные убийства или подставные смерти для смены личности. Рептоиды могут поддерживать маскировку годами, проводя в чужом облике большую часть своей жизни.</p><p>За обычными рептоидами стоят рептоиды-владыки — гениальные заговорщики и руководители шпионских сетей. Их редко упоминают даже в сплетнях и ещё реже видят наяву. Магические способности владык гораздо сильнее, чем у рядовых рептоидов, поэтому их об замыслах и личности почти ничего неизвестно. Считается, что владыки правят рептоидами, но остаётся загадкой, как они приходят к власти или получают способности. Никто в мирах Соглашения не знает, внедряются ли владыки в общество первыми и успешно остаются нераскрытыми, или же прибывают на планету только после того, как остальные рептоиды подготовили для этого почву, или же они и вовсе направляют за своими сородичами издалека, направляя их из секретного штаба. Подобный командный центр может находиться где угодно, причём некоторые теории заговора указывают прямо на станцию «Авессалом».</p><p>Рептоиды, что неудивительно, не выдают информации относительно своих целей и даже под пыткой скорее умрут, чем расскажут о родной планете или расе. Некоторые убеждены, что рептоиды пытаются ослабить общество перед вторжением, в то время как другие считают, что общество уже находится под их контролем, а сами рептоиды паразитируют на нём и вторжение им ни к чему.</p><hr><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +2 Сил, +2 Хар, –2 Лов<br><strong>ПЗ:</strong> 5</p><p><strong>Размер и тип:</strong> рептоиды — гуманоиды среднего размера с подтипами «перевёртыш» и «рептоид».<br><strong>Сумеречное зрение:</strong> рептоиды видят при низком уровне освещённости так же хорошо, как и при среднем.</p><p><strong>Естественное оружие:</strong> рептоиды всегда считаются вооружёнными. Они могут наносить 1d3 смертельного режущего урона безоружным ударом, и атака не считается архаичной. На 3 уровне рептоиды получают уникальную разновидность черты Привычное оружие для своего естественного оружия, позволяющую им добавлять 1,5 уровня персонажа (вместо значения уровня персонажа, как обычно) к урону при использовании естественного оружия.</p><p><strong>Смена формы:</strong> один раз в день рептоид может принять облик одного конкретного гуманоида среднего размера. При использовании этой способности рептоид каждый раз принимает именно эту форму, получая бонус +10 (раса) к проверкам Маскировки при попытках выдать себя за гуманоида выбранного вида. В остальном способность аналогично изменению облика, а время её действия составляет 10 минут. На 3 уровне рептоид может выбрать себе новый гуманоидный облик, потратив 1 неделю на подготовку к преобразованию, но после этого лишается возможности принимать предыдущую форму.</p><p><strong>Холоднокровный:</strong> рептоиды получают бонус +2 (раса) к испытаниям против эффектов с дескриптором «разум» и ядов.</p>',43)])])}const c=a(s,[["render",e]]);export{p as __pageData,c as default};
