import{_ as r,c as a,o as d,ah as n}from"./chunks/framework.MgE-7MWY.js";const p=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Саркезианин.md","filePath":"content/Бестиарий/Саркезианин.md"}'),o={name:"content/Бестиарий/Саркезианин.md"};function s(e,t,h,g,i,l){return d(),a("div",null,[...t[0]||(t[0]=[n('<h2 id="саркезианин-снаипер-специалист" tabindex="-1">Саркезианин-Снайпер [Специалист] <a class="header-anchor" href="#саркезианин-снаипер-специалист" aria-label="Permalink to “Саркезианин-Снайпер  [Специалист]”">​</a></h2><p><strong>КО 5</strong><br> Опыт 1 600</p><p>Саркезианин, оперативник.<br> Принципиальный нейтральный крупный гуманоид (саркезианин).<br><strong>Инициатива:</strong> +5; <strong>Восприятие:</strong> сумеречное зрение; <strong>Внимание</strong> +17.</p><h3 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 64; ПР: 4<br> ЭКБ: 19; ККБ: 20<br><strong>Стойкость</strong> +4; <strong>Реакция</strong> +9; <strong>Воля</strong> +8.<br><strong>Защитные способности:</strong> невероятное проворство, уклонение.</p><h3 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 40 футов.<br><strong>В ближнем бою:</strong> тактический дуэльный меч +10 (1d6 + 5 Р).<br><strong>Дистанционная:</strong> улучшенная диаспорийская винтовка +12 (2d8 + 5 О) или осколочная граната II +12 (2d6 Ко, 15 футов, СЛ 15).</p><h3 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +0; <strong>Лов:</strong> +5; <strong>Вын:</strong> +0; <strong>Инт:</strong> +3; <strong>Мдр:</strong> +0; <strong>Хар:</strong> +2.<br><strong>Навыки:</strong> Акробатика +17, Блеф +12, Выживание +12, Компьютеры +12, Скрытность +17.<br><strong>Языки:</strong> всеобщий, саркезианский.<br><strong>Прочие способности:</strong> оперативная уловка (маскировочное поле), полёт в вакууме, специализация оперативника (призрак).<br><strong>Снаряжение:</strong> эстекский комбинезон III, дуэльный вибромеч, лазерная винтовка «Корона» с 4 аккумуляторами повышенной ёмкости (40 зарядов в каждом), 4 осколочные гранаты II, 2 сыворотки исцеления (тип III).</p><h3 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая с пониженной гравитацией (Диаспора).<br><strong>Численность:</strong> одиночка, пара или отряд (3–5).</p><h3 id="особые-способности" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности" aria-label="Permalink to “Особые способности”">​</a></h3><h4 id="полет-в-вакууме-св" tabindex="-1">Полёт в вакууме (Св) <a class="header-anchor" href="#полет-в-вакууме-св" aria-label="Permalink to “Полёт в вакууме (Св)”">​</a></h4><p>Саркезианин может не дышать в течение 1 часа и не подвержен негативному воздействию вакуума. Потратив 1 ПР, саркезианин может увеличить длительность этого эффекта на 1 час за 1 КО, а потратив 2 ПР — 2 часа за 1 КО. В вакууме саркезиане автоматически выпускают энергетические крылья, получая скорость полёта 120 футов (Св, средняя манёвренность). Крылья позволяют летать только в вакууме.</p><hr><h2 id="саркезианин-киберкомандо" tabindex="-1">Саркезианин-Киберкомандо <a class="header-anchor" href="#саркезианин-киберкомандо" aria-label="Permalink to “Саркезианин-Киберкомандо”">​</a></h2><p><strong>КО 8</strong><br> Опыт 4 800</p><p>Саркезианин, механик.<br> Принципиальный нейтральный крупный гуманоид (саркезианин).<br><strong>Инициатива:</strong> +5; <strong>Восприятие:</strong> сумеречное зрение; <strong>Внимание</strong> +16.</p><h3 id="защита-1" tabindex="-1">Защита <a class="header-anchor" href="#защита-1" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 110; ПР: 4<br> ЭКБ: 23; ККБ: 24<br><strong>Стойкость</strong> +7; <strong>Реакция</strong> +7; <strong>Воля</strong> +11.</p><h3 id="атака-1" tabindex="-1">Атака <a class="header-anchor" href="#атака-1" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 30 футов.<br><strong>В ближнем бою:</strong> дуэльный вибромеч +15 (2d6 + 8 Р).<br><strong>Дистанционная:</strong> лазерная винтовка «Корона» +17 (2d6 + 8 О, крит: поджигание 1d6) или шумовая граната II +17 (взрыв (2d10 Э, 3, 15 футов, СЛ 18)).</p><h3 id="занимаемое-пространство-10-футов" tabindex="-1">Занимаемое пространство:** 10 футов. <a class="header-anchor" href="#занимаемое-пространство-10-футов" aria-label="Permalink to “Занимаемое пространство:** 10 футов.”">​</a></h3><p><strong>Зона досягаемости:</strong> 10 футов.<br><strong>Атакующие способности:</strong> отслеживание цели, перегрузка (СЛ 18).</p><h3 id="параметры-1" tabindex="-1">Параметры <a class="header-anchor" href="#параметры-1" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +0; <strong>Лов:</strong> +6; <strong>Вын:</strong> +0; <strong>Инт:</strong> +4; <strong>Мдр:</strong> +0; <strong>Хар:</strong> +2.<br><strong>Навыки:</strong> Акробатика +21, Запугивание +16, Инженерное дело +21, Компьютеры +21, Пилотирование +16.<br><strong>Языки:</strong> всеобщий, саркезианский.<br><strong>Прочие способности:</strong> беспроводной взлом, золотые руки (1 в день), искусственный интеллект (экзокортекс), профессиональный инструмент, трюк механика (энергетический щит (20 ПЗ, 8 минут), удалённый взлом (СЛ 26).</p><h3 id="экология-1" tabindex="-1">Экология <a class="header-anchor" href="#экология-1" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая с пониженной гравитацией (Диаспора).<br><strong>Численность:</strong> одиночка, пара или отряд (3–5).</p><h3 id="особые-способности-1" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности-1" aria-label="Permalink to “Особые способности”">​</a></h3><h4 id="полет-в-вакууме-св-1" tabindex="-1">Полёт в вакууме (Св) <a class="header-anchor" href="#полет-в-вакууме-св-1" aria-label="Permalink to “Полёт в вакууме (Св)”">​</a></h4><p>см. стр. 90.</p><h4 id="отслеживание-цели-экс" tabindex="-1">Отслеживание цели (Экс) <a class="header-anchor" href="#отслеживание-цели-экс" aria-label="Permalink to “Отслеживание цели (Экс)”">​</a></h4><p>Саркезианин получает бонус +2 (раса) к проверкам Внимания при отслеживании цели.</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Саркезиане, решившие покинуть Диаспору, иногда становятся наёмниками — снайперами и разведчиками, поскольку из-за образа жизни привыкли иметь дело с далёкими целями. Они оттачивают врождённое терпение, чтобы дожидаться цели по несколько дней, устроив засаду на краю обрыва, в полуразрушенном здании или даже в космосе недалеко от места стыковки. За такую самоотверженность готовы хорошо платить, поэтому саркезиане-снайперы особенно востребованы в некоторых частях галактики.</p><p>Эти гуманоиды, предположительно, происходят от выходцев с исчезнувших двойных планет, уничтожение которых привело к возникновению пояса астероидов, именуемого Диаспорой. Саркезиане приспособились к жизни в условиях пониженной гравитации и разреженной атмосферы, ростом они от 10 до 15 футов, у них выпуклые глаза, тонкие и длинные конечности. Эти существа способны выживать в космосе, задерживая дыхание и выпуская сотканные из чистой энергии крылья, которые улавливают солнечный ветер и позволяют саркезианам парить между немногими обитаемыми астероидами и космическими платформами Диаспоры.</p><p>Благодаря магическим механизмам, созданным их предками, саркезиане уже долгое время поддерживают миры-колыбели — астероиды с магической атмосферой, гравитацией и теплом, где они могут спокойно жить и растить потомство. В сравнении с некоторыми планетами системы Голариона, миры-колыбели саркезиан — настоящая идиллия. На них есть место и полям, и лесам, и холмам, и озёрам, и маленьким городкам, в которых живёт лишь по несколько тысяч жителей. Многие из этих заповедных миров соединены между собой Межречьем, необычным водным путём, протекающим между астероидами и удерживаемым цилиндрическим силовым полем неведомого происхождения, которое не даёт воде утечь в космос.</p><hr><h2 id="диаспорииские-винтовки" tabindex="-1">Диаспорийские винтовки <a class="header-anchor" href="#диаспорииские-винтовки" aria-label="Permalink to “Диаспорийские винтовки”">​</a></h2><p>Это изящное оружие было разработано лучшими саркезианскими инженерами специально для элитных наёмников. Самый известный их производитель — «МьюниТек», одна из немногих корпораций, полностью принадлежащих саркезианам и управляемых ими. Диаспорийские винтовки — лазерное снайперское оружие.</p><h2 id="диаспорииские-винтовки-двуручное-снаиперское-оружие" tabindex="-1">Диаспорийские винтовки (Двуручное снайперское оружие) <a class="header-anchor" href="#диаспорииские-винтовки-двуручное-снаиперское-оружие" aria-label="Permalink to “Диаспорийские винтовки (Двуручное снайперское оружие)”">​</a></h2><table tabindex="0"><thead><tr><th>ЛАЗЕРНОЕ ОРУЖИЕ</th><th>УРОВЕНЬ</th><th>ЦЕНА</th><th>УРОН</th><th>ДИСТАНЦИЯ</th><th>КРИТ.</th><th>ЁМКОСТЬ</th><th>РАСХОД</th><th>ВЕС</th><th>ОСОБЫЕ СВОЙСТВА</th></tr></thead><tbody><tr><td>Диаспорийская винтовка, тактическая</td><td>1</td><td>350</td><td>1d8 О</td><td>70 футов</td><td>–</td><td>10 зарядов</td><td>1</td><td>1</td><td>Громоздкое, снайперское (250 футов)</td></tr><tr><td>Диаспорийская винтовка, улучшенная</td><td>5</td><td>3 750</td><td>2d8 О</td><td>70 футов</td><td>–</td><td>20 зарядов</td><td>1</td><td>2</td><td>Громоздкое, снайперское (500 футов)</td></tr><tr><td>Диаспорийская винтовка, элитная</td><td>10</td><td>17 000</td><td>4d8 О</td><td>80 футов</td><td>–</td><td>20 зарядов</td><td>1</td><td>2</td><td>Громоздкое, снайперское (750 футов)</td></tr><tr><td>Диаспорийская винтовка, ультра</td><td>15</td><td>110 000</td><td>6d8 О</td><td>80 футов</td><td>–</td><td>30 зарядов</td><td>1</td><td>2</td><td>Громоздкое, снайперское (1 000 футов)</td></tr><tr><td>Диаспорийская винтовка, имперская</td><td>20</td><td>725 000</td><td>10d8 О</td><td>80 футов</td><td>–</td><td>30 зарядов</td><td>1</td><td>2</td><td>Громоздкое, снайперское (1 000 футов)</td></tr></tbody></table><hr><h2 id="особенности-расы" tabindex="-1">Особенности расы <a class="header-anchor" href="#особенности-расы" aria-label="Permalink to “Особенности расы”">​</a></h2><p><strong>Изменения характеристик:</strong> +2 Лвк, +2 Хар, –2 Сил<br><strong>ПЗ:</strong> 4</p><p><strong>Размер и тип:</strong> саркезиане — гуманоиды крупного размера с подтипом «саркезианин».<br><strong>Сумеречное зрение:</strong> саркезиане видят при низком уровне освещённости так же хорошо, как и при среднем.</p><p><strong>Полёт в вакууме:</strong> саркезиане могут не дышать в течение 1 часа и не подвержены негативному воздействию вакуума. Потратив 1 ПР, саркезиане могут увеличить длительность этого эффекта на 1 час за 1 КО, а потратив 2 ПР — 2 часа за 1 КО. В вакууме саркезиане автоматически выпускают энергетические крылья, получая скорость полёта 60 футов (Св, средняя манёвренность). Крылья позволяют летать только в вакууме.</p><p><strong>Умелый:</strong> саркезиане получают один дополнительный пункт навыков на первом и каждом последующем уровне.</p>',49)])])}const c=r(o,[["render",s]]);export{p as __pageData,c as default};
