import{_ as a,c as n,o as t,ah as o}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"Симбирид [Заклинатель]","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Симбирид.md","filePath":"content/Бестиарий/Симбирид.md"}'),s={name:"content/Бестиарий/Симбирид.md"};function e(g,r,h,i,l,p){return t(),n("div",null,[...r[0]||(r[0]=[o('<h1 id="симбирид-заклинатель" tabindex="-1">Симбирид [Заклинатель] <a class="header-anchor" href="#симбирид-заклинатель" aria-label="Permalink to “Симбирид [Заклинатель]”">​</a></h1><h2 id="симбирид" tabindex="-1">Симбирид <a class="header-anchor" href="#симбирид" aria-label="Permalink to “Симбирид”">​</a></h2><p><strong>КО ⅓</strong><br> Опыт 135</p><p>Нейтральная миниатюрная абберация.<br><strong>Инициатива:</strong> +3; <strong>Восприятие:</strong> ночное зрение 60 футов; <strong>Внимание</strong> +7.</p><h3 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 6<br> ЭКБ: 11; ККБ: 11<br><strong>Стойкость</strong> +0; <strong>Реакция</strong> +2; <strong>Воля</strong> +2.</p><h3 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 15 футов.<br><strong>В ближнем бою:</strong> жало +2 (припасывание).</p><h3 id="занимаемое-пространство-1-фут" tabindex="-1">Занимаемое пространство:** 1 фут. <a class="header-anchor" href="#занимаемое-пространство-1-фут" aria-label="Permalink to “Занимаемое пространство:** 1 фут.”">​</a></h3><p><strong>Зона досягаемости:</strong> 0 футов.</p><h3 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +0; <strong>Лов:</strong> +3; <strong>Вын:</strong> –; <strong>Инт:</strong> +1; <strong>Мдр:</strong> +1; <strong>Хар:</strong> +0.<br><strong>Навыки:</strong> Выживание +7, Скрытность +7.<br><strong>Языки:</strong> всеобщий.<br><strong>Прочие способности:</strong> симбиоз.<br><strong>Снаряжение:</strong> бескарианская «Сверхброня» I с фильтрующим дыхательным аппаратом, 2 осколочные гранаты II, плазменный пистолет «Красная звезда» с 4 аккумуляторами (20 зарядов в каждом), Аугментации: симбирид (рука Даморитоша).</p><h3 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая.<br><strong>Численность:</strong> одиночка.</p><hr><h2 id="хозяин-руки-даморитоша-воитель" tabindex="-1">Хозяин Руки Даморитоша [Воитель] <a class="header-anchor" href="#хозяин-руки-даморитоша-воитель" aria-label="Permalink to “Хозяин Руки Даморитоша  [Воитель]”">​</a></h2><p><strong>КО 6</strong><br> Опыт 2 400</p><p>Человек, солдат.<br> Хаотичный нейтральный средний гуманоид (человек).<br><strong>Инициатива:</strong> +6; <strong>Восприятие:</strong> ночное зрение 60 футов; <strong>Внимание</strong> +13.</p><h3 id="защита-1" tabindex="-1">Защита <a class="header-anchor" href="#защита-1" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 90<br> ЭКБ: 21; ККБ: 23<br><strong>Стойкость</strong> +8; <strong>Реакция</strong> +6; <strong>Воля</strong> +7 (+5 против эффектов с дескриптором «эмоции»).</p><h3 id="атака-1" tabindex="-1">Атака <a class="header-anchor" href="#атака-1" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 30 футов.<br><strong>В ближнем бою:</strong> безоружный удар +16 (1d3 + 14 Д).<br><strong>Дистанционная:</strong> плазменный пистолет «Красная звезда» +13 (1d8 + 6 О &amp; 3, крит: поджигание 1d8) или осколочная граната II +13 (взрыв (2d6 Ко, 15 футов, СЛ 14)).</p><h3 id="прочие-способности" tabindex="-1">Прочие способности <a class="header-anchor" href="#прочие-способности" aria-label="Permalink to “Прочие способности”">​</a></h3><p>атака с разгона, боевой стиль (штурмовик), кипящая ярость.</p><h3 id="параметры-1" tabindex="-1">Параметры <a class="header-anchor" href="#параметры-1" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +5; <strong>Лов:</strong> +2; <strong>Вын:</strong> +3; <strong>Инт:</strong> +0; <strong>Мдр:</strong> +1; <strong>Хар:</strong> –1.<br><strong>Навыки:</strong> Атлетика +20, Запугивание +17, Инженерное дело +11.<br><strong>Языки:</strong> всеобщий.<br><strong>Прочие способности:</strong> симбиоз.<br><strong>Снаряжение:</strong> Т-костюм IV, плазменный пистолет «Красная звезда» с 4 аккумуляторами (20 зарядов в каждом), 4 осколочные гранаты II.</p><h3 id="экология-1" tabindex="-1">Экология <a class="header-anchor" href="#экология-1" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любая.<br><strong>Численность:</strong> одиночка.</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Среди симбиотических отношений наиболее распространены случаи, когда биологические виды-партнёры в процессе эволюции постепенно пришли к взаимовыгодному сотрудничеству, но некоторые симбиоты, называемые симбидрами, обладают способностью соединяться и улучшать практические способности любого другого существа. Они пытаются за счёт хозяина и могут даже влиять на его поведение. Симбидры встречаются повсеместно и обладают рядом схожих признаков, которые указывают на общего предка или, по крайней мере, служат признаком целенаправленного генетического вмешательства в далёком прошлом.</p><p>Симбидры дают хозяевам впечатляющие способности, но сами по себе практически беззащитны. Общение симбидра и хозяина в основном эмпатическое, но некоторые редкие особи разумны и могут говорить с хозяевами и даже подавлять их. Если симбирид отделён от хозяина, то отказывается возвращаться к нему, предпочитая найти другое, более заинтересованное в партнёрстве существо.</p><p>В галактике насчитываются десятки различных видов этих существ, ниже описаны самые распространённые из них. Приведённые базовые шаблоны можно использовать для создания существа, соединяющегося с симбидром; с разрешения ведущего персонаж игрока также может соединиться с симбидром, при этом требования к роли и рекомендуемые модификаторы характеристик не применяются. Симбидры считается аугментацией, «установленной» в указанную часть тела, но в отличие от обычных аугментаций отсоединённый симбидр может использоваться повторно другим существом. Симбидров нельзя ни купить, ни продать (по крайней мере, в приличных поселениях).</p><hr><h2 id="базовыи-шаблон-руки-даморитоша" tabindex="-1">Базовый шаблон Руки Даморитоша <a class="header-anchor" href="#базовыи-шаблон-руки-даморитоша" aria-label="Permalink to “Базовый шаблон Руки Даморитоша”">​</a></h2><p>Этот членистоногий симбирид впивается крошечными ножками в плоть хозяина и питается различными гормонами, предпочитая эпинефрин. Эти симбидры крайне агрессивны и практически всегда предпочитают хищников. Они пользуются популярностью у пиратов и полевых командиров, поскольку постоянно насыщают кровь хозяина адреналином, делая его бесстрашным и жестоким. Хозяина такого симбирида легко определить по налитым кровью глазам, вспухшим венам и пене, иногда идущей изо рта.</p><p>Требуемый тип существа: абберация, волшебный зверь, гуманоид, гуманоидное чудовище, дракон или животное.<br> Требуемая роль: воитель.</p><p><strong>Особенности:</strong> +1 к ЭКБ и ККБ; бонус +4 (боевой дух) к проверкам Атлетики и Запугивания; безоружные удары наносят смертельный урон, не считаются архаичным оружием, существо получает уникальную разновидность черты Привычное оружие, позволяющую добавлять 1,5 КО или уровня персонажа к наносимому ими урону.</p><p><strong>Способности:</strong> кипящая ярость (1 в день).</p><h3 id="кипящая-ярость-экс" tabindex="-1">Кипящая ярость (Экс) <a class="header-anchor" href="#кипящая-ярость-экс" aria-label="Permalink to “Кипящая ярость (Экс)”">​</a></h3><p>В качестве быстрого действия существо может впасть в ярость, чтобы получить 1 временный ПЗ за 1 КО или уровень персонажа и возможность при совершении полной атаки предпринять до трёх атак вместо двух, при этом каждая атака получает штраф –5 (вместо обычного –4). Ярость продолжается 1 раунд за 1 КО или уровень персонажа, по её окончании хозяин считается утомлённым в течение 10 минут.</p><p><strong>Недостатки:</strong> штраф –2 к проверкам Дипломатии, навыков, зависящих от Интеллекта, и испытаниям Воли против эффектов с дескриптором «эмоции».</p><p><strong>Части тела:</strong> мозг, руки (все).</p><p><strong>Рекомендуемые модификаторы характеристик:</strong> Сила, Выносливость.</p><hr><h2 id="базовыи-шаблон-продавца-снов" tabindex="-1">Базовый шаблон Продавца Снов <a class="header-anchor" href="#базовыи-шаблон-продавца-снов" aria-label="Permalink to “Базовый шаблон Продавца Снов”">​</a></h2><p>Эти мясистые существа присасываются к спине хозяина и запускают в мозг тонкие нити, стимулируя его центры удовольствия и активируя экстрасенсорные способности. Хозяева испытывают лёгкую эйфорию, не становятся более ленивыми и не склонными к риску, поэтому данный симбирид в основном встречается в притонах и сектах.</p><p><strong>Требуемый тип существа:</strong> абберация, волшебный зверь, гуманоид, гуманоидное чудовище, дракон или животное.<br><strong>Требуемая роль:</strong> заклинатель или специалист.<br><strong>Особенности:</strong> бонус +2 (боевой дух) к испытаниям Воли и проверкам навыков, зависящих от Мудрости; ограниченная телепатия (30 футов); невосприимчивость к эффектам, позволяющим управлять сновидениями или нарушать их; хозяин может по желанию заснуть в любое время.<br><strong>Псевдозаклинания:</strong></p><ul><li>Неограниченно — психокинетическая рука и телекинетический снаряд;</li><li>3 в день — низшее замешательство (заменяется на замешательство при КО или уровне персонажа 10+).<br><strong>Недостатки:</strong> штраф –2 к проверкам инициативы и навыков, зависящих от Силы или Ловкости.<br><strong>Части тела:</strong> мозг, позвоночник.<br><strong>Рекомендуемые модификаторы характеристик:</strong> Мудрость, Харизма.</li></ul><hr><h2 id="базовыи-шаблон-совершенного" tabindex="-1">Базовый шаблон Совершенного <a class="header-anchor" href="#базовыи-шаблон-совершенного" aria-label="Permalink to “Базовый шаблон Совершенного”">​</a></h2><p>Это крошечное существо вонзает костистую голову в грудь хозяина подобно клещу. Благодаря особым органам тело симбирида — крохотная лаборатория, производящая вещества, улучшающие мыслительные процессы хозяина и увеличивающие количество вырабатываемых феромонов, но это отрицательно сказывается на здоровье хозяина.</p><p>Совершенные относятся к тем немогим симбиридам, которые способны общаться с хозяином напрямую. Они могут лишить непокорного хозяина нижеписанных преимуществ и даже могут причинять ему боль и вызывать случайные галлюцинации, чтобы добиться послушания.</p><p><strong>Требуемый тип существа:</strong> абберация, волшебный зверь, гуманоид, гуманоидное чудовище, дракон или животное.<br><strong>Требуемая роль:</strong> воитель.<br><strong>Особенности:</strong> бонус +1 (боевой дух) ко всем навыкам, зависящим от Харизмы; дополнительный пункт навыков за каждый уровень персонажа; при проверке Выносливости при продолжительной стабилизации совершите двойной бросок и возьмите худший результат.<br><strong>Недостатки:</strong> максимальные ПЗ снижаются на 1 за 1 КО или уровень персонажа; при проверке Выносливости при продолжительной стабилизации совершите двойной бросок и возьмите худший результат.<br><strong>Части тела:</strong> мозг, сердце.<br><strong>Рекомендуемые модификаторы характеристик:</strong> Интеллект, Харизма.</p>',54)])])}const c=a(s,[["render",e]]);export{d as __pageData,c as default};
