import{_ as r,c as n,o as t,ah as e}from"./chunks/framework.MgE-7MWY.js";const p=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Фружай.md","filePath":"content/Бестиарий/Фружай.md"}'),o={name:"content/Бестиарий/Фружай.md"};function s(h,a,i,l,g,d){return t(),n("div",null,[...a[0]||(a[0]=[e('<h2 id="колония-фружаем-воитель" tabindex="-1">Колония Фружаем [Воитель] <a class="header-anchor" href="#колония-фружаем-воитель" aria-label="Permalink to “Колония Фружаем  [Воитель]”">​</a></h2><p><strong>КО 19</strong><br> Опыт 204 800</p><p>Нейтральное колоссальное растение.<br><strong>Инициатива:</strong> +0; <strong>Восприятие:</strong> сумеречное зрение; <strong>Внимание</strong> +32.</p><h3 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 420; ПР: 6<br> ЭКБ: 31; ККБ: 33<br><strong>Стойкость</strong> +22; <strong>Реакция</strong> +15; <strong>Воля</strong> +20.<br><strong>Защитные способности:</strong> абсолютная гравитация, невосприимчивость: невосприимчивость растений.</p><h3 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 30 футов, лазание 20 футов.<br><strong>В ближнем бою:</strong> укус +33 (6d10 + 29 Ко) или крушащий удар +33 (4d12 + 29 Д).<br><strong>Мультиатака:</strong> укус +27 (6d10 + 29 Ко), 2 крушащих удара +27 (4d12 + 29 Д).</p><h3 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +10; <strong>Лов:</strong> +0; <strong>Вын:</strong> +9; <strong>Инт:</strong> +2; <strong>Мдр:</strong> +4; <strong>Хар:</strong> +9.<br><strong>Навыки:</strong> Биологические науки +32, Выживание +32, Запугивание +37.<br><strong>Языки:</strong> фружай, телепатия 300 футов.</p><h3 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любые леса (Ориколай).<br><strong>Численность:</strong> одиночка, пара или общность (2–5).</p><hr><h2 id="фружаи-солдат-воитель" tabindex="-1">Фружай-Солдат [Воитель] <a class="header-anchor" href="#фружаи-солдат-воитель" aria-label="Permalink to “Фружай-Солдат  [Воитель]”">​</a></h2><p><strong>КО 12</strong><br> Опыт 19 200</p><p>Нейтральное крупное растение.<br><strong>Инициатива:</strong> +2; <strong>Восприятие:</strong> сумеречное зрение; <strong>Внимание</strong> +22.</p><h3 id="защита-1" tabindex="-1">Защита <a class="header-anchor" href="#защита-1" aria-label="Permalink to “Защита”">​</a></h3><p>ПЗ: 210<br> ЭКБ: 26; ККБ: 28<br><strong>Стойкость</strong> +16; <strong>Реакция</strong> +14; <strong>Воля</strong> +11.<br><strong>Защитные способности:</strong> абсолютная гравитация, невосприимчивость: невосприимчивость растений.</p><h3 id="атака-1" tabindex="-1">Атака <a class="header-anchor" href="#атака-1" aria-label="Permalink to “Атака”">​</a></h3><p><strong>Скорость:</strong> 30 футов, лазание 20 футов.<br><strong>В ближнем бою:</strong> крушащий удар +25 (6d4 + 20 Д).<br><strong>Дистанционная:</strong> гравитационный якорь +22 (6d4 + 12 урона силой и перестановка или сбивание с ног).</p><h3 id="занимаемое-пространство-10-футов" tabindex="-1">Занимаемое пространство:** 10 футов. <a class="header-anchor" href="#занимаемое-пространство-10-футов" aria-label="Permalink to “Занимаемое пространство:** 10 футов.”">​</a></h3><p><strong>Зона досягаемости:</strong> 10 футов.<br><strong>Атакующие способности:</strong> страж колонии.</p><h3 id="параметры-1" tabindex="-1">Параметры <a class="header-anchor" href="#параметры-1" aria-label="Permalink to “Параметры”">​</a></h3><p><strong>Сил:</strong> +8; <strong>Лов:</strong> +2; <strong>Вын:</strong> +5; <strong>Инт:</strong> –1; <strong>Мдр:</strong> +2; <strong>Хар:</strong> +2.<br><strong>Навыки:</strong> Выживание +22, Запугивание +27, Скрытность +22.<br><strong>Языки:</strong> фружай, телепатия 100 футов.</p><h3 id="экология-1" tabindex="-1">Экология <a class="header-anchor" href="#экология-1" aria-label="Permalink to “Экология”">​</a></h3><p><strong>Среда обитания:</strong> любые леса (Ориколай).<br><strong>Численность:</strong> одиночка, пара или отряд (2–8).</p><hr><h2 id="особые-способности" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности" aria-label="Permalink to “Особые способности”">​</a></h2><h3 id="абсолютная-гравитация-св" tabindex="-1">Абсолютная гравитация (Св) <a class="header-anchor" href="#абсолютная-гравитация-св" aria-label="Permalink to “Абсолютная гравитация (Св)”">​</a></h3><p>Колония фружаем способна управлять своей массой, используя особенности внутриклеточных структур и психонические команды. Она получает бонус +4 к КБ против боевых манёвров тарана, перестановки и сбивания с ног. Более того, колония способна игнорировать сверхъестественные эффекты гравитации, например власть над гравитацией, и всегда может вести себя так, как если бы естественная гравитация была на две категории сильнее или слабее.</p><h3 id="порождение-защитника-экс" tabindex="-1">Порождение защитника (Экс) <a class="header-anchor" href="#порождение-защитника-экс" aria-label="Permalink to “Порождение защитника (Экс)”">​</a></h3><p>Колония фружаем может выращивать фружая-солдата (см. ниже), но обычно это занимает несколько недель. В качестве действия полного хода колония может создать недолговечного фружая-солдата рядом с собой, потратив 1 РР, солдат может сразу приступить к действиям. Созданный таким образом солдат существует в течение 1 часа, после чего рассыпается на части. Потратив 1 РР, колония может вместо этого выбрать три способности. Если у колонии действует какая-либо продолжительная способность (например, полёт или гравитационное поле), то её действие немедленно прекращается, если в новом раунде она не выбрала её снова.</p><h3 id="управление-гравитациеи-св" tabindex="-1">Управление гравитацией (Св) <a class="header-anchor" href="#управление-гравитациеи-св" aria-label="Permalink to “Управление гравитацией (Св)”">​</a></h3><p>Колония фружаем способна управлять гравитацией несколькими способами. В начале своего хода колония выбирает две из пяти описанных способностей и может использовать их до начала своего следующего хода. Потратив 1 РР, колония может вместо этого выбрать три способности. Если у колонии действует какая-либо продолжительная способность (например, полёт или гравитационное поле), то её действие немедленно прекращается, если в новом раунде она не выбрала её снова.</p><h4 id="гравитационная-волна" tabindex="-1">Гравитационная волна <a class="header-anchor" href="#гравитационная-волна" aria-label="Permalink to “Гравитационная волна”">​</a></h4><p>В качестве основного действия колония фружаем может выпустить силовую волну в 60-футовом конусе, нанося каждому существу в области действия 8d6 + 17 дробящего урона и отбрасывая его на 10 футов назад. Успешное испытание Реакции со СЛ 24 позволяет цели отменить перемещение и уменьшить урон вдвое.</p><h4 id="гравитационное-поле" tabindex="-1">Гравитационное поле <a class="header-anchor" href="#гравитационное-поле" aria-label="Permalink to “Гравитационное поле”">​</a></h4><p>В качестве основного действия колония фружаем может создать область аномальной гравитации, аналогичную заклинанию власт над гравитацией (УЗ 19). Колония может поддерживать только один такой эффект, при повторном применении этой способности действие предыдущего гравитационного поля завершается.</p><h4 id="гравитационныи-удар" tabindex="-1">Гравитационный удар <a class="header-anchor" href="#гравитационныи-удар" aria-label="Permalink to “Гравитационный удар”">​</a></h4><p>Колония фружаем может использовать гравитационный удар в качестве атаки. Это дистанционная атака, которая совершается против ЭКБ цели. Максимальная дальность этой атаки 200 футов, шаг дистанции отсутствует.</p><h4 id="гравитационныи-щит" tabindex="-1">Гравитационный щит <a class="header-anchor" href="#гравитационныи-щит" aria-label="Permalink to “Гравитационный щит”">​</a></h4><p>Колония фружаем окружает себя импровизированными левитирующими щитами и использует гравитационные импульсы для отражения атак, получая бонус +4 к КБ. Полёт: колония фружаем получает полёт 40 футов (Экс, скверная манёвренность). Когда действие способности завершается, колония плавно снижается со скоростью 60 футов за раунд и не получает урона при приземлении.</p><h4 id="гравитационныи-якорь" tabindex="-1">Гравитационный якорь <a class="header-anchor" href="#гравитационныи-якорь" aria-label="Permalink to “Гравитационный якорь”">​</a></h4><p>В качестве дистанционной атаки фружай-солдат может создавать сильные колебания гравитации. Это дистанционная атака, которая совершается против ЭКБ цели. Максимальная дальность этой атаки — 150 футов, шаг дистанции отсутствует. При попадании солдат может без затрат действий провести против цели боевой манёвр перестановки или сбивание с ног, используя при этом модификатор атаки гравитационного якоря +4. При перестановке цель можно переместить в пределах 10 футов от её исходной позиции.</p><h4 id="страж-колонии-экс" tabindex="-1">Страж колонии (Экс) <a class="header-anchor" href="#страж-колонии-экс" aria-label="Permalink to “Страж колонии (Экс)”">​</a></h4><p>Каждый раз, когда колония фружаем нанесён урон, все фружай-солдаты в пределах 300 футов от неё получают бонус +4 (боевой дух) к проверкам атаки и увеличивают наземную скорость на 10 футов на 1 раунд.</p><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Планета Ориколай представляет собой тор, поэтому существенные колебания гравитации, освещённости и температуры на ней — в порядке вещей. Среди наиболее успешных обитателей планеты выделяются разумные грибы — фружай, которые не только приспособились к сезонным циклам планеты, но и изменили биологию и развили в себе психонические способности, помогающие адаптироваться к различным уровням гравитации. Фружай похожи на муравьёв: жилой центр колонии порождает сотни рабочих и солдат, обеспечивающих своих рабочих и солдат, посредством которых они проспаиваются, покрывая свою территорию множеством спор, которые прорастают в отважных тушах зверей, погибших во время холодов. Если падали в округе мало, колония начинает агрессивно охотиться на всё живое, атакуя каждого на своём пути.</p><p>Незрелые труты, проросшие из заражённых спорами трупов (используйте блок параметров фружая-солдата без способности страж колонии), бродят по незанятым территориям, охотясь, находя пищу и становясь сильнее. Ранней осенью трути собираются для спаривания, в процессе которого они сражаются и даже пожирают друг друга. Выжившие разбредаются, высвляя мигрирующие стада животных или преследуя крупную добычу до её зимнего логова, стремясь добыть и запасти как можно больше пропитания.</p><p>В зимние месяцы фружай поглощают запасы, достигают колоссальных размеров и преобразуются в полноценные колонии. И сами колонии, и порождённые ими солдаты используют управление гравитацией, чтобы собирать камни и другие предметы, которые постоянно кружат вокруг них и служат им единственным богатством и украшениями.</p><p>Несмотря на странный внешний вид, и колонии, и солдаты фружай на удивление умны. Они считают себя экологическими санитарами, устраняющими слабых и открываями дорогу более сильным существам. Некоторые попытки колонизировать Ориколай не увенчались успехом ввиду того, что фружай приняли поселения за своеобразные кладовые, в которых полным-полно слабовольной пищи. Более крупные колонии фружай иногда дают интересные знаменитым ксенобиологам, но в процессе беседы о философских размышлениях, которые занимают фружай в свободное от еды время, исследователей могут просто-напросто сожрать.</p>',50)])])}const c=r(o,[["render",s]]);export{p as __pageData,c as default};
