import{_ as a,c as r,o as d,ah as e}from"./chunks/framework.MgE-7MWY.js";const c=JSON.parse('{"title":"Электрофаг [Специалист]","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Электрофаг.md","filePath":"content/Бестиарий/Электрофаг.md"}'),n={name:"content/Бестиарий/Электрофаг.md"};function h(o,t,s,i,l,b){return d(),r("div",null,[...t[0]||(t[0]=[e('<h1 id="электрофаг-специалист" tabindex="-1">Электрофаг [Специалист] <a class="header-anchor" href="#электрофаг-специалист" aria-label="Permalink to “Электрофаг [Специалист]”">​</a></h1><p><strong>КО 2</strong><br> Опыт 600</p><p>Нейтральный небольшой волшебный зверь.<br><strong>Инициатива:</strong> +4; <strong>Восприятие:</strong> ночное зрение 60 футов, сумеречное зрение; <strong>Внимание</strong> +12.</p><h2 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h2><p>ПЗ: 23; ПР: 3<br> ЭКБ: 14; ККБ: 14<br><strong>Стойкость</strong> +3; <strong>Реакция</strong> +7; <strong>Воля</strong> +1.<br><strong>Невосприимчивость:</strong> электричество.</p><h2 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h2><p><strong>Скорость:</strong> 30 футов, полёт 30 футов (Экс, средняя манёвренность).<br><strong>В ближнем бою:</strong> удар хвостом +9 (1d4 + 3 Д, крит: кража электричества).</p><h3 id="атакующие-способности" tabindex="-1">Атакующие способности <a class="header-anchor" href="#атакующие-способности" aria-label="Permalink to “Атакующие способности”">​</a></h3><p>электрический разряд (1d6 + 2 Э) и ошеломление, испытание Реакции со СЛ 13 уменьшает урон вдвое.</p><h2 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h2><p><strong>Сил:</strong> +1; <strong>Лов:</strong> +4; <strong>Вын:</strong> +1; <strong>Инт:</strong> –1; <strong>Мдр:</strong> +2; <strong>Хар:</strong> –1.<br><strong>Навыки:</strong> Акробатика +7, Атлетика +7, Выживание +12, Скрытность +12.<br><strong>Языки:</strong> всеобщий.</p><h2 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h2><p><strong>Среда обитания:</strong> любая.<br><strong>Численность:</strong> одиночка, пара, гнездо (3–12) или колония (13+).</p><hr><h2 id="особые-способности" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности" aria-label="Permalink to “Особые способности”">​</a></h2><h3 id="кража-электричества-экс" tabindex="-1">Кража электричества (Экс) <a class="header-anchor" href="#кража-электричества-экс" aria-label="Permalink to “Кража электричества (Экс)”">​</a></h3><p>Когда электрофаг наносит критический удар живому существу, конструкции с подтипом «технологический» или технологическому элементу снаряжения, он крадёт часть электрической энергии цели, восстанавливая 1 ПР (вплоть до максимума в 3 ПР).</p><h3 id="электрическии-разряд-экс" tabindex="-1">Электрический разряд (Экс) <a class="header-anchor" href="#электрическии-разряд-экс" aria-label="Permalink to “Электрический разряд (Экс)”">​</a></h3><p>В качестве основного действия электрофаг может потратить 1 РР, чтобы совершить мощную атаку, вызывая большую часть запасённого электричества. Все существа в пределах 10 футов от электрофага получают 1d6 + 2 урона электричеством и становятся ошеломлёнными на 1 раунд. Цель может пройти испытание Реакции со СЛ 13, чтобы уменьшить получаемый урон вдвое и отменить эффект ошеломления.</p><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Электрофаги, обитатели дикой планеты Верлорр, чуть было не вымерли, когда усилившаяся вулканическая активность привела к высыханию болот, где они обитали, и вымиранию трёхголовых электрических угрей, которыми они питались. Жизнь электрофагов изменилась в лучшую сторону 20 лет назад, когда на Верлорр прибыли исследователи из миров Соглашения. В огромных металлических звездолётах не было недостатка в электричестве, излюбленной пище электрофагов, поэтому несколько особей проникли на космические корабли. С тех пор эти существа, которых покорители космоса прозвали «кабелегрызами», быстро размножились, создав множество колоний повсюду, от станции «Авессалом» до дальних границ Простора.</p><p>Электрофаг не переваривает органические вещества, а питается электрическим током, который добывает из механических устройств и живых существ. Ксенобиологи установили, что внутренняя анатомия электрофага отличается от обычной для животного — у него нет ни желудка, ни кишечника, ни даже печени. Каждая клетка существа — миниатюрная батарея, питающая электрическими импульсами мышцы и мозг. Таким образом, электрофаг представляет собой одну большую нервную систему. Эти существа быстро приспособились к обитанию на звездолётах и космических станциях, где электричество в избытке.</p><p>У электрофага змееподобное тело, покрытое сверкающей чешуёй, и пара кожистых крыльев. Средняя особь достигает 3 футов в длину и весит 25 фунтов. У электрофага три ярко-синих визуальных сенсора: два по бокам головы и один на переносице. Широкую пасть заполняют острые зубы, но нужны они в основном не для боя, а для того чтобы прогрызать толстую изоляцию проводов. Для охоты же электрофаг использует шипастый хвост.</p><p>Одинокий электрофаг не представляет серьёзной угрозы для космических станций и кораблей, но две и более особи начинают размножаться с пугающей быстротой и могут запасённую энергию и зачастую наносит компьютерным системам и органике урон, на который в обычном состоянии ему потребовалось бы вдвое больше времени. К счастью, для совершения подобной атаки электрофагу требуется достаточно долго копить электричество.</p><p>В конструкции крупных звездолётов обычно предусмотрено достаточно резервных систем, поэтому электрофагам требуется гораздо больше времени для отключения ключевых систем корабля, но достаточно большая колония этих существ при благоприятных обстоятельствах может нанести огромный урон в крайне короткие сроки. Даже один электрофаг может в конечном итоге вывести звездолёт из строя. В таблице приведены размеры кораблей и промежуток времени, по истечении которого одна из корабельных систем (определяемая случайным образом) получает критические повреждения («Основная книга правил», стр. 320). Чем больше на звездолёте электрофагов, тем быстрее они выводят его из строя, но для получения критических повреждений таким образом требуется не менее 1 дня.</p><hr><h2 id="размер-звездолета-время" tabindex="-1">Размер звездолёта | Время <a class="header-anchor" href="#размер-звездолета-время" aria-label="Permalink to “Размер звездолёта | Время”">​</a></h2><table tabindex="0"><thead><tr><th>Маленький</th><th>1d6 дней — 1 день за каждых 2 электрофага</th></tr></thead><tbody><tr><td>Небольшой</td><td>2d6 дней — 1 день за каждых 3 электрофага</td></tr><tr><td>Средний</td><td>2d10 дней — 1 день за каждых 6 электрофага</td></tr><tr><td>Крупный</td><td>4d8 дней — 1 день за каждых 12 электрофага</td></tr><tr><td>Огромный</td><td>6d10 дней — 1 день за каждых 30 электрофага</td></tr><tr><td>Гигантский</td><td>3d12 недель — 1 неделя за каждых 50 электрофага</td></tr><tr><td>Колоссальный</td><td>4d6 месяцев — 1 месяц за каждых 100 электрофага</td></tr></tbody></table><h2 id="перчатка-электрофаг" tabindex="-1">Перчатка-Электрофаг <a class="header-anchor" href="#перчатка-электрофаг" aria-label="Permalink to “Перчатка-Электрофаг”">​</a></h2><p>Умелым оружейникам удалось возродить способность электрофагов к поглощению и проведению электричества, разработав перчатки, в которых используется сочетание шипов электрофага, проводников и миниатюрных аккумуляторов, позволяющее наносить электрический урон и при критическом ударе вытягивать электричество из живых существ и роботов.</p><h3 id="перезарядка" tabindex="-1">Перезарядка: <a class="header-anchor" href="#перезарядка" aria-label="Permalink to “Перезарядка:”">​</a></h3><p>При нанесении критического удара по живому существу или конструкции с подтипом «технологический» перчатка-электрофаг восстанавливает указанное в таблице количество зарядов (вплоть до максимальной ёмкости).</p><h2 id="перчатка-электрофаг-одноручное-высокотехнологичное-оружие-ближнего-боя" tabindex="-1">Перчатка-Электрофаг (Одноручное высокотехнологичное оружие ближнего боя) <a class="header-anchor" href="#перчатка-электрофаг-одноручное-высокотехнологичное-оружие-ближнего-боя" aria-label="Permalink to “Перчатка-Электрофаг (Одноручное высокотехнологичное оружие ближнего боя)”">​</a></h2><table tabindex="0"><thead><tr><th>ПРОЧЕЕ</th><th>УРОВЕНЬ</th><th>ЦЕНА</th><th>УРОН</th><th>КРИТ.</th><th>ВЕС</th><th>ОСОБЫЕ СВОЙСТВА</th></tr></thead><tbody><tr><td>Перчатка-электрофаг «Искра»</td><td>2</td><td>750</td><td>1d6 Ко &amp; Э</td><td>Перезарядка 2</td><td>Л</td><td>Аккумуляторное (ёмкость 20, расход 1)</td></tr><tr><td>Перчатка-электрофаг «Сполох»</td><td>7</td><td>6 250</td><td>2d6 Ко &amp; Э</td><td>Перезарядка 2</td><td>Л</td><td>Аккумуляторное (ёмкость 20, расход 1)</td></tr><tr><td>Перчатка-электрофаг «Роза»</td><td>13</td><td>49 000</td><td>6d6 Ко &amp; Э</td><td>Перезарядка 4</td><td>Л</td><td>Аккумуляторное (ёмкость 40, расход 2)</td></tr><tr><td>Перчатка-электрофаг «Буря»</td><td>18</td><td>375 000</td><td>12d6 Ко &amp; Э</td><td>Перезарядка 4</td><td>Л</td><td>Аккумуляторное (ёмкость 40, расход 2)</td></tr></tbody></table>',34)])])}const g=a(n,[["render",h]]);export{c as __pageData,g as default};
