import{_ as a,c as t,o as n,ah as e}from"./chunks/framework.MgE-7MWY.js";const c=JSON.parse('{"title":"Элликот [Воитель]","description":"","frontmatter":{},"headers":[],"relativePath":"content/Бестиарий/Элликот.md","filePath":"content/Бестиарий/Элликот.md"}'),o={name:"content/Бестиарий/Элликот.md"};function s(h,r,i,l,g,d){return n(),t("div",null,[...r[0]||(r[0]=[e('<h1 id="элликот-воитель" tabindex="-1">Элликот [Воитель] <a class="header-anchor" href="#элликот-воитель" aria-label="Permalink to “Элликот [Воитель]”">​</a></h1><p><strong>КО 9</strong><br> Опыт 6 400</p><p>Нейтральный гигантский волшебный зверь.<br><strong>Инициатива:</strong> +0; <strong>Восприятие:</strong> слепое чутьё (жизнь) 60 футов, сумеречное зрение; <strong>Внимание</strong> +22.<br><strong>Аура:</strong> радиация (30 футов, СЛ 17).</p><h2 id="защита" tabindex="-1">Защита <a class="header-anchor" href="#защита" aria-label="Permalink to “Защита”">​</a></h2><p>ПЗ: 145<br> ЭКБ: 22; ККБ: 24<br><strong>Стойкость</strong> +13; <strong>Реакция</strong> +13; <strong>Воля</strong> +8.</p><h2 id="атака" tabindex="-1">Атака <a class="header-anchor" href="#атака" aria-label="Permalink to “Атака”">​</a></h2><p><strong>Скорость:</strong> 50 футов.<br><strong>В ближнем бою:</strong> рога +22 (2d10 + 15 Ко) или вытягивание души +22 (см. ниже).</p><h3 id="занимаемое-пространство-20-футов" tabindex="-1">Занимаемое пространство:** 20 футов. <a class="header-anchor" href="#занимаемое-пространство-20-футов" aria-label="Permalink to “Занимаемое пространство:** 20 футов.”">​</a></h3><p><strong>Зона досягаемости:</strong> 20 футов.</p><h2 id="параметры" tabindex="-1">Параметры <a class="header-anchor" href="#параметры" aria-label="Permalink to “Параметры”">​</a></h2><p><strong>Сил:</strong> +6; <strong>Лов:</strong> +0; <strong>Вын:</strong> +4; <strong>Инт:</strong> –3; <strong>Мдр:</strong> +0; <strong>Хар:</strong> +2.<br><strong>Навыки:</strong> Атлетика +17, Выживание +17, Запугивание +17.<br><strong>Языки:</strong> эзикский (не может говорить ни на одном языке).<br><strong>Прочие способности:</strong> недышащий.</p><h2 id="экология" tabindex="-1">Экология <a class="header-anchor" href="#экология" aria-label="Permalink to “Экология”">​</a></h2><p><strong>Среда обитания:</strong> любые равнины или пустыни (Эокс).<br><strong>Численность:</strong> одиночка, пара или стадо (3–6).</p><hr><h2 id="особые-способности" tabindex="-1">Особые способности <a class="header-anchor" href="#особые-способности" aria-label="Permalink to “Особые способности”">​</a></h2><h3 id="аура-радиации-экс" tabindex="-1">Аура радиации (Экс) <a class="header-anchor" href="#аура-радиации-экс" aria-label="Permalink to “Аура радиации (Экс)”">​</a></h3><p>Из-за условий окружающей среды элликот вынужденно поглощает огромное количество радиации и приспособился запасать и перенаправлять её без вреда для себя. Элликот излучает радиацию среднего уровня на 15 футов и низкого уровня ещё на 15 футов.</p><h3 id="вытягивание-души-св" tabindex="-1">Вытягивание души (Св) <a class="header-anchor" href="#вытягивание-души-св" aria-label="Permalink to “Вытягивание души (Св)”">​</a></h3><p>В качестве основного действия элликот может ударить хоботами одно живое существо или нежить в зоне досягаемости, совершая атаку против ККБ цели. При попадании она получает 3d6 + 9 дробящего урона и становится ошеломлена на 1 раунд. Цель может пройти испытание Стойкости со СЛ 16, чтобы уменьшить урон вдвое и отменить состояние ошеломления. Элликот восстанавливает ПЗ в количестве, равном урону, который нанесён ПЗ цели (вплоть до максимума).</p><hr><h2 id="описание" tabindex="-1">Описание <a class="header-anchor" href="#описание" aria-label="Permalink to “Описание”">​</a></h2><p>Элликот вырастает до 50 футов в высоту, весит свыше 60 000 фунтов и внешне похож на странного спона с двумя хоботами, стоящего на невероятно тонких ногах-ходулях. Огромное тело элликота покрыто рогами и наростами, а кожа усыпана волдырями и трещинами и постоянно кажется ободранной и обожжённой. Виной всему радиация, которую существо накапливает и хранит внутри тела. Часть радиации — космическое излучение, заливающее лишённый атмосферы Эокс, но основной её источник — пустоши планеты. Элликот по какой-то причине привлекает радиоактивные области, сохранившие следы катаклизма, который опустошил Эокс много лет назад.</p><p>Летописи костяных старейшин указывают на то, что элликоты существовали задолго до ядерного апокалипсиса, постигшего Эокс, и некогда были травоядными, а длинные ноги и гибкие хоботы позволяли им добираться до сочных плодов, растущих на верхушках высоких колючих деревьев джикобалан. Судя по всему, во время катастрофы часть стад элликотов мутировала под действием магии. Современные элликоты больше не питаются растительной пищей и даже не дышат, как обычные животные. Они добывают пропитание, вытягивая жизненную энергию пряником из других существ, и используют её для продления собственного мучительного существования. Элликоты прекрасно усваивают и энергию некромантии, окживляющую мертвецов, и энергию душ живых существ, поэтому большую часть их рациона составляют привидения, зомби и прочая нечисть, то и дело возникающая в пустошах Эокса. Периодически элликоты, доведённые до бесчестия или отчаяния, целыми стадами прорываются через стены, защищающие эзоксийские поселения, и в безумном исступлении вытягивают энергию из всех на своём пути, пока не будут побиты местными вооружёнными силами. Костяные старейшины стараются следить за популяциями элликотов и вылавливать наиболее агрессивные стада в отдалённые пустоши, но большая часть властителей считает полезным держать небольшое количество элликотов, чтобы граждане не забывали, что даже нежити нужно сильное правительство, способное защитить.</p><p>Некоторые костяные старейшины не видят в диких элликотах особого смысла и приручают этих существ для боя, сохраняя древние традиции, появившиеся ещё до Разрыва. Среди рогов на широкой спине элликота сопружается бронированный паланкин, из которого невосприимчивые к радиации живые мертвецы обстреливают врагов, направляя боевых зверей при помощи прямой нейротехнологической связи или магии. Боевые элликоты дороги в содержании и лишены некоторых преимуществ современной техники, но благодаря своим преимуществам способны поддерживать мощную броню или закреплять на рогах тяжёлое вооружение.</p><p>Элликоты — млекопитающие и жвачеродовые существа. Обычно на свет появляется один детёныш, который через несколько лет достигает зрелости и в благоприятных условиях может жить сотни лет. В стаде элликотов нет определённого вожака, оно следует за той взрослой особью, которая движется наиболее уверенно и целенаправленно, поэтому при наступлении угрозы стадо ведёт себя непредсказуемо и хаотично. Трупы элликотов встречаются крайне редко: когда организм элликота стареет и начинает сдавать, он инстинктивно отделяется от стада и направляется к самому радиоактивному месту в округе — обычно в сердце радиоактивной пустоши, где ложится на землю и громко поёт прощальную песнь до последнего издыхания. После смерти в клетках тела элликота происходят некие процессы, связанные, вероятно, с накоплённой в них радиацией, и в результате трут за несколько часов полностью превращается в лучищу радиоактивной жижи.</p><p>Мирная миграция элликотов периодически нарушается внезапными приступами неконтролируемого бешенства. Из-за этого их считают неразумными, но они куда умнее, чем кажутся. Эти существа не умеют разговаривать, но обладают базовым пониманием языка, если постоянно слышат его. Натуралсты и дрессировщики, которые провели с элликотами достаточно времени, убеждены, что их внутренний мир гораздо богаче, чем у обычных животных. Попытки расшифровать их тоскливые завывания, распространяющиеся через почву и ядовитые остатки атмосферы планеты, пока ник к чему не привели, однако элликоты, которых обучили экзикскому языку и попросили указывать на слова и пиктограммы хоботами, привели учёных к потрясающему открытию. Хотя элликоты недостаточно умны, чтобы передавать историю устно, они каким-то образом сохраняют коллективные воспоминания о гибели планеты и предшествующей этому истории. Элликотов глубоко печалит текущее положение вещей, они с тоской вспоминают былые времена зелёных полей и высоких деревьев — времена, которые ни один ныне живущий элликот не застал. Каким образом воспоминания передаются от поколения к поколению, неизвестно даже самим элликотам, которые на вопрос «вопрос лишь снова и снова указывают на слово «ожидание».</p>',26)])])}const b=a(o,[["render",s]]);export{c as __pageData,b as default};
