import{_ as r,c as n,o as e,ah as o}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"content/Заклинания/1 Круг/Голограмма.md","filePath":"content/Заклинания/1 Круг/Голограмма.md"}'),s={name:"content/Заклинания/1 Круг/Голограмма.md"};function p(a,t,_,g,m,c){return e(),n("div",null,[...t[0]||(t[0]=[o('<h2 id="голограмма" tabindex="-1">Голограмма <a class="header-anchor" href="#голограмма" aria-label="Permalink to “Голограмма”">​</a></h2><p>[Техномант 1-6 круг] <strong>Школа:</strong> иллюзия<br><strong>Время сотворения:</strong> 1 основное действие<br><strong>Дистанция:</strong> см. текст<br><strong>Область:</strong> см. текст<br><strong>Длительность:</strong> см. текст<br><strong>Испытание:</strong> Воли разоблачает<br><strong>Устойчивость к магии:</strong> нет</p><p>Вы создаёте из фотонов иллюзорные голограммы, вид которых ограничивается лишь вашим воображением. Создаваемые голограммы обычно способны обмануть камеры, роботов и живых существ.</p><p><strong>1 круг:</strong> заклинание создаёт визуальную иллюзию в пределах дальней дистанции (400 футов + 40 футов за 1 УЗ). Иллюзия не издаёт звуков, не распространяет запахи, не имеет текстуры и температуры. Объём изображения не может превышать четырёх кубов 10×10×10 футов плюс один куб 10×10×10 футов за 1 УЗ. Голограмма существует, пока вы на ней концентрируетесь. Вы можете перемещать её в пределах области действия.</p><p><strong>2 круг:</strong> заклинание работает так же, как <em>голограмма</em> 1 круга, но может включать негромкие звуки (кроме членораздельной речи). Голограмма существует, пока вы на ней концентрируетесь и ещё 2 раунда после этого.</p><p><strong>3 круг:</strong> заклинание работает так же, как <em>голограмма</em> 1 круга, но дополнительно создаёт иллюзии звуков (кроме членораздельной речи), запахов и температуры. От прикосновения иллюзия исчезает, если вы не заставляете её реагировать соответственно. Голограмма существует, пока вы на ней концентрируетесь и ещё 3 раунда после этого.</p><p><strong>4 круг:</strong> заклинание работает так же, как <em>голограмма</em> 3 круга, но действует по заданному вами сценарию в течение 1 минуты за 1 УЗ и не требует концентрации. Эта голограмма способна воспроизводить членораздельную речь.</p><p><strong>5 круг:</strong> заклинание работает так же, как <em>голограмма</em> 4 круга, но её размер не может превышать куба 20×20×20 футов плюс один куб 10×10×10 футов за 1 УЗ.</p><p>В момент сотворения заклинания вы можете сделать голограмму постоянной. Концентрируясь, вы способны перемещать голограмму в пределах дистанции, но всё остальное время она неподвижна.</p><p>В качестве альтернативного варианта вы можете выбрать, при каких условиях появится голограмма. Условие устанавливается во время сотворения заклинания и может быть как общим, так и конкретным, но всегда должно основываться на наблюдаемых вещах — визуальных, звуковых, тактильных или обонятельных. Заклинание не реагирует на параметры, не обнаруживаемые обычным восприятием (например, мировоззрения). Заклинание действует до тех пор, пока не сработает, а после срабатывания голограмма существует 1 раунд за 1 УЗ.</p><p><strong>6 круг:</strong> заклинание создаёт вашего иллюзорного двойника в пределах средней дистанции (100 футов + 10 футов за 1 УЗ). Он выглядит как вы, издаёт такие же звуки и запахи, но при этом нематериален. Двойник повторяет ваши действия (включая речь), если только вы не прикажете ему делать что-то другое (сопутствующее действие). Вы способны смотреть его глазами и слышать его ушами, как если бы находились на его месте, и в свой ход в качестве сопутствующего действия можете переключаться между его и своими органами восприятия. Пока вы пользуетесь восприятием двойника, ваше собственное тело считается слепым и глухим.</p><p>Вы также можете, творя любое заклинание с дистанцией «касание» или более дальней, сделать так, чтобы оно исходило от двойника, а не от вас. В остальном такое заклинание работает обычным образом. Двойник не может накладывать заклинания на себя, за исключением заклинаний школы иллюзии.</p><p>Предметы автоматически разоблачают иллюзию, как если бы успешно прошли испытание Воли. Голограмма существует на протяжении 1 раунда за 1 УЗ и всё время должна находиться на вашей линии досягаемости: если что-то преграждает линию досягаемости, заклинание прекращает работать. Если вы используете <em>дверь в пространстве</em>, <em>межпланетный переход</em>, <em>телепортацию</em> или подобные заклинания, хотя бы на мгновение нарушающие линию досягаемости, заклинание также прекращает действовать. Этот вариант заклинания имеет дескриптор «тень».</p>',13)])])}const l=r(s,[["render",p]]);export{d as __pageData,l as default};
