import{_ as r,c as _,o as n,ah as e}from"./chunks/framework.MgE-7MWY.js";const l=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"content/Заклинания/5 Круг/Возвращение к жизни.md","filePath":"content/Заклинания/5 Круг/Возвращение к жизни.md"}'),o={name:"content/Заклинания/5 Круг/Возвращение к жизни.md"};function s(a,t,p,c,g,i){return n(),_("div",null,[...t[0]||(t[0]=[e('<h2 id="возвращение-к-жизни" tabindex="-1">Возвращение к жизни <a class="header-anchor" href="#возвращение-к-жизни" aria-label="Permalink to “Возвращение к жизни”">​</a></h2><p>[Мистик 5 круг] <strong>Школа:</strong> воплощение [исцеление]<br><strong>Время сотворения:</strong> 1 минута<br><strong>Дистанция:</strong> касание<br><strong>Цель:</strong> одно мертвое существо<br><strong>Длительность:</strong> мгновенная<br><strong>Испытание:</strong> нет; см. текст<br><strong>Устойчивость к магии:</strong> да (безвредно)</p><p>Вы возвращаете жизнь умершему, если он был мёртв не более чем 1 день за 1 УЗ до этого момента. Душа существа должна быть свободна и согласна вернуться. Если она не согласна, заклинание не срабатывает. Для согласной души испытаний не требуется. Для сотворения заклинания нужно подробное изображение умершего существа стоимостью не менее 5 000 кредитов, которое будет служить путеводным маяком для души умершего. После сотворения заклинания изображение исчезает.</p><p><em>Возвращение к жизни</em> не даётся легко. Воскресший получает два постоянных отрицательных уровня; если же он 1 или 2 уровня, то вместо этого его значение Выносливости навсегда снижается на 2 (если это уменьшит его Выносливость до 0 или ниже, то воскресить его невозможно). Воскрешённое существо не имеет ПР, ПЖ и ячеек заклинаний (пока не отдохнёт, чтобы их восстановить). Существо воскрешается с 5 ПЗ, значения характеристик, сниженных до 0, становятся равными 1. Обычные яды и болезни излечиваются, но магические болезни и проклятия остаются. Заклинание заживляет смертельные ранения, но тело существа должно быть целым, иначе оно вернётся к жизни без недостающих частей тела.</p><p>Заклинание не воскрешает умерших от старости и не действует на конструкции, элементалей и потусторонних существ. Если существо было обращено в нежить, его можно воскресить, но для этого потребуется более мощный маяк для души стоимостью не менее 15 000 кредитов.</p>',5)])])}const m=r(o,[["render",s]]);export{l as __pageData,m as default};
