import{_ as r,c as n,o as e,ah as o}from"./chunks/framework.MgE-7MWY.js";const l=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"content/Заклинания/5 Круг/Глаза-разведчики.md","filePath":"content/Заклинания/5 Круг/Глаза-разведчики.md"}'),_={name:"content/Заклинания/5 Круг/Глаза-разведчики.md"};function s(a,t,p,c,g,i){return e(),n("div",null,[...t[0]||(t[0]=[o('<h2 id="глаза-разведчики" tabindex="-1">Глаза-разведчики <a class="header-anchor" href="#глаза-разведчики" aria-label="Permalink to “Глаза-разведчики”">​</a></h2><p>[Техномант 5 круг] <strong>Школа:</strong> прорицание<br><strong>Время сотворения:</strong> 1 минута<br><strong>Дистанция:</strong> 1 миля<br><strong>Эффект:</strong> 20 магических сенсоров<br><strong>Длительность:</strong> 1 час за 1 УЗ (П), см. текст<br><strong>Испытание:</strong> нет<br><strong>Устойчивость к магии:</strong> нет</p><p>Вы создаете 20 полупрозрачных, видимых магических сфер (называемых «глазами»). Они двигаются, разведывают местность и возвращаются к вам, выполнив инструкции, полученные при сотворении заклинания. Каждый глаз может видеть на 120 футов (только обычным зрением) во всех направлениях. Он довольно уязвим, зато малозаметен. Каждый глаз является конструкцией крошечного размера — примерно с мелкое яблоко; у него 1 ПЗ, ЭКБ и ККБ 18, скорость полёта 30 футов, +20 к проверкам Акробатики для полёта и +16 к проверкам Скрытности. Бонус Внимания глаза равен +20, на него действуют иллюзии, тьма, туман и любые иные помехи для зрения. В темноте глазам приходится искать путь на ощупь.</p><p>Сотворяя глаза, вы даете им инструкции длиной не более 25 слов; всё, что знаете вы, знают и они. Чтобы доложить об увиденном, глазу надо вернуться к вам в руку, где он передает вам изображение увиденного со скоростью 1 час наблюдений за 1 раунд. Доложившись, глаз исчезает.</p><p>Если глаз удаляется от вас дальше чем на милю, он тут же перестаёт существовать. Однако у вас нет способа определить, пропал ли глаз потому, что оказался слишком далеко, или же по другой причине.</p><p>Глаза продолжают существовать в течение 1 часа за УЗ или пока не вернутся к вам. <em>Рассеивание магии</em> может их уничтожить; в случае рассеивания в области проверка рассеивания проходится отдельно для каждого глаза.</p>',6)])])}const m=r(_,[["render",s]]);export{l as __pageData,m as default};
