import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"АБАДАР — ВЛАДЫКА ПЕРВОГО ХРАНИЛИЩА","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Абадар.md","filePath":"content/Миры игры/Вера и религия/Абадар.md"}'),o={name:"content/Миры игры/Вера и религия/Абадар.md"};function _(s,t,p,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="абадар-—-владыка-первого-хранилища" tabindex="-1">АБАДАР — ВЛАДЫКА ПЕРВОГО ХРАНИЛИЩА <a class="header-anchor" href="#абадар-—-владыка-первого-хранилища" aria-label="Permalink to “АБАДАР — ВЛАДЫКА ПЕРВОГО ХРАНИЛИЩА”">​</a></h1><p><strong>Принципиальный нейтральный бог богатства, закона, коммерции и цивилизации</strong></p><ul><li><strong>Центры поклонения:</strong> станция «Авессалом», Актон, Верчес</li><li><strong>Символ:</strong> золотой ключ</li></ul><p>Абадар терпелив, проницателен и дальновиден. Он покровительствует распространению цивилизации по всему материальному плану, поддерживает коммерческие связи между планетами и приносит богатство тем, кто ведёт свободную торговлю и уважает власть закона. Он владыка и страж Первого Хранилища, сокровищницы в его экстрапланарном царстве, где содержатся идеальные версии каждого когда-либо созданного существа и объекта — совершенный андроид, совершенная ваза, совершенный лазерный пистолет, совершенное заклинание и даже совершенный закон.</p><p>Абадар поощряет осторожность и планирование, а импульсивность порицает. Он не вспыльчив и не тороплив, поскольку знает, что распространение цивилизации и закона постепенно, но неизбежно. Только дисциплинированный, тяжёлый труд и трезвые суждения могут достичь счастья и благосостояния. Риск иногда позволителен и может принести щедрое вознаграждение, но только после тщательного планирования и взвешивания всех за и против. Абадар не склоняется ни к добру, ни ко злу, так как у обеих сторон есть свои преимущества, но он предпочитает наёмный труд рабству, поскольку работники, которым платят зарплату, могут инвестировать накопления и, следовательно, содействуют развитию цивилизации в целом.</p><p>Самыми ярыми почитателями Абадара обычно являются аристократы, колонисты, предприниматели, юристы, политики и торговцы, однако многие бедные люди поклоняются Владыке Первого Хранилища в надежде привлечь его внимание и изменить свою судьбу. Каждый преданный Абадару ремесленник или художник старается воспроизвести совершенные формы объектов из его божественного царства в своих работах. Почитатели Абадара верят, что это он возвысил разумные расы галактики от примитивных племён до технологических цивилизаций, и многие стремятся следовать его примеру при встрече с менее развитыми существами на недавно открытых планетах, при строительстве новой колонии или установлении торговых связей.</p><p>Церковь Абадара, корпорация «Абадар» (см. стр. 476), является одновременно и религиозной организацией, и одной из самых успешных и влиятельных корпораций в мирах Соглашения. Её руководители имеют и церковный сан, а её многочисленные офисы, склады, торговые форпосты и магазины, которые можно найти почти в любом освоенном мире, также являются храмами. Здания корпорации построены в легко узнаваемом и достаточно неоднозначно воспринимаемом стиле — прочные, практичные конструкции (для защиты от воров), которые роскошно украшены (чтобы продемонстрировать богатство, часто скрытое за этими стенами). У многих зданий корпорации «Абадар» прочные полупрозрачные керамические окна, которые выкрашены в ярко-жёлтый цвет, чтобы освещать внутреннее пространство золотым светом.</p><hr>',8)])])}const f=e(o,[["render",_]]);export{m as __pageData,f as default};
