import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"БЕСМАРА — КОРОЛЕВА ПИРАТОВ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Бесмара.md","filePath":"content/Миры игры/Вера и религия/Бесмара.md"}'),o={name:"content/Миры игры/Вера и религия/Бесмара.md"};function _(s,t,p,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="бесмара-—-королева-пиратов" tabindex="-1">БЕСМАРА — КОРОЛЕВА ПИРАТОВ <a class="header-anchor" href="#бесмара-—-королева-пиратов" aria-label="Permalink to “БЕСМАРА — КОРОЛЕВА ПИРАТОВ”">​</a></h1><p><strong>Хаотичная нейтральная богиня борьбы, космических чудовищ и пиратства</strong></p><ul><li><strong>Центры поклонения:</strong> станция «Авессалом», Актон, Апостэ, Вескариум, Диаспора</li><li><strong>Символ:</strong> череп со скрещёнными костями</li></ul><p>Расцвет торговли между планетами миров Соглашения породил пиратов, охотящихся на торговые суда, а благодаря им стала известна и Бесмара. Когда-то давно она была одним из мелких морских богов Голариона, но теперь её почитают как могущественную богиню пиратов, вооружённой борьбы и инопланетных чудищ, плавящих сквозь космическую пустоту. Некоторые служители её культа утверждают, что Бесмара обрела настоящую власть и место в пантеоне богов в качестве награды после дерзкого нападения, когда она сместила другого бога, но это событие, предположительно, произошло во время Разрыва, так что это невозможно подтвердить или опровергнуть. Звездолёт Бесмары носит название «Звёздный дух» и бороздит просторы космоса и планов с верным экипажем пиратов и бесстрашных пиратов из бесчисленных миров на борту. Одно из созвездий в небесах миров Соглашения также носит название Звёздный Дух, и многие полагают, что это одно и то же.</p><p>Бесмара — дерзкая, непостоянная, жадная и похотливая богиня, но она следует собственному кодексу чести и верна своему экипажу и чужакам, пока те полезны ей и служат её интересам. Она презирает законы и цивилизацию, предпочитая свободу и счастливый случай на просторах открытого космоса, наслаждаясь лишь битвами, грабежом, риском и азартом погони. Бесмара не задаётся вопросами добра или зла, но она презирает бессмысленные убийства, которые не приносят добычи или награды. Она уважает борьбу и обманом, полагая, что если у кого-то есть то, что она хочет, она может это присвоить.</p><p>Многие вески-наёмники почитают Бесмару как богиню борьбы, но основную часть её последователей составляют пираты, контрабандисты и другие преступные элементы, наживающиеся на военных конфликтах. Даже те пираты, которые обычно презирают религию, до сих пор молятся Бесмаре, чтобы та сменила гнев на милость, или выкидывают часть добычи в открытый космос в качестве подношения, чтобы получить благословение Королевы пиратов. Впрочем, всё это скорее суеверия, а не религия, поэтому у Бесмары мало храмов, а организованной церкви как такой почти нет. Тем не менее в часто посещаемых пиратами портах и на космических станциях можно найти маленькие часовни богини, а на удалённых астероидах, космических сигнальцах и на кладбищах космических кораблей встречаются её алтари.</p><p>Большинство Вольных капитанов (см. стр. 475) почитают Бесмару хотя бы на словах, многие из них также носят с собой маленькие ценные безделушки (например, кусочек редкого минерала или старинный лазерный пистолет, украденный у знаменитого капитана), полагая, что их потеря навлечёт беду. Некоторые из этих побрякушек передаются от одного Вольного капитана другому на протяжении десятилетий.</p>',7)])])}const f=e(o,[["render",_]]);export{m as __pageData,f as default};
