import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"ВЕЙДАН — БЕСКРАЙНИЙ ГОРИЗОНТ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Вейдан.md","filePath":"content/Миры игры/Вера и религия/Вейдан.md"}'),o={name:"content/Миры игры/Вера и религия/Вейдан.md"};function _(s,t,p,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="веидан-—-бескраинии-горизонт" tabindex="-1">ВЕЙДАН — БЕСКРАЙНИЙ ГОРИЗОНТ <a class="header-anchor" href="#веидан-—-бескраинии-горизонт" aria-label="Permalink to “ВЕЙДАН — БЕСКРАЙНИЙ ГОРИЗОНТ”">​</a></h1><p><strong>Хаотичный добрый бог исследований, открытых, равенства и свободы</strong></p><ul><li><strong>Центры поклонения:</strong> станция «Авессалом», Верчес, Диаспора, «Идари»</li><li><strong>Символ:</strong> звездолёт, устремившийся в неизведанное</li></ul><p>Вейдан, в отличие от богов, начавших путь смертными и обретших божественность, действовал обратным образом и продолжает поступать так даже по сей день.</p><p>Будучи богом открытых и исследований, Вейдан постоянно отделяет осколки своей божественной сущности и создаёт из них аватаров — собственных смертных отражений любого пола, формы или расы, которые могут незамеченными путешествовать среди бесчисленных цивилизаций Материального плана, свободно от ответственности, возложенной на плечи любого бога, и способные прочувствовать все аспекты существования, недоступные сведущим божествам. Поступая так, Вейдан стремится показать основополагающее равенство всех существ, включая богов, и вдохновляет последователей относиться ко всем доброжелательно, ведь даже самый отъявленный грешник или инопланетное чудище на самом деле может оказаться тайным мессией. Аватары Вейдана время от времени раскрывают свою сущность, вступаясь за беззащитных или направляя верующих в трудные времена, но чаще всего они просто путешествуют, исследуя Мультивселенную на пределе своих смертных возможностей, пока смерть не вернёт их искру обратно в божественное пламя.</p><p>В то время как Дезна выступает за путешествия ради самих путешествий, Вейдан видит смысл в конечной цели путешествия, даже если эта цель — лишь исследование. Изучение неведомого — священный процесс, во время которого исследователь постигает не только звёздную систему или мир, но и себя самого. Вселенная таит множество чудес, только и ждущих первооткрывателей, и Вейдан радуется каждой находке последователей, неважно, мала она или значительна. Как и Дезна, он разделяет любовь к свободе и верит в право каждой разумной расы быть независимой и не скованной рамками жестоких законов. Он считает равенство всех существ любого рода занятий основной этой свободы и необходимым условием процветающего общества и не одобряет рабство и любую систему, при которой одни возвышаются за счёт эксплуатации других, из-за чего часто конфликтует с капиталистическим учением Абадара.</p><p>Посланики, исследователи, гностики, социалисты, Звёздные искатели (см. стр. 478) и колонисты, устремившиеся к недавно открытым планетам в поисках нового дома, составляют большую часть последователей и священнослужителей Вейдана. Многие люди и андроиды, особенно те, кто вступил в борьбу с притеснением, почитают Вейдана, как и молодые касаты, восставшие против суровых традиций своей культуры, и ширены, ценящие его за помощь угнетённым. Церковь Вейдана, следуя заветам равенства, практически отказалась от внутренней иерархии. Его храмы обычно расположены на звездолётах — и действующих, и списанных.</p><hr>',8)])])}const f=e(o,[["render",_]]);export{m as __pageData,f as default};
