import{_ as e,c as a,o as t,ah as n}from"./chunks/framework.MgE-7MWY.js";const c=JSON.parse('{"title":"ДРУГИЕ БОГИ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Другие Бои/Другие боги.md","filePath":"content/Миры игры/Вера и религия/Другие Бои/Другие боги.md"}'),s={name:"content/Миры игры/Вера и религия/Другие Бои/Другие боги.md"};function o(l,r,i,p,h,g){return t(),a("div",null,[...r[0]||(r[0]=[n('<h1 id="другие-боги" tabindex="-1">ДРУГИЕ БОГИ <a class="header-anchor" href="#другие-боги" aria-label="Permalink to “ДРУГИЕ БОГИ”">​</a></h1><p>Каждая культура обладает своими божествами и традициями, и хотя 20 основных богов, описанных выше, наиболее известны в мирах Соглашения, в системе Голариона поклоняются сотням других божеств, и совсем неисчислимо их количество заботится о существах по всей галактике. Ниже представлены некоторые из тех богов, независимо от их меньшего количества храмов и верующих, всё же достаточно известных. Это могут быть некогда популярные боги, поклонение которым постепенно вышло из моды, божества, в которых верит определённая раса или цивилизация, или мелкие, не особо влиятельные боги.</p><hr><h2 id="анград" tabindex="-1">АНГРАД <a class="header-anchor" href="#анград" aria-label="Permalink to “АНГРАД”">​</a></h2><p><strong>Кузнечный Огонь</strong><br><strong>Принципиальный добрый бог войны, дварфов, огня и традиций</strong></p><p>Покровитель дварфов, Тораг, исчез вместе с Голарионом во время Разрыва и оставил младшего брата Анградда приставать за роскошью дварфов. Агрессивный и воинственный Анград призывает отстаивать древние традиции дварфов и сражаться за их сохранение.</p><hr><h2 id="аршеи" tabindex="-1">АРШЕЙ <a class="header-anchor" href="#аршеи" aria-label="Permalink to “АРШЕЙ”">​</a></h2><p><strong>Дух раскрепощённости</strong><br><strong>Нейтральное доброе божество свободы, сексуальности и физической красоты</strong></p><p>Андрогинный Аршей поощряет страсть и ободряет подавленных. Он ратует за полную свободу тела и души, вдохновляя последователей испытать и познать все возможности полов, любви и сексуальности.</p><hr><h2 id="асмодеи" tabindex="-1">АСМОДЕЙ <a class="header-anchor" href="#асмодеи" aria-label="Permalink to “АСМОДЕЙ”">​</a></h2><p><strong>Князь тьмы</strong><br><strong>Принципиальный злой бог гордыни, контрактов, рабства и тирании</strong></p><p>Асмодей, владыка Преисподней, правит архидьяволами этого плана. Это бог строгой иерархии и власти сильного над слабым — всемогущий тиран, создающий и разрушающий по своему усмотрению.</p><hr><h2 id="гротус" tabindex="-1">ГРОТУС <a class="header-anchor" href="#гротус" aria-label="Permalink to “ГРОТУС”">​</a></h2><p><strong>Бог последних времён</strong><br><strong>Хаотичный нейтральный бог забвения, пустошей и развалин</strong></p><p>Гротус — неизвестное откуда взявшийся бог конца света. Он представляет собой лапку, зловещую, похожую на череп луну, зависшую над Костяным Двором Фаразмы. Он беспристрастно наблюдает за происходящим и терпеливо ждёт конца всего сущего. У Гротуса нет церкви как таковой.</p><hr><h2 id="калистрия" tabindex="-1">КАЛИСТРИЯ <a class="header-anchor" href="#калистрия" aria-label="Permalink to “КАЛИСТРИЯ”">​</a></h2><p><strong>Укол Возмездия</strong><br><strong>Хаотичная нейтральная богиня эльфов, коварства, мести и страсти</strong></p><p>Покровительница эльфов Калистрия воплощает непостоянную и отрешённую природу этой расы и их желание мстить всем, кто причинил им зло. Эта богиня импульсивна, обворожительна, склонна к интригам и всегда ищет способ достичь превосходства.</p><hr><h2 id="ламашту" tabindex="-1">ЛАМАШТУ <a class="header-anchor" href="#ламашту" aria-label="Permalink to “ЛАМАШТУ”">​</a></h2><p><strong>Матерь чудиц</strong><br><strong>Хаотичная злая богиня безумия, ночных кошмаров и чудиц</strong></p><p>Ламашту — наиболее могущественная из демонических владык бездынь. Ей подчиняются все неразумные чудовища, а её кошмары вторгаются в мирный сон. Её цель — изрвать смертных и искажить Вселенную, населить её своим бесформенным потомством.</p><hr><h2 id="лиссала" tabindex="-1">ЛИССАЛА <a class="header-anchor" href="#лиссала" aria-label="Permalink to “ЛИССАЛА”">​</a></h2><p><strong>Наследница Семерых</strong><br><strong>Принципиальная злая богиня подчинения, служения и судьбы</strong></p><p>Лиссала была древним божеством Ацланта и Тассилона, государств древнего Голариона, а ныне она покровительствует Звёздной империи Ацланта. Она сурова, педантична и не терпит ослушания, но вознаграждает правителей и их подданных за чёткое следование её учению.</p><hr><h2 id="стареишие" tabindex="-1">СТАРЕЙШИЕ <a class="header-anchor" href="#стареишие" aria-label="Permalink to “СТАРЕЙШИЕ”">​</a></h2><p><strong>Владыки фей</strong><br><strong>Нейтральные правители фей Первого мира</strong></p><p>Старейшие — правящий Первым миром пантеон из девяти повелителей фей, в который в том числе входят Зелёная Матерь, Светоч, Скрытый Иг и Многоликий Шика. Феи, гномы и другие последователи часто заключают сделки с кем-то из них, но иногда и поклоняются всем Старейшим в целом. Наиболее почитаемым из них можно назвать Радагана, Повелителя вод, который является основным божеством водного мира Кало-Махой.</p><hr><h2 id="черная-бабочка" tabindex="-1">ЧЁРНАЯ БАБОЧКА <a class="header-anchor" href="#черная-бабочка" aria-label="Permalink to “ЧЁРНАЯ БАБОЧКА”">​</a></h2><p><strong>Молчание Пустоты</strong><br><strong>Хаотичная добрая богиня космоса, расстояний и тишины</strong></p><p>Звёзды принадлежат Дезне, а Чёрной Бабочке — тёмный космос между ними, поэтому её иногда называют Тенью Дезны. Она презирает зло и учит, что в тишине между вздохами и мыслями можно увидеть свою истинную природу.</p><hr><h2 id="внешние-боги" tabindex="-1">ВНЕШНИЕ БОГИ <a class="header-anchor" href="#внешние-боги" aria-label="Permalink to “ВНЕШНИЕ БОГИ”">​</a></h2><p><strong>Самый известный Внешний бог — Ньярлатхотеп, но он далеко не единственный из этих непостижимых чужеродных существ, чьё влияние достигает миров Соглашения. Даймонический супант Азатот — хаотичный нейтральный Внешний бог безумия, бессмысленного разрушения и энтропии, первородное средоточие стихийной мощи и неконтролируемого разрушения, расположенное в самом центре Вселенной. Так называемый «Слепой безумный бог» не ведает о своих почитателях, а его бездумные вселенские энергии опустошают цепь планеты. Шуб-Ниггурат — хаотичная злая чудовищная богиня плодородия, породившая, согласно поверьям, тысячи жутких детёнышей. Также известная как Чёрная Коза Чашиб, она является Внешней богиней лесов, плодородия и чудовищ, а её культ распространён на плодородных мирах, покрытых лесами, джунглями или болотами. Иог-Сотот — хаотичный нейтральный Внешний бог врат, времени и пространства, обитающий в Тёмной Завесе, хотя самые рьяные его адепты утверждают, что он сам и есть Тёмная Завеса. Иог-Сотот также известен как Ключ и Врата и существует в всём пространстве и времени. Его культists верят, что он постепенно готовит разные миры Вселенной к новой эре ужаса, которую предвозвестит пробуждение Великих Древних, богоподобных существ, служащих Внешним богам.</strong></p><hr><h1 id="даикицу-daikitsu" tabindex="-1">Дайкицу (<a href="https://2e.aonprd.com/Deities.aspx?ID=174" target="_blank" rel="noreferrer">Daikitsu</a>) <a class="header-anchor" href="#даикицу-daikitsu" aria-label="Permalink to “Дайкицу (Daikitsu)”">​</a></h1><p>Госпожа Лис (Lady of Foxes)</p><ul><li>Н</li></ul><p>Дайкицу широко поклоняются в Тянь Ся, поскольку она является богиней риса, а также сельского хозяйства и ремесленничества. Фермеры молятся Дайкицу о хороших урожаях, кузнецы и ремесленники ищут ее благословения для своих творений, а семьи просят ее защиты для своих домов и семей. Еще она является покровительницей <a href="https://pf2e-ru-translation.readthedocs.io/ru/latest/ancestries_and_backgrounds/ancestries/kitsune.html#ancestry-kitsune" target="_blank" rel="noreferrer">Кицунэ (Kitsune)</a>, которые почитают ее как мать своего народа.</p><p>Известная как Госпожа Лис, она обычно выглядит как красивая женщина-кицунэ с белоснежным мехом и девятью хвостами.</p><p>(Описание взято из 1E. Актуальное для 2E на данный момент отсутствует)</p><p><strong>Эдикты</strong>: Обеспечивать здоровье посевов и растений, совершенствовать ремесло или торговлю, оставлять подношения лисам, праздновать смену времен года</p><p><strong>Анафема</strong>: Плохо обращаться со своими инструментами; пройти мимо нищего, не подавая милостыню; дискриминировать секс-работников или низший класс</p><p><strong>Сферы интересов</strong>: Сельское хозяйство, ремесло, кицунэ, рис</p><p><strong>Мировоззрение последователей</strong>: ПН, НД, Н, ХН (LN, NG, N, CN)</p><p><strong>Царство</strong>: <a href="https://pf2e-ru-translation.readthedocs.io/ru/latest/game_mastering/tools/The-Planes.html#plane-nirvana" target="_blank" rel="noreferrer">Нирвана (Nirvana) / План</a></p><p><strong>Источник</strong>: Lost Omens: Gods &amp; Magic pg. 132-133, Dragon Empires Gazetteer pg. 59</p><h2 id="преимущества-последователя-devotee-benefits" tabindex="-1">Преимущества последователя (Devotee Benefits)<a href="https://pf2e-ru-translation.readthedocs.io/ru/latest/lost_omens/Deity/Tian-God/Daikitsu.html#devotee-benefits" title="Ссылка на этот заголовок" target="_blank" rel="noreferrer"></a> <a class="header-anchor" href="#преимущества-последователя-devotee-benefits" aria-label="Permalink to “Преимущества последователя (Devotee Benefits)”">​</a></h2><p><strong>Источник</strong>: Lost Omens: Gods &amp; Magic pg. 132-133</p><p><strong>Божественная характеристика</strong>: Ловкость или Интеллект</p><p><strong>Божественный навык</strong>: Ремесло</p><p><strong>Предпочитаемое оружие</strong>: цеп</p><p><strong>Домены</strong>: <a href="https://pf2e-ru-translation.readthedocs.io/ru/latest/lost_omens/Domain/change.html" target="_blank" rel="noreferrer">Изменение (Change)</a>, <a href="https://pf2e-ru-translation.readthedocs.io/ru/latest/lost_omens/Domain/creation.html" target="_blank" rel="noreferrer">Творчество (Creation)</a>, <a href="https://pf2e-ru-translation.readthedocs.io/ru/latest/lost_omens/Domain/family.html" target="_blank" rel="noreferrer">Семья (Family)</a>, <a href="https://pf2e-ru-translation.readthedocs.io/ru/latest/lost_omens/Domain/nature.html" target="_blank" rel="noreferrer">Природа (Nature)</a></p><p><strong>Божественная сила</strong>: <em>исцеляющая</em></p><p><strong>Заклинания жреца</strong>:</p><p><strong>1-й</strong>: <a href="https://pf2e-ru-translation.readthedocs.io/ru/latest/spells/P/pest-form.html#spell-p-pest-form" target="_blank" rel="noreferrer">Форма вредителя (Pest Form) / Закл. 1</a></p><p><strong>2-й</strong>: <a href="https://pf2e-ru-translation.readthedocs.io/ru/latest/spells/H/humanoid-form.html#spell-h-humanoid-form" target="_blank" rel="noreferrer">Форма гуманоида (Humanoid Form) / Закл. 2</a></p><p><strong>5-й</strong>: <a href="https://pf2e-ru-translation.readthedocs.io/ru/latest/spells/I/illusory-scene.html#spell-i-illusory-scene" target="_blank" rel="noreferrer">Иллюзорная сцена (Illusory Scene) / Закл. 5</a></p>',65)])])}const f=e(s,[["render",o]]);export{c as __pageData,f as default};
