import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"ЗОН-КУТОН — ВЛАДЫКА ПОЛУНОЧИ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Зон-Кутон.md","filePath":"content/Миры игры/Вера и религия/Зон-Кутон.md"}'),_={name:"content/Миры игры/Вера и религия/Зон-Кутон.md"};function o(p,t,s,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="зон-кутон-—-владыка-полуночи" tabindex="-1">ЗОН-КУТОН — ВЛАДЫКА ПОЛУНОЧИ <a class="header-anchor" href="#зон-кутон-—-владыка-полуночи" aria-label="Permalink to “ЗОН-КУТОН — ВЛАДЫКА ПОЛУНОЧИ”">​</a></h1><p><strong>Принципиальный злой бог боли, зависти, тьмы и утрат</strong></p><ul><li><strong>Центры поклонения:</strong> Актон, Апоста, Верчес, Диаспора, Докс</li><li><strong>Символ:</strong> череп с цепями в глазницах</li></ul><p>Давным-давно Зон-Кутон звался Ду-Брал и был сводным братом богини любви и красоты, но зависть к её талантам заставила его отправиться в путешествие в неизведанные края за границами Великого Запрета. Там ему встретилось нечто, что изменило его к худшему, и после возвращения он стал новым богом боли, страданий и потерь.</p><p>Зон-Кутон двуличен, жесток и завистлив. Он оскверняет плоть, чтобы сеять боль и унижение, и олицетворяет тяжёлые утраты, сопутствующие зависти, эмоциональную тьму и по-стоянную боль. Он — неисправимое зло, и причиняемая другим боль доставляет ему лишь краткое удовольствие.</p><p>Само его существование — это разложение и паразитирование на теле Вселенной. Его разум непрестанно ищет новые способы притеснения, унижения, разрушения и уничтожения других, однако его истинные цели непостижимы. Владыка Полуночи не предлагает великой мудрости, не обещает открыть истину и не гарантирует наград в загробном мире. Возможно, его безрассудственный нигилизм — лишь часть тщательно продуманного плана, неведомого даже его самым ярым служителям, но до сих пор смысл его учения заключался в том, что само существование есть боль.</p><p>Культ Зон-Кутона привлекает злобных садистов, умалишённых мазохистов и тех, чья душа так сильно ранена, что только всепоглощающая боль отвлекает их от печали. Он подстёгивает разум серийных убийц, направляет руки палачей, играет на нервах страдающих. Он покровительствует рабовладельцам, подпольным хирургам и всем тем, кто сошёл с ума от ревности и утраты, потеряв себя настолько, что утешение им приносит лишь боль, а радость — только страдания других. Его служители, известные как Слуги Полуночи, стремятся проникнуть за край Великого Запрета и достичь того же, что и Зон-Кутон.</p><p>В его учении нет глобальных заповедей, помимо принципа боли, но его церкви удаётся оставаться многочисленной. Во многом это происходит благодаря изобретениям Веселчаков — добровольных ампутантов, которые из-за отсутствия конечно-стей или органов чувств посвящают всё своё время изобретению всё более зловещих устройств для кутонаских торговцев оружием (например, теневых двигателей, которыми до открытых потока пользовались те, для кого скорость была важнее немыслимой боли). Храмы Зон-Кутона выглядят и действуют как пыточные камеры, а обряды поклонения состоят из пыток и самоистязания, размывающих грань между удовольствием и болью.</p><p>О сводной сестре Зон-Кутона, Шелин, известно лишь немногое. Иногда она даёт себе знать, но её ответы последователям редки и обрывочны. Многие полагают, что она путешествует за пределами изученной Мультивселенной в поисках исцеления древнего и загадочного недуга, поразившего её брата.</p>',9)])])}const f=e(_,[["render",o]]);export{m as __pageData,f as default};
