import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"ИБРА — НЕПОСТИЖИМЫЙ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Ибра.md","filePath":"content/Миры игры/Вера и религия/Ибра.md"}'),o={name:"content/Миры игры/Вера и религия/Ибра.md"};function p(s,t,_,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="ибра-—-непостижимыи" tabindex="-1">ИБРА — НЕПОСТИЖИМЫЙ <a class="header-anchor" href="#ибра-—-непостижимыи" aria-label="Permalink to “ИБРА — НЕПОСТИЖИМЫЙ”">​</a></h1><p><strong>Нейтральный бог загадок Вселенной, космоса и небесных тел</strong></p><ul><li><strong>Центры поклонения:</strong> Верчес, «Идари», Лиавара</li><li><strong>Символ:</strong> стрела и круг с созвездием внутри</li></ul><p>Вселенная полна удивительного, а Ибра — бог этих чудес: далёких галактик, подёрнутых дымкой туманностей, миров, кружащихся около бесчисленных солнц, и необозримого пространства между ними. Наука может объяснить всё это, но великолепие космоса не знает границ, и всегда находятся новые чудеса и тайны, притаившиеся в ожидании первооткрывателей. Сам Ибра — одна из таких загадок Вселенной. У него нет поля или определённой формы. Неясно, возникло ли это божество вместе с рождением Вселенной, или это некое совершенное существо, получившее божественный статус, или, быть может, оно — олицетворение самого космоса.</p><p>Последователи Ибры верят, что можно обрести смысл, истину и умиротворение в красоте космоса, но для этого нужно постараться и разгадать тайны Вселенной. Ибра не даёт ответы на великие вопросы сотворения, но поощряет тех, кто расширяет границы науки и знаний, исследуя Вселенную и открывая её чудеса. В понимании Ибры не существует ни хаоса, ни порядка, ни добра, ни зла. Есть только вопросы, заданные сиянием новорождённой звезды, завораживающим танцем небесных тел и полным одиночеством кометы, проносящей темноту, при этом неважно, получат ли они когда-нибудь ответы или нет.</p><p>Астрономы, астрофизики, космологи, математики и межзвёздные исследователи считают Ибру покровителем, а многие мистики и солярионы служат священниками Непостижимого. Храмы Ибры чаще встречаются в космосе, нежели на планетах. В них всегда есть обсерватории, где его почитатели могут изучать космос и вести записи наблюдений и теорий, причём некоторые из обсерваторий ничем не отделены от пустоты космоса.</p><p>Никто в мирах Соглашения не знает, чем были первые почитатели Ибры, так как самые древние из известных монументов, посвящённых этому божеству, представляли собой огромные храмовые комплексы, расположенные на необитаемых мирах, включая крошечную луну Бреседы, Чамари.</p><p>Священный символ Ибры был впервые обнаружен в старинных храмах. Он непостижим так же, как и сам бог: считается, что золотистый круг — это глаз, компас или планета, а стрела указывает верующему путь вперёд, в неизвестное.</p><p>Чуть менее загадочно созвездие из шести звёзд, заключённое внутри символа, хотя и по его поводу возникают вопросы. Что это за созвездие, которого нет на небосводе ни одного из известных миров? Что оно означает? Каково значение меньших по размеру звёзд внутри символа? Некоторые священники Ибры посвящают жизнь исследованию новых миров в поисках схожих созвездий. По слухам, это созвездие видно только с Голариона, так что символ является ключом, позволяющим его найти.</p><hr>',10)])])}const f=e(o,[["render",p]]);export{m as __pageData,f as default};
