import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"ИОМЕДЭЙ — ДУША ГОЛАРИОНА","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Иомедэй.md","filePath":"content/Миры игры/Вера и религия/Иомедэй.md"}'),o={name:"content/Миры игры/Вера и религия/Иомедэй.md"};function _(p,t,s,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="иомедэи-—-душа-голариона" tabindex="-1">ИОМЕДЭЙ — ДУША ГОЛАРИОНА <a class="header-anchor" href="#иомедэи-—-душа-голариона" aria-label="Permalink to “ИОМЕДЭЙ — ДУША ГОЛАРИОНА”">​</a></h1><p><strong>Принципиальная добрая богиня доблести, справедливости, человечества и честной битвы</strong></p><ul><li><strong>Центры поклонения:</strong> станция «Авессалом», Актион, Вескариум, Триакс</li><li><strong>Символ:</strong> длинный меч в сиянии солнца</li></ul><p>Иомедэй была смертной, рождённой на Голарионе, и стала богиней задолго до Разрыва. Когда люди покинули родную планету и отправились к звёздам, они унесли веру в Иомедэй в своих сердцах. С исчезновением Голариона его связь с богиней приобрела большее значение, и она стала почитаться как Душа Голариона, покровительница человечества. Иомедэй — добродетельный рыцарь, бесстрашная в бою, проповедующая благородное господство добра и сражающаяся за него. Она олицетворяет славу, честь, справедливость, силу и доблесть и бесстрашно обрушивает меч на величайшее зло, угрожающее человечеству и мирам Соглашения.</p><p>Иомедэй одновременно присущи и отчаянная воинственность, и мудрое благородство, она внушает уважение любому божеству, которое имеет с ней дело. Она считает, что её смертные слуги должны проявлять учтивость и вправе ожидать того же в ответ. Она яростно жаждет справедливости для всех, достойного поведения от каждого и храбрости в бою, вне зависимости от ситуации.</p><p>Иомедэй учит, что лучше заставить злодеев сложить оружие и достойно сдаться, чем убить их, но разрешает последователям вести непрекращающуюся борьбу против тех, кто продолжает нести зло.</p><p>Как покровительница человечества, Душа Голариона считает людей своими основными почитателями, однако многих весков и касат также привлекает её кодекс чести. Обычный последователь Иомедэй благоразумен и трудолюб, готов помочь другим и принять предложенную помощь.</p><p>Крестоносцы, посланники, солдаты и солдаты почитают Иомедэй, и многие используют в бою различные варианты священного оружия богини — длинного меча. Её священнослужители борются с притеснением и стремятся изменить Вселенную к лучшему, при необходимости прибегая к силовым методам.</p><p>Церковь Иомедэй содержит несколько рыцарских орденов, в частности, Рыцарей Голариона (см. стр. 479), на могучих кораблях-соборах патрулирующих миры Соглашения и далёкие звёздные системы, где обосновались люди, и неустанно сражающихся со злом.</p><p>Последователи Иомедэй сооружают большие храмы в местах своего поселения на других планетах, лунах и больших астероидах, чтобы иметь возможность восхвалять богиню. Обычно эти здания выполнены в стиле церквей старого Голариона, с большими фонтанами, устремлёнными ввысь арками и стенами, похожими на белый камень. Несмотря на внешний вид, храмы не испытывают недостатка в современных технологических удобствах, так как нередко используются рыцарями-крестоносцами в качестве тренировочных площадок, восстановительных баз или временного жилья.</p>',10)])])}const f=e(o,[["render",_]]);export{m as __pageData,f as default};
