import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"ЛАО ШУ ПО — БАБУШКА-КРЫСА","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Лао Шу По.md","filePath":"content/Миры игры/Вера и религия/Лао Шу По.md"}'),_={name:"content/Миры игры/Вера и религия/Лао Шу По.md"};function o(p,t,s,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="лао-шу-по-—-бабушка-крыса" tabindex="-1">ЛАО ШУ ПО — БАБУШКА-КРЫСА <a class="header-anchor" href="#лао-шу-по-—-бабушка-крыса" aria-label="Permalink to “ЛАО ШУ ПО — БАБУШКА-КРЫСА”">​</a></h1><p><strong>Нейтральная злая богиня воров, крыс, убийц и шпионов</strong></p><ul><li><strong>Центры поклонения:</strong> станция «Абессалом», Актон, Диаспора</li><li><strong>Символ:</strong> свернувшаяся клубком измождённая крыса</li></ul><p>Легенда гласит, что давным-давно Лао Шу По была простой крысой, шныряющей среди теней. Однажды ей довелось погрызть тело умершего бога, после чего она впитала в себя силу божественной сущности и сама стала богиней. Сначала она была богиней ночи, но затем обратила свой взор на тех, кто рыскает под покровом темноты. В её власти отравленный кубок, кинжал за спиной, заминированный реактор корабля. Её глаза и уши всегда открыты, чтобы в подходящий момент услышать неосторожное слово, за которым кроется тайна, и обратить её в свою пользу.</p><p>Лао Шу По помнит о своём скромном происхождении и учит, что те, у кого ничего нет, должны делать всё возможное для выживания, будь то убийство, диверсия или кража. Вселенная ничего не даёт даром, и личное дело каждого забрать то, что ему нужно или что он хочет, а затем надёжно спрятать. Иногда можно объединиться с другими для нападения или защиты, как стая крыс, но всецело можно полагаться только на себя. Всегда найдётся лжец или предатель, поэтому нужно постоянно быть начеку, чтобы солгать или предать до того, как это сделает кто-то другой.</p><p>Убийцы, контрабандисты, шпионы, воры и все, кто действует скрытно под покровом темноты и избегает внимания властей, поклоняются Лао Шу По. Многие считают её покровительницей исоки, но большинство крысиного народа молится Бабушке-крысе, только чтобы задобрить её и избежать её гнева, а не потому, что разделяют её ценности. Тем не менее многие её священники — исоки, которые развивают сеть связей в преступном мире на благо своих общин.</p><p>Храмы и алтари Лао Шу По малочисленны, малы и неприятны и часто скрыты в самых очевидных местах — аптека на задворках, где продают яды, тёмная комната в дальнем углу наркопритона, неиспользуемый отсек в секторе переработки отходов космической станции или узкое переплетение технических туннелей прямо под улицами оживлённого рынка.</p><p>Хотя многие сообщества истребляют и искореняют культ Лао Шу По, устраивая своего рода дератизацию, всегда находятся те, кто ценил умение её последователей доводить начатое до конца. Глуп тот, кто всецело доверяет адептам Лао Шу По, которые называют себя «внуками», однако обычно они остаются преданными работодателям или делу до тех пор, пока это соответствует их собственным целям.</p><p>Некоторые из внешне безупречных правительств тайно занимают шпионов и убийц богини, а корпорации хорошо платят им за промышленный шпионаж. В некоторых случаях они даже могут действовать открыто: трудно осудить тех, кто взрывает корабль врага или освобождает заложников из защищённого укрытия, какими бы причинами они при этом ни руководствовались.</p><hr>',10)])])}const f=e(_,[["render",o]]);export{m as __pageData,f as default};
