import{_ as e,c as a,o as n,ah as _}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"ПОЖИТАТЕЛЬ — ПОГЛОТИТЕЛЬ ЗВЁЗД","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Пожиратель.md","filePath":"content/Миры игры/Вера и религия/Пожиратель.md"}'),r={name:"content/Миры игры/Вера и религия/Пожиратель.md"};function o(s,t,p,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[_('<h1 id="пожитатель-—-поглотитель-звезд" tabindex="-1">ПОЖИТАТЕЛЬ — ПОГЛОТИТЕЛЬ ЗВЁЗД <a class="header-anchor" href="#пожитатель-—-поглотитель-звезд" aria-label="Permalink to “ПОЖИТАТЕЛЬ — ПОГЛОТИТЕЛЬ ЗВЁЗД”">​</a></h1><p><strong>Хаотичный злой бог разрушения, сверхновых звёзд и чёрных дыр</strong></p><ul><li><strong>Центры поклонения:</strong> Актон, Апоста, Верчес, Диаспора, Докс</li><li><strong>Символ:</strong> чёрная дыра, слегка окрашенная красным</li></ul><p>Давным-давно Материальный план возник в одно мгновение, и когда-нибудь он столкнётся с внезапным исчезновением, что за канчивает своё существование всё живое. Некоторые верят, что в момент создания возник и Пожиратель, а кто-то — что он является призраком некоей сущности из предыдущего витка существования. В любом случае Пожиратель стремится приблизить неизбежный конец и разрушить целый план, уничтожая всё сущее, пока не останется ничего, кроме самого Пожирателя. Считается, что любая сломавшаяся машина, умершее существо, каждая звезда, становящаяся сверхновой, каждый фотон, про- павший в чёрной дыре, каждый затухающая галактика — дело рук Пожирателя.</p><p>У Пожирателя нет имени, формы или сущности. Он скорее безличная сила Вселенной, чем бог — воплощение зловещей антропии, занятое лишь уничтожением всего сущего. Он не обращает внимания на бессмысленное существование жизни во всех её проявлениях, его нельзя убедить, задержать или остановить, он почти всегда игнорирует мольбы и молитвы даже самых ярых своих адептов. Некоторые космологи и теологи выдвигают теорию о том, что когда Пожиратель поглотит всё и мир смертных исчезнет, он породит новую Вселенную вместо старой. Обезумевшие члены культа, почитающие Поглотителя звёзд как божество, знают, что это всего лишь глупая ложь во спасение и на самом деле не будет ни перерождения, ни второго создания — только неизмеримая пустота небытия.</p><p>У церкви Пожирателя почти отсутствует и структура, и руководство, ведь она состоит из разрозненных, хаотичных ячеек, куда входят берсерки, канибалы, заговорщики-нигилисты, безразветвенные грабители и безжалостные солдаты. Многие сторонники Пожирателя нападают на всех разумных существ на своём пути, наслаждаясь разрушением и хаосом, но некоторые ведут себя хитрее и тщательно планируют нападения, кропотливо готовят катастрофы и вызывают стихийные бедствия, чтобы максимизировать число жертв, урон зданиям и ресурсам.</p><p>Культ Пожирателя запрещён почти во всех цивилизованных мирах, но его тайные алтари можно найти на отдалённых астероидах и покинутых лунах, а многие секты собирают разношёрстные флотилии звездолётов, атакуя и убивая каждого, кто встретится им в глубинах космоса.</p><p>Хотя Пожирателя логично представить в виде простой чёрной точки или воронки — обозначения конечного коллапса пространственно-временного континуума, его наиболее распространённым символом служит Кровавая дыра — чёрная дыра с красной спиралью, уходящей в её разрastaющийся диск. Культисты верят, что, когда наступит конец, само бытие будет истекать кровью богов перед тем, как обратиться в ничто.</p><p>Подробнее о культe Пожирателя можно прочесть на стр. 497.</p>',9)])])}const f=e(r,[["render",o]]);export{m as __pageData,f as default};
