import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"САРЕНРЭЙ — ЦВЕТОК ЗАРИ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Саренрэй.md","filePath":"content/Миры игры/Вера и религия/Саренрэй.md"}'),o={name:"content/Миры игры/Вера и религия/Саренрэй.md"};function _(s,t,p,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="саренрэи-—-цветок-зари" tabindex="-1">САРЕНРЭЙ — ЦВЕТОК ЗАРИ <a class="header-anchor" href="#саренрэи-—-цветок-зари" aria-label="Permalink to “САРЕНРЭЙ — ЦВЕТОК ЗАРИ”">​</a></h1><p><strong>Нейтральная добрая богиня искупления, исцеления и солнца</strong></p><ul><li><strong>Центры поклонения:</strong> Абалон, станция «Авессалом», Верчес, Солнце</li><li><strong>Символ:</strong> ангел с пылающими крыльями</li></ul><p>Тысячелетия назад Саренрэй была ангелом, приносящим солнечный свет, истину и честность на Голарион и соседние миры. Она сразилась с силами зла, жаждущими обратить новорождённые планеты в вечную тьму, и одержала победу — хотя некоторые из тех, кто обратился ко злу, благодаря её осознали свою неправоту и воспевали были помилованы. Саренрэй добра и полна любви — она само воплощение исцеления, наставничества и света. Несмотря на сострадание, Саренрэй — могучая сила в злом и беспощадная к тем, кого не исправит.</p><p>Саренрэй считается богиней Солнца миров Соглашения, и её почитатели иногда называют его звездой Цветка Зари в её честь, однако она черпает свою силу со всех солнц во Вселенной. Она божество безграничной любви — заботливая мать, сестра и защитница всех нуждающихся. Она находит удовольствие в исцелении больных, помощи обездоленным и способна озарить путеводным светом даже самые тёмные сердца и миры. Саренрэй не принимает всерьёз обиды, но на насилие и жестокость по отношению к беззащитным отвечает очищающим огнём и опалляющим светом. Древняя и нестареющая Саренрэй бесстрашно противостоит наступлению тьмы, обещая, что утренняя заря всегда настанет и надежда, доброта и истина восторжествуют.</p><p>Цветку Зари поклоняются повсеместно — многим дано лицезреть её свет, находить спокойствие в любви и сострадании и истово верить в искупление и праведные поступки. Хотя люди часто называют Саренрэй одной из своих богинь, традиция поклонения солнцу присуща всем, кто питает и согревает солнечный свет, поэтому большинство цивилизаций в мирах Соглашения и за их пределами в какой-то момент истории почитали Саренрэй. Среди привлечённых учением Саренрэй об искуплении и сострадании вторыми поселенцами по следам людей по численности идут ширрены. Многие её священники являются посланниками, мистиками, солярионами или солдатами — теми, кто проповедует мир и доброту, пока неисправимое зло не побудит их к действию.</p><p>Там, где это возможно, храмы Саренрэй открыты небу, в них всегда есть хотя бы световое окно или стеклянная крыша, благодаря которым верующие могут купаться в лучах солнечного света.</p><p>Берег Рассвета, главный город Пылающего Архипелага на самом Солнце, вмещает сердце церкви Саренрэй в мирах Соглашения. Многие сарениты системы чувствуют внутреннюю потребность совершить паломничество в это святое место, хотя каждый чтит Саренрэй по-своему, а церковь в основном служит для организации и обучения священников и тех последователей, кто затем отправится в путешествие по галактике, служа богине и неся её учение.</p><hr>',9)])])}const f=e(o,[["render",_]]);export{m as __pageData,f as default};
