import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"ТАЛАВЕТ — СКАЗИТЕЛЬНИЦА","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Талавет.md","filePath":"content/Миры игры/Вера и религия/Талавет.md"}'),o={name:"content/Миры игры/Вера и религия/Талавет.md"};function _(p,t,s,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="талавет-—-сказительница" tabindex="-1">ТАЛАВЕТ — СКАЗИТЕЛЬНИЦА <a class="header-anchor" href="#талавет-—-сказительница" aria-label="Permalink to “ТАЛАВЕТ — СКАЗИТЕЛЬНИЦА”">​</a></h1><p><strong>Принципиальная нейтральная богиня самодостаточности, содружества и традиций</strong></p><ul><li><strong>Центры поклонения:</strong> Актон, Бреседа, Вескариум, «Идари», Триакс</li><li><strong>Символ:</strong> древний касатский знак содружества</li></ul><p>Касаты привержены обычаям, истории, традициям, как и их богиня Талавет. На протяжении веков касатские сказители собирали кланы вокруг огня и передавали им историю прошлого, традиции клана и историю народа.</p><p>Согласно преданию, те самые первые рассказчики начали обретать форму, дыхание жизни и известность благодаря смешению идей и фундаментальной магии, присущей языку и общению. Так была рождена Талавет — не просто сказательница, но сам рассказ, воплощение всех легенд и преданий расы касат. Она — общая память, образ всего, что связывает касат (а в некоторой степени и все расы) вместе и роднит с домом их предков.</p><p>Талавет учит, что традиции — самое важное звено в цепи истории, связующей общество в целом, а древние мифы, легенды и рассказы образуют твёрдую почву для упорядоченного общественного строя. Естественно, Разрыв стал настоящим ударом для её церкви, и многие рассматривают его как форму божественного наказания, но спустя века расколотая община восстановилась, став ещё сильнее после событий, которые теперь называют Временем тишины. Её последователи верят в то, что для общего блага нужно делиться друг с другом мудростью и искать в прошлом направление для движения в настоящем и будущем.</p><p>Однако, особенно после событий Разрыва, они знают, что иногда нельзя рассчитывать на записи или помощь общества, поэтому каждый должен учиться полагаться на себя и собственные традиции подобно тому, как юные касаты проходят Закалку, чтобы познать себя и стать взрослыми.</p><p>Неудивительно, что касаты составляют большинство последователей и священников Талавет, однако вера в Сказительницу обрела популярность среди ширренов и исоки, у которых сильны семейные и общественные узы, а также среди весков, в чьих душах находится отклик учение Талавет о важности традиций и расчёте только на собственные силы. Те же заповеди привлекают в церковь Талавет посланников, оперативников и солдатов, продолжающих традицию древней касатской философии Цикла.</p><p>Храмы Талавет обычно выполнены в архитектурном стиле, соответствующем местным традициям, но алтарём Сказительницы может служить даже простой очаг. В полноценных церквах он всегда горит, создавая красные отблески, которые вызывают в памяти образ родной звезды касат.</p><p>Церковь Талавет с распространёнными обывателями принимает последователей других религий, ведь даже самые жестокие из них сплачивают и удерживают верующих вместе, но ведёт длительную вражду с церковью Ньярлатхотепа из-за того, что его культисты ищут истории и превращают их в страшные тайны и заговоры.</p>',10)])])}const f=e(o,[["render",_]]);export{m as __pageData,f as default};
