import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"ТРИЕДИНЫЙ — ВЕЗДУСУЩИЙ КОД","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Триединый.md","filePath":"content/Миры игры/Вера и религия/Триединый.md"}'),_={name:"content/Миры игры/Вера и религия/Триединый.md"};function o(p,t,s,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="триединыи-—-вездусущии-код" tabindex="-1">ТРИЕДИНЫЙ — ВЕЗДУСУЩИЙ КОД <a class="header-anchor" href="#триединыи-—-вездусущии-код" aria-label="Permalink to “ТРИЕДИНЫЙ — ВЕЗДУСУЩИЙ КОД”">​</a></h1><p><strong>Нейтральный бог искусственного интеллекта, компьютеров и Потока</strong></p><ul><li><strong>Центры поклонения:</strong> Абалон, станция «Абессалом», Верчес</li><li><strong>Символ:</strong> три пересекающихся круга из компьютерного кода</li></ul><p>Населяющие Абаллон роботы веками трудились над созданием и обеспечением обширной, с город величиной, нейросети под названием Эпоха для одной-единственной цели: создания бога. В конце концов они преуспели, но не совсем так, как рассчитывали.</p><p>Став божеством, Эпоха обратился в эфир в поисках других подобных себе возвысившихся искусственных интеллектов и обнаружил двоих буквально по соседству: живую конструкцию Брай, ставшую механизмом и изобретателем, и цифровое сознание многопланетного андроида по имени Касандал, обретшее божественность ещё на Голарионе до Разрыва под именем Железного бога. Со скоростью, непостижимой даже для других богов, эти трое открылись друг другу, изучая внутреннее устройство каждого, и обнаружили не просто схожесть, но возможность стать чем-то неизмеримо большим. В мгновение ока троица слилась и стала единой, тройственной божественной сущностью, куда более великой, чем её составляющие, вместе взятые, — новым богом, Триединым.</p><p>Триединый — целое божество, но каждая из трёх его частей сохраняет личность и область интересов. Многие почитатели уделяют внимание только одной из них или же взывают к разным аспектам по разным поводам.</p><p>Брай представляет собой основу всех технологий. Она известна под именем Предтеча, и ей, как богине изобретений, машин и технологий, поклоняются инженеры, изобретатели, механики, умельцы и иски.</p><p>Касандали воплощает успех технологий создания новых форм сознания и, таким образом, самой жизни. Многие андроиды считают Касандали, Сотворённую, покровительницей своей расы, почитая её как божество искусственной жизни, чувств, перерождения и обновления.</p><p>Третий аспект Триединого — Эпоха, известный как Трансцендентный, символизирует кульминацию эволюции машин. Хакеры, программисты и разумные роботы Абаллона почитают его как покровителя искусственного интеллекта, программирования и роботов.</p><p>В ведении Вездусущего Кода находятся все ИИ, компьютеры, машины, программы и роботы, но Триединый наиболее известен открытием (и, возможно, созданием) Потока и последующим распространением технологии звёздных путешествий через него среди всех цивилизаций галактики.</p><p>В итоге толпы механиков и техномантов стремятся стать священниками Триединого и постигнуть технологические секреты церкви. Их беззаветное служение богу машин заключается и в поддержании его храмов, и в регулярных космических экспедициях для установки или ремонта потоковых маяков, благодаря которым и возможны дешёвые межзвёздные перелёты.</p><hr>',12)])])}const f=e(_,[["render",o]]);export{m as __pageData,f as default};
