import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"УРГАТОА — ВЛЕДНАЯ ПРИНЦЕССА","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Ургатоа.md","filePath":"content/Миры игры/Вера и религия/Ургатоа.md"}'),o={name:"content/Миры игры/Вера и религия/Ургатоа.md"};function _(s,t,p,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="ургатоа-—-вледная-принцесса" tabindex="-1">УРГАТОА — ВЛЕДНАЯ ПРИНЦЕССА <a class="header-anchor" href="#ургатоа-—-вледная-принцесса" aria-label="Permalink to “УРГАТОА — ВЛЕДНАЯ ПРИНЦЕССА”">​</a></h1><p><strong>Нейтральная злая богиня болезней, нежити и ненасытности</strong></p><ul><li><strong>Центр поклонения:</strong> Зокс</li><li><strong>Символ:</strong> муха с черепом на спине или бабочка мёртвая голова</li></ul><p>Ургатоа когда-то была смертной и обладала такой неумной жаждой жизни, что после смерти восстала против суда Фаразмы, вырвалась из бесконечной очереди душ и вернулась из Великого Запределья, став первой нежитью во Вселенной. Само существование Ургатоа является искажением естественного порядка вещей. Говорят, что её первые следы на почве Материального плана вызывали чуму и заражение и что первые неживые тени и духи были рождены её дыханием.</p><p>Ургатоа — совершенно аморальна, жаждущая наслаждений богиня, чьей заботой является только удовлетворение собственных желаний, невзирая на губительные для других последствия. По мнению Бледной принцессы, ушлое существование мёртвой души бессмысленно и банально по сравнению с насыщенным чувственным мироощущением смертных и нежити, поэтому её последователи ненасытно предаются разврату и обжорству в попытке пережить как можно больше чувств и эмоций. Ургатоа учит нарушать все табу и переходить все границы — ничего запрещённого не существует. Ей претят самоограничение и выдержка, если только это не часть какой-нибудь всепоглощающей игры, и в её понимании необходимость нежити — то, к чему надо стремиться.</p><p>Церковь Ургатоа привлекает страстных и порочных существ, считающих Вселенную всего лишь местом для своих игр. Приходят туда те, кто поклоняется смерти и болезням. Большую часть её паствы составляют ненасытные обзоры, зловещие некроманты, нежить и те, кто желает ей стать. Они жаждут чувственного опыта без ограничений и последствий и непрестанно гонятся за наслаждением. Хотя церковь Ургатоа в основном интересуется нежитью, некоторые её секты возводят ненасытность в культ, предаваясь разнузданному оргиям и декадентским пирам. Пороки тех, кто поклоняется Ургатоа, становятся всё более странными и разварёнными по мере того, как они преуспевают в служении ей. В большинстве цивилизованных миров веры в Бледную принцессу сторонятся и избегают, если не запрещают её напрямую, но нежить на Зоксе в открытую поклоняется ей, а во многих городах есть посвящённые ей тайные клубы, где отдыхает искушённая элита. Храмы Ургатоа представляют собой залы для пиров, часто прилегающие к кладбищу или склепу, населённому верной нежитью.</p><p>Священниками церкви Ургатоа обычно становятся мистики или посланники, хотя кто угодно может соблазниться её обещаниями. Интересно отметить, что её верующие разделились на два лагеря на почве споров о технологиях, продлевающих жизнь: в то время как одни яростно настаивают на виртуальном бессмертии посредством оцифровки сознания и других подобных способов, традиционалисты беспокоятся, что отступление от магических методов создания нежити может постепенно ослабить влияние богини. Другие же считают это непринципиальным и полагают, что рано или поздно Фаразма и её слуги прикроют подобные лазейки.</p>',7)])])}const f=e(o,[["render",_]]);export{m as __pageData,f as default};
