import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"ФАРАЗМА — ВЛАДЫЧИЦА МОГИЛ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Фаразма.md","filePath":"content/Миры игры/Вера и религия/Фаразма.md"}'),o={name:"content/Миры игры/Вера и религия/Фаразма.md"};function p(_,t,s,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="фаразма-—-владычица-могил" tabindex="-1">ФАРАЗМА — ВЛАДЫЧИЦА МОГИЛ <a class="header-anchor" href="#фаразма-—-владычица-могил" aria-label="Permalink to “ФАРАЗМА — ВЛАДЫЧИЦА МОГИЛ”">​</a></h1><p><strong>Нейтральная богиня предсказаний, рождения, смерти и судьбы</strong></p><ul><li><strong>Центры поклонения:</strong> станция «Абессалом», Апоста, Зокс</li><li><strong>Символ:</strong> кометоподобная спираль энергии</li></ul><p>Будучи богиней рождения, смерти и судьбы, Фаразма ожидает каждого смертного в конце его земного пути. Её царство, Костяной Двор, находится на вершине невероятно высокого шпиля над городом-планом под названием Стержень и является конечным пунктом Реки Душ, метафизического потока, несущего души умерших к её царству для суда и последующего направления на один из Внешних планов.</p><p>Верующие утверждают, что Фаразма находится рядом с каждым живым существом с момента рождения до самой смерти. Она видит все возможные варианты развития каждого события и его отдалённые последствия.</p><p>Когда существо неминуемо умирает и предстаёт перед её судом, она и армия её психопомпов решают, вознаградить или наказать душу, согласно тому, что она заслужила в течение жизни. Никто ещё не ушёл от её суда, и даже боги после гибели предстают перед Владычицей могил.</p><p>Фаразма пристально следит за жизнью и смертью, выбирая загробный мир в соответствии с заслугами смертных, но при этом беспристрастна к грешникам и праведникам. Несмотря на статус богини смерти, она не противится магическому исцелению или даже воскрешению, так как видит судьбу и знает разницу между истинной смертью и временным состоянием небытия.</p><p>Тем не менее она полагает нежить осквернением памяти плоти и искажением пути души к суду, поэтому даёт указания последователям уничтожать эту скверну повсеместно. Те, кто создаёт подобных чудовищ, заслуживают такой же участи.</p><p>Смертные существа всех видов чтят Фаразму в известной степени, хотя многие знают её под другими именами или поклоняются ей иначе, чем принято в мирах Соглашения. Самыми пылкими её почитателями являются предсказатели, будущие родители, похоронные дел мастера и истребители нежити. Многие, кто принадлежит к её церкви, также следуют заветам беспристрастности и не вмешиваются в политику и другие мирские заботы.</p><p>Большинство священнослужителей Фаразмы в мирах Соглашения — мистики, целители, повитухи и прорицатели, хотя она пользуется популярностью среди некоторых воинов. Все священники Фаразмы как минимум хорошо разбираются в похоронных ритуалах и обычаях своей культуры, а многие из них стремятся изучить как можно больше ритуалов, чтобы помочь максимальному числу нуждающихся.</p><p>Хотя святилище Владычицы могил служит даже самый простой пост, храмы Фаразмы в мирах Соглашения напоминают о прошлых временах, если позволяют обстоятельства, строятся в виде величественных готических соборов, обычно по соседству с крематорием, кладбищем или другим местом упокоения.</p><hr>',12)])])}const f=e(o,[["render",p]]);export{m as __pageData,f as default};
