import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"ХАЙЛАКС — ВЕЧНАЯ КОРОЛЕВА","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Хайлакс.md","filePath":"content/Миры игры/Вера и религия/Хайлакс.md"}'),o={name:"content/Миры игры/Вера и религия/Хайлакс.md"};function p(_,t,s,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="хаилакс-—-вечная-королева" tabindex="-1">ХАЙЛАКС — ВЕЧНАЯ КОРОЛЕВА <a class="header-anchor" href="#хаилакс-—-вечная-королева" aria-label="Permalink to “ХАЙЛАКС — ВЕЧНАЯ КОРОЛЕВА”">​</a></h1><p><strong>Принципиальная добрая богиня дипломатии, дружбы, мира и первого контакта</strong></p><ul><li><strong>Центры поклонения:</strong> станция «Авессалом», Верчес, Кастровель, Нчак (спутник Лиавары)</li><li><strong>Символ:</strong> голова ширрена, увенчанная короной из звёзд</li></ul><p>До того, как Рой стал единым улем, помышляющем об опустошении и разрушении, каким его знают теперь, он поклонялся богине Хайлакс. Ширрены верят, что она была первой из их вида — смертной королевы, возвышившейся до божества, дабы наблюдать за своим потомством вечно. Но когда Рой отказался от индивидуальности и предпочёл коллективное сознание, он отрёкся от Хайлакс. Она отнеслась с уважением к решению Роя и решила не вмешиваться в его дела, обратив взор на другие разумные насекомоподобные виды в галактике, однако богиня всегда надеялась, что её дети вернутся к ней.</p><p>В конце концов терпение Вечной Королевы было вознаграждено: когда ширрены отделились от Роя и вновь обрели самостоятельность, они вспомнили о древней покровительнице.</p><p>Хайлакс знает, что мир лучше войны и что дружба даёт большие, чем вражда. Она учит своих последователей быть дипломатичными в общении, выступать за товарищество и гармонию и всегда искать союзников, а не стремиться к завоеваниям. Однако Хайлакс осознаёт, что дипломатия не всегда приносит успех и что даже мирным созданиям приходится защищать невинных. Иногда заключить мир не удастся — важна сама попытка, какой бы сложной она ни казалась.</p><p>Разумные членистоногие с Нчака, луны Лиавары, поклонялись Хайлакс задолго до Разрыва, полагая своих лидеров смертным воплощениям Вечной королевы и выстраивая цивилизацию согласно её учению. С прибытием ширренов её культ намного шире распространился по мирам Соглашения.</p><p>Ширрены составляют большинство её последователей, наряду с формианами, хаанами и другими насекомоподобными существами, однако дипломаты, посланники, посредники, переговорщики и миротворцы всех рас также считают её покровительницей.</p><p>Священнослужители Хайлакс часто находятся на кораблях, совершающих межзвёздные исследовательские полёты, где отвечают за установление первого контакта с новыми инопланетными видами.</p><p>Храмы Хайлакс, похожие на ульи, расположены везде, где сосуществуют расы родом из разных миров, их двери всегда открыты для желающих погрузиться в атмосферу дружелюбия и гармонии.</p><p>Хотя Хайлакс — божество созданий, совместно живущих в ульях или муравейниках, ей, как ни парадоксально, нравится самостоятельность, и она поощряет верующих искать силу в объединении, но осознавать и принимать собственную ценност. Различия в мировоззрении и традициях между её последователями вполне естественны, и, пусть Вечная Королева Нчака воспринимается общественными членистоногими как Верховный понтифик, ширрены стремятся к более личным отношениям с божеством.</p><hr>',12)])])}const f=e(o,[["render",p]]);export{m as __pageData,f as default};
