import{_ as e,c as a,o as n,ah as r}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"ЯРАИСА — ГОСПОЖА МУДРОСТИ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Вера и религия/Яраиса.md","filePath":"content/Миры игры/Вера и религия/Яраиса.md"}'),o={name:"content/Миры игры/Вера и религия/Яраиса.md"};function _(s,t,p,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[r('<h1 id="яраиса-—-госпожа-мудрости" tabindex="-1">ЯРАИСА — ГОСПОЖА МУДРОСТИ <a class="header-anchor" href="#яраиса-—-госпожа-мудрости" aria-label="Permalink to “ЯРАИСА — ГОСПОЖА МУДРОСТИ”">​</a></h1><p><strong>Нейтральная добрая богиня знаний, интеллектуального совершенствования, науки и ученичества</strong></p><ul><li><strong>Центры поклонения:</strong> Абалон, станция «Авессалом», Бреседа, Кастровель</li><li><strong>Символ:</strong> атом с ядром в виде мозга</li></ul><p>Яраиса учит, что основой разума является бесконечный поиск знаний и смысла — стремление к развитию общества через образование, обучение и эксперименты. Яраиса — богиня этой борьбы, вечного стремления к развитию разума и достижения интеллектуального и духовного совершенства. Лашунты приписывают многие свои ранние научные открытия Яраисе, утверждая, что когда-то она была учёным и изучила всё, что было под силу пстичь смертному. Доведя до совершенства свой разум, она обрела божественность, стремясь познать всю мудрость и знания Великого Запределья.</p><p>Последователи Яраисы стремятся подражать богине в надежде когда-нибудь превзойти пределы смертных тел. Способ совершенствования может быть любым и остаётся личным выбором каждого, будь то образование, физические тренировки, научная работа или медитация. Даже простое чтение (неважно, электронных или печатных книг либо даже путём прямой проекции в глаза) может служить актом поклонения Яраисе, ведь каждая крупица полученных знаний или понимания сути вещей приближает последователя к богине. В своём учении она соединяет такие разные понятия, как наука и духовное совершенствование, говоря, что чем больше ты узнаёшь о законах Вселенной, тем больше ты познаешь самого себя.</p><p>Большинство последователей и священников Яраисы составляют лашунты, но вера в неё популярна во всех мирах Соглашения, особенно среди ширренов. Преподаватели, механики, экстрасенсы, учёные и научные работники, а также техноманты почитают Госпожу мудрости, исследуя границы как познаний смертных, так и собственных умов. Храмы Яраисы напоминают древние библиотеки, полные книг (даже если книги на самом деле лишь голографические декорации), и в них всегда есть маленькие тихие комнатки, где прихожане могут спокойно читать или медитировать. Во многих университетах, особенно расположенных на Кастровеле или спонсируемых лашунтами, есть часовни Яраисы.</p><p>Одной из причудливых особенностей почитателей Яраисы является их открытое отрицание веры — или, по крайней мере, некоего общепринятого значения этого термина. Яраиса учит, что лучшим способом понимания Вселенной служит наука и знания, полученные в результате проведения контролируемых, воспроизводимых и верифицируемых экспериментов. Некоторые боги сулят знания и власть в качестве награды за служение, но Яраиса требует от последователей упорно добывать знания самостоятельно. Это отнюдь не значит, что она отказывается даровать им магическую силу — напротив, именно так она обычно и поступает, но использует для этого последовательные и предсказуемые способы, обучая им, как науке, и гораздо охотнее подталкивает к открытиям, нежели даёт прямой ответ. Для принадлежащих к её церкви вера — это не слепое доверие, а плодотворные и скептические отношения между уважаемым учителем и учениками.</p>',7)])])}const f=e(o,[["render",_]]);export{m as __pageData,f as default};
