import{_ as n,c as r,o,ah as e}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"ДЭГОКС 4","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/За пределами Миров Соглашения/Дэгокс 4.md","filePath":"content/Миры игры/За пределами Миров Соглашения/Дэгокс 4.md"}'),s={name:"content/Миры игры/За пределами Миров Соглашения/Дэгокс 4.md"};function a(i,t,l,_,p,c){return o(),r("div",null,[...t[0]||(t[0]=[e('<h1 id="дэгокс-4" tabindex="-1">ДЭГОКС 4 <a class="header-anchor" href="#дэгокс-4" aria-label="Permalink to “ДЭГОКС 4”">​</a></h1><p><strong>ПЛАНЕТА-ТЮРЬМА</strong></p><ul><li><strong>Диаметр:</strong> ×2/3</li><li><strong>Масса:</strong> ×4/9</li><li><strong>Гравитация:</strong> ×1</li><li><strong>Местоположение:</strong> ближний космос</li><li><strong>Атмосфера:</strong> обычная</li><li><strong>День:</strong> 20 часов</li><li><strong>Год:</strong> 2 года</li></ul><p>Дэгокс 4 не зависит ни от одного из правительств или объединений и служит планетой-тюрьмой для сотен тысяч преступников со всех концов галактики. Бесконечно тянущиеся здания с камерами разного размера и удобства в зависимости от преступлений заключённых занимают один из двух континентов планеты, в центре которого находится комплекс строгого режима, где содержатся наиболее опасные преступники. Второй же материк утопает в пышных лесах и диких прериях, где заключённые низкой степени опасности могут перемещаться абсолютно свободно. В центре этого прекрасного континента рядом с широкой рекой расположена Гора. Это внушительное здание, занимающее сотни акров, — административный центр планеты.</p><p>Реальная власть на планете сосредоточена в руках корпорации «Дэгокс», которой управляет загадочная раса гуманоидов, отказавшаяся раскрыть местоположение своего родного мира. Лидеры корпорации находятся на орбите планеты, на корабле «Звездочёт». Тюрьма принимает заключённых из всех признаваемых руководством корпорации государств — разумеется, не бесплатно. Поговаривают, что на самом деле корпорация — это и есть раса инопланетян во всей её полноте, и что эта планета — их родной мир, но эта информация остаётся неподтверждённой. Заключённых на Дэгокс 4 удерживают не только камеры и отсутствие современного транспорта. Невидимый пузырь окружает всю планету подобно односторонней мембране. Единственный надёжный способ покинуть планету находится глубоко в недрах Горы, где техномагические врата открывают путь в один из десятка связанных миров. Персонал «Дэгокса», контролирующий Гору, утверждает, что ещё ни один заключённый не сбежал оттуда, однако ходят легенды, что некто по прозвищу Королёк недавно смог это сделать. Планета в основном экономически самостоятельна, а корпорация «Дэгокс» получает большую часть дохода в виде платы за размещение заключённых. Хотя кое-какую прибыль обеспечивают и сами заключённые, которые предлагают надзирателям обработанные полезные ископаемые (а также, по слухам, услуги профессиональных убийц и солдат-смертников) в обмен на информацию и предметы с воли.</p><p>К счастью для охранников Дэгокса, в атмосфере планеты высока концентрация спор (возможно, распылённых самой корпорацией), которые оказывают успокаивающий эффект на существ с других планет, делая их послушными и сговорчивыми, поэтому в докладах корпорации масштабные стычки и восстания заключённых практически не упоминаются.</p><hr>',7)])])}const m=n(s,[["render",a]]);export{d as __pageData,m as default};
