import{_ as n,c as r,o,ah as e}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"ДЭЙМАЛКО","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/За пределами Миров Соглашения/Дэймалко.md","filePath":"content/Миры игры/За пределами Миров Соглашения/Дэймалко.md"}'),s={name:"content/Миры игры/За пределами Миров Соглашения/Дэймалко.md"};function a(i,t,l,_,p,c){return o(),r("div",null,[...t[0]||(t[0]=[e('<h1 id="дэималко" tabindex="-1">ДЭЙМАЛКО <a class="header-anchor" href="#дэималко" aria-label="Permalink to “ДЭЙМАЛКО”">​</a></h1><p><strong>РАЗВАЛИНЫ И БУЙНЫЕ КОЛОССЫ</strong></p><ul><li><strong>Диаметр:</strong> ×1/4</li><li><strong>Масса:</strong> ×1/16</li><li><strong>Гравитация:</strong> ×1</li><li><strong>Местоположение:</strong> ближний космос</li><li><strong>Атмосфера:</strong> обычная</li><li><strong>День:</strong> 48 часов</li><li><strong>Год:</strong> 3 года</li></ul><p>Чудовища кишмя кишат в этом засушливом мире скалистых гор и обрывистых впадин, скитаясь по развалинам двух некогда великих цивилизаций, а немногочисленные потомки этих сообществ ведут борьбу за выживание под землёй.</p><p>Давным-давно на Дэймалко процветала цивилизация, а рассказы об огромных городах были просто легендами. В те дни обычная жизнь вращалась вокруг конфликта между телепатической Конфедерацией Волкарии и Священным королевством Икарт, заявлявшем о божественном благословении, полученном от светозарной владычицы Деллоны, Воительницы. Однажды около 200 лет назад вселенский катаклизм, бушевавший месяц, положил конец этой войне.</p><p>Серокожие дэймалканцы теперь называют это событие Пробуждением. Из-за вибрации от землетрясений океаны планеты испарились, а земля захлебнулась. Не всё хуже то, что катаклизм пробудил чудовищ из древних преданий. Их называли колоссами, кайдзо и другими именами, а выглядели они как громадные твари, непохожие ни на каких других существ, с головами из языков, зубов и других устрашающих частей. Эти создания немедленно принялись разорять воющие государства.</p><p>Большинство жителей планеты погибло либо во время Пробуждения, либо сразу после него благодаря колоссам. Оставшиеся в живых сбежали в подземные пещеры, которые нарекли Убежищем. И хотя стычки иногда возникали, национальность отошла на второй план, уступив место борьбе за выживание. В конце концов подземные поселения беженцев разрослись и появились лидеры — Опекуны. Заявив о способности направлять бесчеловечных колоссов при помощи загадочных магических шаров, Опекуны в течение последних 50 лет помогли создать небольшие колонии на поверхности. Планета до сих пор крайне опасна. Очаи сопротивления икартанцев и волкарианцев угрожают стабильности Убежища. Более того, обрывы сигналов, полученные кораблями на орбите, указывают на то, что в разрушенных крупных городах живут потомки враждовавших наций, которые почему-то не заслужили внимания обитателей планеты. Учитывая недавние нападения гигантов на колонии, а также сообщения от подземных поселенцев об организованных атаках бескожих, безглазых созданий, некоторые из дэймалканцев активно протестуют против любого контакта с мирами Соглашения, полагая, что прибытие иноземцев спровоцирует второе Пробуждение.</p>',7)])])}const m=n(s,[["render",a]]);export{d as __pageData,m as default};
