import{_ as n,c as r,o,ah as e}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"ПРЕЛЮРИЯ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/За пределами Миров Соглашения/Прелюрия.md","filePath":"content/Миры игры/За пределами Миров Соглашения/Прелюрия.md"}'),s={name:"content/Миры игры/За пределами Миров Соглашения/Прелюрия.md"};function a(i,t,l,_,c,g){return o(),r("div",null,[...t[0]||(t[0]=[e('<h1 id="прелюрия" tabindex="-1">ПРЕЛЮРИЯ <a class="header-anchor" href="#прелюрия" aria-label="Permalink to “ПРЕЛЮРИЯ”">​</a></h1><p><strong>ОРБИТАЛЬНЫЕ КОЛЬЦА БЕСЧИСЛЕННЫХ МИРОВ</strong></p><ul><li><strong>Диаметр:</strong> ×100</li><li><strong>Масса:</strong> ×100</li><li><strong>Гравитация:</strong> варьируется</li><li><strong>Местоположение:</strong> ближний космос</li><li><strong>Атмосфера:</strong> обычная</li><li><strong>День:</strong> 12 часов</li><li><strong>Год:</strong> 25 лет</li></ul><p>Прелюрия — массивный зелёный газовый гигант, населённый только равнодушными газовыми жижами, которые абсолютно невосприимчивы к кислотным газам планеты, поскольку сами из них состоят. Недостаток цивилизации в толще планеты вполне компенсируется многочисленными поселениями на её широких орбитальных кольцах. Они состоят из миллионов ледяных и минеральных тел всевозможных размеров, на которых расположены бесчисленные схроны и базы контрабандистов, учебные лагеря наёмников, а также места уединения состоятельных, влиятельных или нелюдимых существ со всей галактики. Здесь нет централизованного правительства, поэтому политическая жизнь постоянно находится в движении, представляя собой калейдоскоп из альянсов, противостояний и вооружённой нейтралитета.</p><p>Самые большие поселения на кольцах Прелюрии находятся на 23 телах, достаточно крупных, чтобы иметь естественную гравитацию, различную интенсивность и собственную атмосферу. Именно там расположены сильнейшие фракции, враждующие между собой. Наиболее влиятельные из них — Ворлаты, наёмники, работающие на того, кто больше платит, но при этом не забывающие и о собственных независимых целях; на первый взгляд мирное Ксистрианское братство, чьи члены притворяются пацифистами, но устраивают диверсии против врагов; Патруль Прелюрии, пытающийся с переменным успехом избавить систему от преступников и сохранить порядок; Поисковая компания Фройгана, заявляющая права на любые ценности, найденные на кольцах; и Институт Рейволана, который проводит сомнительные эксперименты и исследования, приглашая учёных, которым запретили вести деятельность на других планетах. На более мелких телах находятся разнообразные надземные и подземные структуры, являющиеся либо частью конгломератов, либо принадлежащие частным лицам, не поддерживающим никаких связей с соседями. Некоторые из них обладают искусственной атмосферой, хотя на большинстве нет ничего, кроме рукотворных построек.</p>',5)])])}const m=n(s,[["render",a]]);export{d as __pageData,m as default};
