import{_ as n,c as r,o,ah as e}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"ШАДАРИ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/За пределами Миров Соглашения/Шадари.md","filePath":"content/Миры игры/За пределами Миров Соглашения/Шадари.md"}'),s={name:"content/Миры игры/За пределами Миров Соглашения/Шадари.md"};function a(i,t,l,_,p,c){return o(),r("div",null,[...t[0]||(t[0]=[e('<h1 id="шадари" tabindex="-1">ШАДАРИ <a class="header-anchor" href="#шадари" aria-label="Permalink to “ШАДАРИ”">​</a></h1><p><strong>СОКРЫТАЯ КОНФЕДЕРАЦИЯ</strong></p><ul><li><strong>Диаметр:</strong> ×1</li><li><strong>Масса:</strong> ×1</li><li><strong>Гравитация:</strong> ×1</li><li><strong>Местоположение:</strong> Простор</li><li><strong>Атмосфера:</strong> обычная</li><li><strong>День:</strong> 40 дней</li><li><strong>Год:</strong> 2 года</li></ul><p>Великий Шадар — самый большой из семи связанных миров, вращающихся вокруг тусклой звезды в туманности Кёрзак на краю владений Звёздной империи Ацланти. Плотные клубы пыли и ионизированного газа окутывают миры Конфедерации Шадари тускло-зелёной, с примесью серого и коричневого цветов, пеленой, скрывающей их от сканеров и патрулей империи. Такая защита делает их надёжным убежищем для преступников, проходящих и всех тех, кто скрывается от длинных рук имперских властей.</p><p>Семь планет Шадари и их луны населены существами самых разнообразных рас и культур, уникальных по форме, внешнему виду и эмоциональной структуре, но единых в своей приверженности шадарским идеалам отсутствия ограничений и законов. Дрэлики, коренные жители Великого Шадара (второй по счёту планеты от тусклого солнца этой системы), возглавляют межрасовый альянс. Дрэлики гладкокожи и изящны, а их удлинённые хвосты и перепонки на руках и ногах выдают океаническое происхождение. Однако на Великом Шадаре нет подходящих водоёмов, где они могли бы обитать, не говоря уже о том, чтобы даже предположить, что они зародились именно там. Как и все расы Конфедерации, дрэлики носят знаки Третьего Глаза Прозрения — символ религии, в которой основное внимание уделено странным межпространственным существам — скедьюнарам. Среди других рас Шадари, наделённых Третьим Глазом, выделяются скреллы — выпуклые создания с длинными шупальцами, тянущимися позади. Эти обладающие телепатическими способностями существа парят среди клубящихся облаков Пятого Шадара, мрачного газового гиганта, напоминающего срез подгнившего фрукта.</p><p>Даже искусственные балродды (широкогрудые автоматоны, сбежавшие из рабства империи Ацланти тысячелетия назад) отмечены Глазом Прозрения, как преданные члены Конфедерации. В Звёздной империи Ацланти последнее время ходят слухи, что скедьюнары даровали Государю дрэликов и его Нижнему совету оружие неслыханной силы — Отметель. Как говорят, это тяжелораненая сущность, называемая вечным, которая сохранилась ещё со времён возникновения Вселенной. Шадари трудились десятилетиями, чтобы обратить силы вечного на «отмену» всего мироздания — ужасающая перспектива, если помнить, что вечный находится в распоряжении сборища космических пиратов и преступников.</p><hr>',7)])])}const m=n(s,[["render",a]]);export{d as __pageData,m as default};
