import{_ as n,c as r,o,ah as e}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"ЭМБРОЙ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/За пределами Миров Соглашения/Эмброй.md","filePath":"content/Миры игры/За пределами Миров Соглашения/Эмброй.md"}'),s={name:"content/Миры игры/За пределами Миров Соглашения/Эмброй.md"};function a(i,t,l,_,c,g){return o(),r("div",null,[...t[0]||(t[0]=[e('<h1 id="эмброи" tabindex="-1">ЭМБРОЙ <a class="header-anchor" href="#эмброи" aria-label="Permalink to “ЭМБРОЙ”">​</a></h1><p><strong>ЖЕЛЕЗНЫЙ РАВ ПРЕИСПОДНЕЙ</strong></p><ul><li><strong>Диаметр:</strong> ×3/4</li><li><strong>Масса:</strong> ×9/16</li><li><strong>Гравитация:</strong> ×1</li><li><strong>Местоположение:</strong> ближний космос</li><li><strong>Атмосфера:</strong> обычная</li><li><strong>День:</strong> 18 часов</li><li><strong>Год:</strong> 1 год</li></ul><p>Планета Эмброй мерцает фиолетовым сиянием во тьме космоса и представляет собой царство воплощённой сложности и эффективности. Шесть континентов разделены аметистовые моря планеты, которые служат домом для бесчисленного множества видов больших величественных моллюсков. Континенты опоясаны архипелагами и носят названия Антикавара, Демагалло, Иррало Келад, Канфрей, Просторы Баскара и Чапру Вон. Просторы Баскара — самый большой, но наименее населённый континент. Это земля развалин, пепельных штормов и чумы — всё это останки почти забытых, но ненавидимых государств Баскара. Большая часть населения проживает либо на Демагалло, основном континенте, либо на Иррало Келаде, территории железных гор и промышленных шахт.</p><p>Масколицые эмбри составляют большую часть населения планеты. Они крепко держатся за место в обществе и соблюдают ежедневный режим с почти религиозным рвением. Личные желания здесь признаны аномалией и после веков культурных репрессий и общественного давления задушены на корню. На планете мало свободомыслящих эмбри, и нет ни восстаний, ни даже тихого сопротивления. У каждого эмбри есть роль, и либо он исполняет её хорошо, либо его заменяют другим. Они чётко следуют инструкциям и вознаграждаются за это мелкими поощрениями и пустыми обещаниями. Благодаря этому Эмброй — особенно ценный мир рабов в империи Преисподней на Материальном плане. Немногие жители планеты и её гости из миров Соглашения осознают, что Эмброй пребывает под властью Преисподней. Её представители предпочитают держаться в тени, а дьявольское влияние почти незаметно. Информация о широкой сети подземных туннелей была стёрта из истории Эмброя. Это нижнее царство является сейчас единичным армией бесшумных левалоков, местом размещения пылающих порталов на Малебольг, шестой круг Преисподней, и покрытых ржавчиной машин для жертвоприношений. За всем этим приглядывает мальбранш Окирасой, покорившая Эмброй до наступления Разрыва. Она веками ждёт, всегда готовая к тому, что её мир внесёт свою лепту в таинственные планы Асмодея.</p>',5)])])}const m=n(s,[["render",a]]);export{d as __pageData,m as default};
