import{_ as n,c as r,o,ah as e}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"АПОСТЭ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Миры Соглашения/Апостэ.md","filePath":"content/Миры игры/Миры Соглашения/Апостэ.md"}'),s={name:"content/Миры игры/Миры Соглашения/Апостэ.md"};function a(p,t,i,l,_,c){return o(),r("div",null,[...t[0]||(t[0]=[e('<h1 id="апостэ" tabindex="-1">АПОСТЭ <a class="header-anchor" href="#апостэ" aria-label="Permalink to “АПОСТЭ”">​</a></h1><p><strong>ПОСЛАННИК</strong></p><ul><li><strong>Диаметр:</strong> ×1/5</li><li><strong>Масса:</strong> ×1/250</li><li><strong>Гравитация:</strong> ×1/10</li><li><strong>Атмосфера:</strong> обычная или отсутствует</li><li><strong>День:</strong> 7 дней</li><li><strong>Год:</strong> 243 года</li></ul><p>Апостэ — каменистый планетоид, который гораздо меньше большинства миров Соглашения. Его орбита сильно отклоняется от плоскости эклиптики и почти перпендикулярна орбите станции «Авессалом». Эти факты говорят в пользу распространённой теории, что планетоид появился где-то вне системы и был притянут на орбиту Солнца за тысячи лет до Разрыва. Остаётся лишь гадать, был ли он направлен сюда намеренно или прибыл в результате случайности.</p><p>В немногих уцелевших записях тех, кто побывал на планетоиде до Разрыва, описывается пустынная, безвоздушная поверхность и лабиринт наполненных атмосферой гротов и туннелей, пронизывающих породу планеты до самого ядра. Многие из пещер были пусты, но в других были обнаружены неразрушимые двери, всё ещё гудевшие древние машины, находящиеся в спячке создания разных инопланетных видов и хранилища с передовыми технологиями и оружием. Несколько команд исследователей изучали Апостэ ещё до Разрыва и вступили в контакт с сыне вымершей расой айли, жившей в туннелях, но ни одна группа из других миров не основала постоянного поселения на планетоиде, который большинство считали неким древним кораблём поколений или иной автономной средой обитания. Странно, что ничто в архивах не указывает на то, что айли знали хоть что-нибудь о своём происхождении, поэтому согласно одной из теорий они либо вырождались среди звёзд на протяжении тысячелетий, либо были рабами или расой слуг, которые пережили своих хозяев. Никто не знает наверняка, что привело к полному вымиранию айли, но с окончания Разрыва Апостэ преимущественно пребывает под контролем нескольких могущественных домов дроу — эльфов с фиолетовой кожей, которые отреклись от собратьев с Кастровеля и поклоняются могущественным демоническим покровителям в искажённой пародии на древний исход эльфов.</p><p>Сегодня, несмотря на многовековые попытки расширить владения и увеличить численность населения, дроу всё ещё удерживают лишь частичный контроль над внутренней частью плането-корабля, а их влияние на пустынной поверхности ограничено единственным большим городом рядом с одной из огромных дверей, ведущих внутрь скалы. Однако даже этого, вкупе с сильной экономикой, оказалось достаточно для провозглашения Апостэ их родным миром и для подписания Соглашения, несмотря на громкие протесты эльфов Кастровеля.</p><p>Дома дроу, подобно костяным старейшинам Эокса, не образуют единое правительство, а собираются лишь для того, чтобы избрать послов, как того требует Соглашение.</p><p>Каждый дом дроу действует независимо, обычно управляя семейной корпорацией, чаще всего занимающейся различного рода вооружением. Особое внимание уделяется изобретению новых технологий или их реконструкции из инопланетных объектов, обнаруженных археологическими экспедициями внутри Апостэ.</p><p>Орки, полуорки и другие расы, живущие в поселениях дроу, являются в лучшем случае гражданами второго сорта без права голоса в вопросах политики. Их используют в качестве рабочих или как пушечное мясо в ходе подземных операций, когда нужно вломиться в запечатанные пещеры в поисках легендарного Арсенала или Обители Жизни.</p><p>Наиболее влиятельный дом Апостэ — дом Цейцерер, создавший самую широкую и успешную сеть по торговле оружием. Дом Цейцерер контролирует Врата Ночи — крупнейший город на поверхности планетоида и единственную известную точку входа внутрь Апостэ. Все другие огромные двери, встроенные в поверхность планеты, заперты и неразрушимы. Как и в любом крупном космопорте, здесь легко можно купить любое оружие — от винтовок до корабельного орудия, нанять военный отряд орков или полуорков или вживить в тело искажающие плоть аугментации.</p><p>Помимо поиска инопланетных технологий в подземных планетах (по официальному разрешению дроу или проскользнув мимо охраны), гости планеты часто посещают Врата Ночи, чтобы изучить одноимённую арку в центре города. Эти врата очень похожи на те, что соединяют многие миры системы (функционирующее кольцо подобных порталов в городе позволяет переместиться во многие другие миры Соглашения). Они покрыты символами, которые до сих пор никто не смог расшифровать. Врата активируются спонтанно, редко и на разные по продолжительности интервалы времени, открывая портал в неизвестные места. Пока что ни одна из групп, ушедших сквозь эти недолговечные порталы, не вернулась назад.</p>',11)])])}const m=n(s,[["render",a]]);export{d as __pageData,m as default};
