import{_ as n,c as r,o,ah as e}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"БРЕСЕДА","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Миры Соглашения/Бреседа.md","filePath":"content/Миры игры/Миры Соглашения/Бреседа.md"}'),s={name:"content/Миры игры/Миры Соглашения/Бреседа.md"};function a(p,t,_,i,l,c){return o(),r("div",null,[...t[0]||(t[0]=[e('<h1 id="бреседа" tabindex="-1">БРЕСЕДА <a class="header-anchor" href="#бреседа" aria-label="Permalink to “БРЕСЕДА”">​</a></h1><p><strong>КОЛЫБЕЛЬ</strong></p><ul><li><strong>Диаметр:</strong> ×11</li><li><strong>Масса:</strong> ×320</li><li><strong>Гравитация:</strong> ×2,5 (на «поверхности»)</li><li><strong>Атмосфера:</strong> особая</li><li><strong>День:</strong> 6 дней</li><li><strong>Год:</strong> 30 лет</li></ul><p>Бреседа, самый большой газовый гигант в системе — постоянно клокочущая масса голубых и фиолетовых грозовых облаков. Сквозь эти бури и стаи малых форм жизни пролетают объёмные барату, представители самой развитой местной расы, которых иногда называют бреседианами или, оскорбительно, «летунами». Из-за прозрачных тел они походят на длинных медуз или дирижабли, а их повороты и нырки создают впечатление, что они — странные птицы или дельфины. На самом деле барату — высокоразумные любопытные создания с необычным коллективным подходом к выживанию. Когда барату сталкивается с задачей, которую не может решить самостоятельно, он временно сливается с другими барату, образуя уникальное коллективное существо, гораздо более сильное и умное, чем любая из его составляющих. Как только необходимость отпадает, барату снова разъединяются.</p><p>Из-за малого количества металла на планете барату обладают исключительно биологическими технологиями. Они легко могут изменять форму и выращивать новые приспособления из считанных секунд — например, защитить свои тела твёрдой оболочкой или создать дополнительные конечности. Кроме того, способности барату к изменению генов позволяют им создавать и порождать живые инструменты, на основе только собственных тел, будь то специально созданные вирусы или полуразумные расы слуг. Некоторые из лучших биотехнологий во всей системе были разработаны на Бреседе, в парящих городах-аркологиях, где трудятся выходцы из других миров, либо на местных предприятиях, например в корпорации «Сопет», которая фактически представляет собой разумное существо, состоящее из тысяч барату.</p><p>Мирное и свободолюбивое общество барату не нуждается в правительстве, но они усвоили, что во избежание эксцессов жителей других миров должны придерживаться определённых правил. Для этого было создано Слияние — сущность с невероятным интеллектом, созданная из барату, которые ощущают практические религиозную тягу к труду на благо общества. Слияние устанавливает правила для гостей Бреседы и следит за их соблюдением. При необходимости Слияние порождает специально созданных представителей и контролёров, оснащая их патентованными биоимплантами, позволяющими поддерживать связь с Слиянием на любом расстоянии и в реальном времени. Ради доступа к такой технологии корпорации готовы пойти даже на убийство — многие так и поступили, — но алгоритм самоуничтожения, внедрённый в так называемых Агентов Слияния, а также возможность ответных действий со стороны руководящей сущности Бреседы отпустили большинство жадных до технологий исследователей.</p><p>Бреседу иногда называют Колыбелью — своим прозвищем она обязана десяткам лун, некоторые из которых достаточно велики, чтобы являться самостоятельными планетами. Многие луны обитаемы, но изучены лишь некоторые из них, а остальные почти не исследовались.</p><p>Покрытый слоем льда водный мир Кало-Махой — самая промышленно развитая луна Бреседы, а её жители, глубоководные кало, имеют собственных представителей в Совете Соглашения. Хотя расы, дышащие воздухом, часто не хотят спускаться в глубины холодных морей, а на поверхности расположено много космических портов и контактных центров, кало изо всех сил стараются сделать мир гостеприимнее. Кало обитают в органических городах возле геотермальных источников и являются основоположниками всех новых течений в области искусства и высокой моды.</p><p>Засушливые леса Мараты, напротив, являются предметом многих антропологических споров, потому что племена семиполых маракоев совершили скачок от каменных инструментов до космоплавания всего за несколько поколений. Некоторые традиционалисты из правительства маракоев при соединении активистов из других рас утверждают, что из-за космического глобализма шерстистые гуманоиды теряют культуру и основополагающее право развиваться в собственном темпе, однако большинство маракоев возмутило возможное ограничение свободного доступа к мирам Соглашения, и они стали массово переселяться на другие планеты системы.</p><p>Покрытый кристаллами Дикон населяют кремниевые формы жизни, из которых особенно выделяются уроги, гениальные математики. Эти кристаллические существа размером с быка скользят по поверхности с помощью жгутиков и электромагнитных полей и поглощают солнечный свет сквозь панцирь. Уроги иногда работают штурманами звездолётов, техномантами или физиками.</p><p>Только самые отчаянные или осуждённые попадают на снискавшие дурную славу «смертельные луны» Бреседы, коих великое множество. На радиоактивном Тисте, к примеру, незащищённая плоть, погружённая в местную реку, облезает с костей за секунды. Несмотря на это, туда постоянно отправляются экспедиции, чтобы добывать минералы, изучать уникальную животную жизнь или искать магическую броню, которую почему-то выбрасывают местные криптоиды, поглощающие свет и редко попадающиеся на глаза. В то же время на нагретом приливными силами Варосе элементалисты усмиряют силу вулканов и добывают уникальные драгоценные камни, которые формируются только в мантии этой луны.</p>',11)])])}const m=n(s,[["render",a]]);export{d as __pageData,m as default};
