import{_ as n,c as r,o,ah as e}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"ДИАСПОРА","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Миры Соглашения/Диаспора.md","filePath":"content/Миры игры/Миры Соглашения/Диаспора.md"}'),s={name:"content/Миры игры/Миры Соглашения/Диаспора.md"};function a(_,t,i,l,p,c){return o(),r("div",null,[...t[0]||(t[0]=[e('<h1 id="диаспора" tabindex="-1">ДИАСПОРА <a class="header-anchor" href="#диаспора" aria-label="Permalink to “ДИАСПОРА”">​</a></h1><p><strong>ЗАТЕРЯННЫЕ</strong></p><ul><li><strong>Диаметр:</strong> миллионы астероидов, каждый диаметром до 600 миль</li><li><strong>Масса:</strong> общая ×2 (менее ×1/100 для каждого отдельного астероида)</li><li><strong>Гравитация:</strong> варьируется</li><li><strong>Атмосфера:</strong> особая</li><li><strong>День:</strong> варьируется</li><li><strong>Год:</strong> варьируется</li></ul><p>Огромный пояс астероидов, именуемый Диаспорой, возник, когда близнецы (двойные планеты Дамиар и Иово) были уничтожены в результате неизвестной катастрофы тысячелетия тому назад, задолго до Разрыва. Осколки этих двух миров неспешно обращаются по новым орбитам, и, хотя в Диаспоре более миллиона небесных тел диаметром больше мили, все они находятся достаточно далеко друг от друга, поэтому редко сталкиваются. Большинство планетоидов — бесформенные куски камня или льда, хотя некоторые из них сохранили следы цивилизаций Дамиара и Иово. На одних гравитация (или технологические устройства) едва-едва удерживает разрежённую атмосферу, другие же совершенно лишены пригодных для дыхания газов. В пространстве между астероидами парят пыль, камни и металлические осколки, представляющие угрозу для звездолётов, проносящихся на высоких скоростях.</p><p>Самое опасное здесь — обитатели Диаспоры. Контрабандисты и космические пираты, особенно Вольные капитаны с Расколотой Скалы, прячут на астероидах накопленные богатства. Некоторые оснащают тайники высокотехнологичной защитой, другие скрывают убежища прямо среди неприметных глыб. Многие планетоиды с залежами минералов и других ресурсов находятся в собственности добывающих компаний, из которых самыми известными являются Звёздные цитадели дварфов, рьяно бросающихся в бой, защищая свои интересы. Вдобавок андроиды, называющие себя Отшельниками, недавно начали строительство собственного мира и стягивают астероиды воедино с помощью лучей захвата и гравитационных излучателей. Будущее покажет, удастся ли им это, но, по слухам, они относятся к работе с почти религиозным рвением и остро реагируют на любые помехи.</p><p>Не стоит забывать и о коренных жителях Диаспоры — саркезианах. Эти гуманоиды, предположительно, происходят от выходцев с исчезнувших двойных планет. Они эволюционировали и приспособились к жизни в тяжёлых условиях вакуума. Саркезиане способны неограниченно долго задерживать дыхание, что позволяет им парить между одинокими астероидами на крыльях из чистой энергии, раскинутых невероятно широко, чтобы улавливать солнечный ветер. За эту особенность саркезиане иногда называют «ангелами» — и в насмешку, и с восхищением. Они владеют несколькими терраформированными мирами-колыбелями внутри пояса, где могут отдыхать и спокойно расти дети. Саркезиане обычно миролюбивы, но не могут забыть старой вражды с Эоксом, который винят в разрушении своих миров. Как и многие соседи, саркезиане отправляют делегатов в Совет Соглашения для представления своих интересов, хотя у них нет общего правительства, которое бы позволило им получить статус полноценного мира Соглашения, а не протектората — причиной этого служат их либертарианские взгляды: саркезиане довольствуются тем, что имеют, и позволяют другим жить так же.</p><p>Самое большое небесное тело Диаспоры — Нисида, диаметр которой составляет 600 миль. Этот планетоид покрыт коркой льда и состоит в основном из пресной воды, в которой полно хищников, унесших жизни бесчисленного множества потенциальных колонистов. В перевёрнутых поселениях-куполах, закреплённых на нижней поверхности льда, обитает лишь небольшое количество саркезиан. Жители этих маленьких анклавов всегда начеку и готовы в любой момент бросить дома и перебраться в укрепления на поверхности в случае нападения из глубин. Воды Нисиды являются источником Межречья, необычного водного пути, окруженного атмосферой и удерживающего цилиндрический энергетический полем. Межречье проходит через Диаспору, соединяя между собой многие астероиды и миры-колыбели. Раньше Межречье широко использовалось, позволяя быстро перемещаться по поясу астероидов, но сейчас его воды потемнели, а моряки стали рассказывать о жестоких атаках невиданных созданий. Движение по реке резко сократилось со времени появления этих «диаспорских змеев», а некоторые учёные заметили, что и изменение цвета воды, и нападения совпали ещё с одним необъяснимым событием: Нисида начала медленно увеличиваться в размерах.</p><p>Одним из самых известных мест Диаспоры является Дом Пустоты — загадочный монастырь-астероид, в котором живут монахи, облачённые в чёрные одеяния и не желающие рассказывать о своих целях. На другой стороне пояса расположен ныне пустующий полый астероид, прозванный Воющим Камнем. Давным-давно в нём были пробурены многие мили звукоизолированных тоннелей, в которых сначала организовали карантинную зону для жертв, поражённых неким ужасным безумием, а позже — исправительную колонию для преступников, но в какой-то момент во время Разрыва все обитатели бесследно пропали.</p><p>До сих пор ни одна попытка заселить астероид не увенчалась успехом — все, кто оставался там дольше чем на несколько дней, жаловались на ужасные ночные кошмары и видения искривлённых фигур в жёлтых лохмотьях.</p>',9)])])}const m=n(s,[["render",a]]);export{d as __pageData,m as default};
