import{_ as n,c as r,o as e,ah as o}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"КАСТРОВЕЛЬ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Миры Соглашения/Кастровель.md","filePath":"content/Миры игры/Миры Соглашения/Кастровель.md"}'),s={name:"content/Миры игры/Миры Соглашения/Кастровель.md"};function a(_,t,p,i,l,c){return e(),r("div",null,[...t[0]||(t[0]=[o('<h1 id="кастровель" tabindex="-1">КАСТРОВЕЛЬ <a class="header-anchor" href="#кастровель" aria-label="Permalink to “КАСТРОВЕЛЬ”">​</a></h1><p><strong>ДИКАРЬ</strong></p><ul><li><strong>Диаметр:</strong> ×1</li><li><strong>Масса:</strong> ×1</li><li><strong>Гравитация:</strong> ×1</li><li><strong>Атмосфера:</strong> обычная</li><li><strong>День:</strong> 1 день</li><li><strong>Год:</strong> ½ года</li></ul><p>Жаркие и влажные джунгли и болота Кастровеля часто бушуют бурями и приливами, а в их дебрях процветают необычайно разнообразные формы жизни — от огромных рептилоидных тварей до смертоносных плесневых бурь, способных поглотить цепь поселений.</p><p>Из всего многообразия разумных рас, живущих на этом плодородном мире, выделяются прежде всего лашунты, эльфы и формиане. Эти три космополитичные цивилизации тесно связаны благодаря сети древних магических порталов, именуемых аюдарами.</p><p>С давних пор лашунты Кастровеля жили в независимых городах-государствах с разными формами правления, среди которых наиболее распространенным был выборной матриархат. В настоящее время правительства небольших городов часто подчинены корпоративным интересам региональных или межпланетных компаний, которые подкупают чиновников или строят независимые частные поселения. Несмотря на то, что благодаря развитию экономических связей пыл лашунтского национализма несколько погас и уже почти не приводит к вооружённым столкновениям, граждане обеспокоены ростом промышленного шпионажа и вмешательства корпораций в общественную жизнь.</p><p>Среди городов-государств, всё ещё способных бороться на равных с властью корпораций, наиболее выдающимся является Куабарат, Сияющий бриллиант Западного моря — самый большой космический порт на планете. В здании Тройного дома леди Морана Кеш и её главный консорт, Грантэус, плетут тонкую паутину интриг, чтобы не допустить господства корпораций над планетой. Такие значительной властью и влиянием обладают городские университеты, хранящие архивы ещё со времён до Разрыва. Они обучают лучших в системе исследователей, учёных и астронавигаторов. В архитектурном плане город представляет собой настоящее произведение искусства: его древние стены сделаны из сияющих дроблёных раковин, гармонично сочетающихся с современными элементами. Это ощущение вневременности и связи с долгой историей планеты наполняет сердца горожан, когда они слышат звуки тренировок, доносящиеся с Военных Дворов, споры студентов, добродушно дискутирующих на ступенях университета, или рокот и шипение приземляющихся звездолётов, которые почти заменили водные суда в Корабельном Пределе. Большинство гостей планеты прибывает в порт Куабарата, который связан порталами с другими крупными поселениями лашунт, например изолированным Лаубу Меса, Коменой в Парящих скалах, Нерушимым Джабаском или Кандаресом, спавшимся бесчисленными водопадами и открытой добычей трупиума на крутых обрывах.</p><p>Насекомоподобные формиане враждовали с лашунтами на протяжении тысячелетий, но подписание Соглашения и хрупкий мир, достигнутый при посредничестве ширренов, положили конец боевым действиям всего 30 лет назад. В наше время формианские ульи, называемые Колониями, работают сообща, возделывая землю и ведя торговлю с другими расами. При этом королевы каждого поселения подчиняются Владычице — идеальному образу, созданному во время исторического события — Встречи королев.</p><p>Самые длинные ходы формианских ульев могут тянуться на многие мили, периодически расширяясь и образуя рукотворные холмы с сотами, поэтому ориентироваться в них достаточно трудно (а завоевать их, как показала история, и вовсе невозможно).</p><p>Несмотря на воцарившийся мир, большинство эльфов, населяющих Совириан, во взглядах придерживаются традиционализма и ксенофобии, и потому торгуют с чужаками только при крайней необходимости. Представителям других рас редко разрешается постоянное проживание на эльфийском континенте и практически воспрещается даже просто посещать Эл — испещрённую каналами столицу Совириана, управляемую Великими Домами, каждый из которых контролирует один из районов. Впрочем, правители города регулярно нарушают пограничный режим: для выполнения тайных и трудных поручений обычно привлекаются иноzemцы.</p><p>Бескрайние нетронутые просторы Кастровеля часто поражают гостей, привыкших к передовым технологиям лашунт, хотя для многих граждан этого мира богатства природы служит показателем просвещённого общества. Они считают, что поддержание природной среды необходимо для научного развития и продолжения эволюции, подарившей сознание им самим. Обильные ресурсы планеты должны добываться умеренно и возобновляться — так будет лучше для всех. Во имя этой цели и главные судоверфи, и наиболее вредные производства были перенесены на спутник планеты, Элиндрэ, безводную каменистую луну. Конечно, различные виды опасной мегафауны, от горных угрей с фасеточными глазами до ядовитых неболовов, продолжают нападать на поселения Кастровеля, что порой приводит к «случайным» пожарам, выжигающим удалённые заповедники, но бдительность Ксеностражей чаще всего не позволяет ситуации выйти из-под контроля.</p>',11)])])}const m=n(s,[["render",a]]);export{d as __pageData,m as default};
