import{_ as n,c as r,o as _,ah as o}from"./chunks/framework.MgE-7MWY.js";const d=JSON.parse('{"title":"СТАНЦИЯ «АВЕССАЛОМ»","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Миры Соглашения/Станция Авессалом.md","filePath":"content/Миры игры/Миры Соглашения/Станция Авессалом.md"}'),e={name:"content/Миры игры/Миры Соглашения/Станция Авессалом.md"};function s(a,t,p,i,l,c){return _(),r("div",null,[...t[0]||(t[0]=[o('<h1 id="станция-«авессалом»" tabindex="-1">СТАНЦИЯ «АВЕССАЛОМ» <a class="header-anchor" href="#станция-«авессалом»" aria-label="Permalink to “СТАНЦИЯ «АВЕССАЛОМ»”">​</a></h1><p><strong>СВЯЗУЮЩЕЕ ЗВЕНО</strong></p><ul><li><strong>Диаметр:</strong> 5 миль</li><li><strong>Масса:</strong> ×1/100</li><li><strong>Гравитация:</strong> ×1 (искусственная)</li><li><strong>Атмосфера:</strong> обычная</li><li><strong>День:</strong> 1 день (искусственный)</li><li><strong>Год:</strong> 1 год</li></ul><p>Станция «Авессалом» движется по прежней орбите Голариона и является главным пристанищем человечества, а также административным и торговым центром системы. Именно отсюда Совет Соглашения и его многочисленные делегаты со всех признанных миров-участников поддерживают хрупкое равновесие в системе и регулируют права собственности на новые открытые миры. Здесь же располагается штаб межпланетных сил охраны порядка — Блюстителей, головной офис корпорации «Абадар» на Материальном плане, архивы Звёздных искателей — отважных исследователей Вселенной и многое другое. Самой важной отличительной чертой космической станции является не её величественная и загадочная архитектура и не огромное количество проживающих на ней существ, а странный артефакт, находящийся под несущей охраной в самом сердце станции — легендарный Звёздный Камень. Он не только питает «Авессалом» энергией, но и служит мощнейшим гиперпространственным маяком — и даже жрецы Триединого не знают почему.</p><p>Звёздный Камень позволяет звездолётам быстро перемещаться к нему с любого расстояния, что сделало станцию удобным перевалочным пунктом для путешественников, возвращающихся из-за пределов солнечной системы.</p><p>Никто не ведает, кем была построена эта огромная станция и как она заняла орбиту Голариона, хотя точно известно, что её название связано с далёкой историей пропавшего мира. Искусственная гравитация создаёт постоянную силу тяжести в большинстве радиальных модулей станции и её высоких башен, и, если не учитывать отдельные районы, переоборудованные для нужд инопланетных граждан, большая её часть словно создана для того, чтобы обеспечивать комфорт людей. С другой стороны, соседствующие районы часто радикально отличаются культурой и условиями жизни, начиная с захудалых инженерных отсеков Искр и жилых резервуаров Затона, заканчивая лихими космическими барами Потокового тупика и автономными анклавами корпораций в Синей башне. В общем и целом, все деньги и власть сосредотачиваются вокруг огромного паркового купола, называемого Оком, тогда как бедняки прозывают на отшибе, в тесных, забитых механизмами трущобах Шипа. Неблагополучными районами (Железной, Сливом и Трубопроводом) заправляют банды, влияние которых достигает высших кругов правительства через Гильдию синдиков. Это законодательный орган, который даёт каждому району право голоса в политике станции и избирает главного управляющего (или главу).</p><p>«Авессалом» окружён роем непристыкованных кораблей, находящихся здесь на временных или постоянных условиях. Они насмешливо именуются Армадой, хотя большинство из них не военные (охраняемый флот самой станции достаточно скромен, а в вопросах безопасности она полагается на Блюстителей и мощные стационарные орудия). Армада — преимущественно корабли, принадлежащие тем, кто хочет пользоваться всеми благами жизни рядом со станцией, не обременяя себя при этом большинством её законов. Уступчивое правительство станции «Авессалом» смотрит на это сквозь пальцы, поскольку с существующими объёмами торговли даже чрезвычайно низких налогов достаточно для процветания.</p><p>Всё вышеописанное отнюдь не означает, что на станции царит полная гармония — из-за огромного количества противоречивых интересов обитателей «Авессалом» постоянно находится на грани хаоса.</p><p>Частные охранные компании ведут тайные войны с уличными бандами и группировками боевиков, инопланетные послы заключают непростые торговые соглашения, а исследователи с оружием отстаивают права на новые открытые миры. Одним из самых опасных объединений является недавно появившееся движение «За сильный Авессалом». Эта группировка убеждена, что Звёздный Камень принадлежит только расам Голариона как компенсация за утраченный родной мир, а другие либо должны платить непомерный налог, либо им вовсе должно быть запрещено пользоваться станцией как перевалочным пунктом. И хотя политическое крыло этого движения официально порицает ксенофобный терроризм её экстремистских частей, растущая сила группировки несёт в себе смертельную угрозу для правительства, основанного на межпланетном сотрудничестве.</p><p>Основные плюсы «Авессалома» — развитая торговля и политика, а также атмосфера равных возможностей, но есть и множество других. Арканамирум обучает одних из лучших техномантов в системе, а в прославленном Космонастыре можно постичь путь солариона. Религиозные организации ищут новых последователей, агентство «Открытое окно» предоставляет в найём детективов-экстрасенсов, общество Звёздных искателей снаряжает экспедиции — продолжать перечисление можно долго.</p><p>Эта бесконечная суета — признак того, что станция «Авессалом» — территория возможностей, где существа всех сортов получают шанс начать всё сначала.</p>',11)])])}const m=n(e,[["render",s]]);export{d as __pageData,m as default};
