import{_,c as e,o as a,ah as n}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"АУГМЕНТИРОВАННЫЕ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Фракции и организации/Аугментированные.md","filePath":"content/Миры игры/Фракции и организации/Аугментированные.md"}'),r={name:"content/Миры игры/Фракции и организации/Аугментированные.md"};function o(s,t,p,i,l,c){return a(),e("div",null,[...t[0]||(t[0]=[n('<h1 id="аугментированные" tabindex="-1">АУГМЕНТИРОВАННЫЕ <a class="header-anchor" href="#аугментированные" aria-label="Permalink to “АУГМЕНТИРОВАННЫЕ”">​</a></h1><p><strong>КИБЕРНЕТИЧЕСКИЕ АКТИВИСТЫ</strong></p><ul><li><strong>Руководство:</strong> Совет Совершенных</li><li><strong>Штаб-квартира:</strong> Витые башни, Кашак, Верчес</li></ul><p>Будучи скорее общественным движением, нежели организацией, Аугментированные впервые появились на Верчесе и посвятили себя совершенствованию тел с помощью механических модификаций и вживления имплантатов. По мере развития кибернетики и сообщения между мирами эта философия быстро распространилась по всему Верчесу и за его пределами, и представители всех рас стали улучшать тела с помощью технологий. В наши дни использование простейших киберимплантов не вызывает споров в мирах Соглашения, однако Аугментированные пошли ещё дальше и высказываются в защиту тех, кто бросает вызов экстремальным модификациям, и тех, кто верит, что за подобными изменениями стоит будущая эволюция.</p><p>Верчес остаётся центром сообщества Аугментированных, особенно это касается государства Кашак, которое первым публично осудило традиционную кастовую систему верчанов и стало проводить технологическую модификацию для всех. Хотя и в целом Верчес является лидером в разработке и производстве кибернетики, частным лабораториям и университетам столицы Кашака, Набокона, нет равных. Они привлекают самых лучших техномантов миров Соглашения для работы в таких компаниях, как корпорация «Эверлайф адаптэйшн», «Гаруспик интерпланетари», и «Спеллсайт кооперэйтив». Здесь господствует Совет Совершенных, который считается рупором движения Аугментированных и оказывает давление на правительство и корпорации из Витых башен — трёх круглых, подобных щупальцам шпилей, переплетающихся в тройную спираль. Хотя члены этого объединения намерено прикованы к зданию (их тела получили бесконечно благодаря медицинскому оборудованию, а мозги постоянно анализируют инфосферу планеты), их жукообразные дроны и живые агенты перемещаются по всей галактике, выполняя поручения и влияя на высокопоставленных правительственных чиновников. Многие из этих агентов и курьеров — андроиды, поскольку искусственные существа вызывают благоговение у последователей Совета, ведь они так близки к идеалу Аугментированных.</p><p>Несмотря на то, что Совет проповедует принятие и понимание народа с продвижением технологий, не все аугментированные придерживаются таких же широких взглядов. Технотеррористы из группировок «Преобразователи» и «Шаг вперёд» считают, что все «исходники» — то есть полностью биологические граждане — значительно тормозят прогресс. Они также уверены, что слоган «На эволюцию согласия не нужно» оправдывает заражение воды нанороботами, а также похищение и насильственную аугментацию известных личностей. Забавно, что Совет, зная о ненависти, которую подобные группировки испытывают против «исходников», является их главным противником. Обе стороны уже на протяжении нескольких поколений ведут тайную высокотехнологичную войну, причём Совет регулярно нанимает независимых третьих лиц для выслеживания и устранения тех, кто готов убивать без разбора во имя прогресса.</p><p>Хотя Аугментированные традиционно предпочитают компьютеризированные или механические импланты, большинство членов сообщества уверено, что окончательное совершенство значит больше, чем конкретный механизм, поэтому в последние годы резко возросло количество установивших биоимпланты или полностью магические улучшения. Они продолжают причислять себя к Аугментированным и принимают участие в управлении движением. Ксеностражи особенно укрепили позиции в этом сообществе, убеждая всех, что аугментация может снизить негативное воздействие на окружающую среду. Барату с Бреседы также по непонятным причинам стремятся участвовать в собраниях Аугментированных. Многие прислушиваются к мнению новых членов, но некоторые беспокоятся, что «свежая кровь» может сместить внимание группы в новом неудобном направлении.</p>',7)])])}const f=_(r,[["render",o]]);export{m as __pageData,f as default};
