import{_ as e,c as a,o as n,ah as _}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"БЛЮСТИТЕЛИ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Фракции и организации/Блюстители.md","filePath":"content/Миры игры/Фракции и организации/Блюстители.md"}'),r={name:"content/Миры игры/Фракции и организации/Блюстители.md"};function o(s,t,p,i,l,c){return n(),a("div",null,[...t[0]||(t[0]=[_('<h1 id="блюстители" tabindex="-1">БЛЮСТИТЕЛИ <a class="header-anchor" href="#блюстители" aria-label="Permalink to “БЛЮСТИТЕЛИ”">​</a></h1><p><strong>МИРОТВОРЦЫ И ДИПЛОМАТЫ</strong></p><ul><li><strong>Руководство:</strong> командующий Лин Камулан</li><li><strong>Штаб-квартира:</strong> Бастион, станция «Авессалом»</li></ul><p>Блюстители были образованы тысячелетия назад на Верчесе как неправительственная миротворческая организация, целью которой была защита Кольца Наций и обеспечение лояльности отдельных стран к союзу. С подписанием Авессаломского соглашения, связавшего миры солнечной системы воедино, Блюстители увидели призвание в большем и посвятили себя новой цели, переместив штаб-квартиру на станцию «Авессалом».</p><p>Блюстители действуют как элитные боевые подразделения и дипломаты, чьей задачей является поддержание хрупкого союза, связывающего миры Соглашения. Когда новые Блюстители приносят присягу, они отказываются от верности своим бывшим организациям или родным мирам, посвящая жизнь защите Соглашения и его граждан. Что именно подразумевается под защитой, решает командующий и Конклав легатов, но это может быть что угодно, от патрулирования нейтральных областей космоса и содействия торговым переговорам до силового подавления начинающихся военных конфликтов (и, по слухам, даже тайных убийств). Далеко не все рады вмешательству Блюстителей, но в целом их благородный нейтралитет считается достойным уважения, и по рядовым ми Блюстителями восхищаются за профессионализм и высокий моральный облик.</p><p>Хотя их штаб-квартира, суровая цитадель Бастион, расположена на станции «Авессалом», организация сохраняет полную независимость от руководства станции и формально неподконтрольна даже Совету Соглашения. На самом деле Блюстители почти всегда следуют решениям Совета, за исключением крайне редких случаев, однако стоит лишь чиновникам отдать приказ, противоречащий букве закона или принципам Соглашения — и лидеры Блюстителей будут вправе не повиноваться.</p><p>Иерархия Блюстителей построена по военному образцу, однако они часто действуют в одиночку или малыми группами. Согласно тем же принципам, организация обладает внушительным флотом, но не держит больших гарнизонов на планетах, причиной этому служат как профессионализм самих Блюстителей, так и их статус скорее советников, нежели оккупантов, а большинство военных действий в системе ведутся государствами, которым принадлежит территория, где возник конфликт. Ещё более весомой причиной является Тяга, сеть связанных между собой межпланетных врат, расположенных на каждой базе Блюстителей, чья совершенно секретная технология позволяет мгновенно переместить военные силы туда, где они больше всего нужны. Тем не менее Блюстители применяют насилие только в качестве крайней меры, ведь достижение мира путём войны противоречит самим принципам их мировоззрения. Отказ Блюстителей выходить за рамки полномочий является их гордостью и слабостью одновременно, ведь преступники легко могут скрыться от них за пределами системы или на территории, подведомственной планетарному правительству, вынуждая Блюстителей прибегать к услугам независимых охотников за головами или организаций, подобных Рыцарям Голариона или Легиону Небесного пламени. К счастью, Блюстители могут трактовать законы достаточно вольно и порой планируют хитроумные операции, чтобы заманить нарушителей обратно в пространство миров Соглашения. Сержанты-инструкторы в бастионе часто напоминают новичкам, что, хотя Блюстители должны быть самоотверженными и неколебимо преданными службе на благо Соглашения, это совершенно не значит, что они не должны при этом думать головой.</p>',7)])])}const f=e(r,[["render",o]]);export{m as __pageData,f as default};
