import{_,c as e,o as a,ah as n}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"ВОЛЬНЫЕ КАПИТАНЫ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Фракции и организации/Вольные капитаны.md","filePath":"content/Миры игры/Фракции и организации/Вольные капитаны.md"}'),r={name:"content/Миры игры/Фракции и организации/Вольные капитаны.md"};function l(o,t,i,p,s,c){return a(),e("div",null,[...t[0]||(t[0]=[n('<h1 id="вольные-капитаны" tabindex="-1">ВОЛЬНЫЕ КАПИТАНЫ <a class="header-anchor" href="#вольные-капитаны" aria-label="Permalink to “ВОЛЬНЫЕ КАПИТАНЫ”">​</a></h1><p><strong>КОАЛИЦИЯ ПРЕСТУПНИКОВ</strong></p><ul><li><strong>Руководство:</strong> Пиратский совет</li><li><strong>Штаб-квартира:</strong> Расколотая Скала, Диаспора</li></ul><p>Капитану торгового судна, у которого украли груз или угнали звездолёт, пираты миров Соглашения могут показаться разрозненными кучками отягчённых мерзавцев. На самом деле многие флибустьеры, промышляющие разбоем в просторах космоса, придерживаются своеобразного кодекса чести или по меньшей мере убеждены, что у них друг с другом больше общего, чем с кораблями и экипажами, на которые они нападают. Их называют Вольными капитанами, и хотя не каждый разбойник, атакующий мирный корабль в нейтральном космосе, является одним из них, наиболее успешные и печально известные в мирах Соглашения пираты состоят в этой разношёрстной организации.</p><p>Преступная ассоциация состоит из независимых капитанов звездолётов, занимающихся мародёрством, пиратством, грабежами, контрабандой и прочей противозаконной деятельностью. Считается, что Вольными капитанами управляет совет из избранных пиратов, но что действительно связывает этих непохожих индивидов — так это кодекс, которому они следуют. Именно в нём говорится, кто может быть ограблен, чей корабль может быть угнан или уничтожен, и, самое главное, на кого нападать ни в коем случае нельзя.</p><p>Правила кодекса могут оказаться необоснованными или причудливыми, но сам факт их наличия жизненно необходим для слаженной работы, ведь в случае войны всех против всех окончательная победа достанется Блюстителям, которые без труда захватят выживших. Правила кодекса могут время от времени меняться в зависимости от причуд совета, однако некоторые положения остаются незыблемыми:</p><ul><li>Вольный капитан может напасть на другого Вольного капитана только во время одобренного советом поединка.</li><li>Клиенты, заплатившие Пиратскому совету высокую цену за «защиту», должны быть в полной безопасности от несанкционированных нападений.</li><li>Уговор есть уговор, и если Вольный капитан заключил сделку, он обязан исполнить обязательства. Это особенно важно, когда пираты предлагают сохранить жертве жизнь в обмен на груз, — те могут быть уверены, что пираты после этого не убьют их.</li><li>Все члены экипажа вольны покинуть судно в порту, но в полёте они обязаны головы сложить за капитана, если тот так прикажет.</li></ul><p>Вольные капитаны базируются и в мирах Соглашения, и за их пределами, но центр пиратской вольницы расположен в Диаспоре, поскольку россыпь разбросанных астероидов, космических глыб и планетезималей надёжно скрывает от закона бесчисленные стыковочные станции, порты и безопасные гавани для дезертиров, изгнанников и пиратов.</p><p>Пиратский совет Вольных капитанов собирается на астероиде под названием Расколотая Скала, и хотя его точное местоположение хранится в строжайшей тайне, говорят, что он прекрасно охраняется во избежание попыток Блюстителей или планетарной полиции помешать противозаконным деяниям Вольных капитанов.</p><p>Общих целей у Вольных капитанов нет, каждый желает лишь разбогатеть, но, по данным пиратов, которые получили планетарные правительства, из-за возросшего влияния и военной мощи Вольных капитанов на Диаспоре зарождается некое «пиратское королевство».</p>',10)])])}const f=_(r,[["render",l]]);export{m as __pageData,f as default};
