import{_,c as e,o as a,ah as n}from"./chunks/framework.MgE-7MWY.js";const m=JSON.parse('{"title":"КОРПОРАЦИЯ «АБАДАР»","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Фракции и организации/Корпорация Абадар.md","filePath":"content/Миры игры/Фракции и организации/Корпорация Абадар.md"}'),r={name:"content/Миры игры/Фракции и организации/Корпорация Абадар.md"};function o(s,t,i,l,p,c){return a(),e("div",null,[...t[0]||(t[0]=[n('<h1 id="корпорация-«абадар»" tabindex="-1">КОРПОРАЦИЯ «АБАДАР» <a class="header-anchor" href="#корпорация-«абадар»" aria-label="Permalink to “КОРПОРАЦИЯ «АБАДАР»”">​</a></h1><p><strong>КРУПНАЯ РЕЛИГИОЗНАЯ КОРПОРАЦИЯ</strong></p><ul><li><strong>Руководство:</strong> президент и исполнительный архидиректор Геврант Исеки-Окайбо</li><li><strong>Штаб-квартира:</strong> Золотое Хранилище, станция «Авессалом»</li></ul><p>Корпорация «Абадар» — крупнейшая, богатейшая и влиятельнейшая в мирах Соглашения во многом благодаря тому, что это ещё и церковь Абадара, бога цивилизации, торговли и благосостояния. Помимо производства, транспортировки и продажи широкого ассортимента технологических и магических товаров, корпорация «Абадар» предлагает множество других услуг: банковские, финансовые, юридические, коммунальные и транспортные. Её корпоративные офисы, фабрики, склады, торговые форпосты (все которые к тому же действуют как храмы «Абадара») находятся в каждом из миров Соглашения и на почти всех цивилизованных планетах, поддерживающих торговые отношения с системой. Практически всё, что можно купить в мирах Соглашения или дружественных системах, продаётся, а часто и производится корпорацией «Абадар» или одной из её дочерних компаний. Нельзя сказать, что товары корпорации отличаются превосходным качеством или что у них нет достойных конкурентов. Кое-кто может утверждать, что лазерная винтовка фирмы «Арабини армс» лучше фокусирует луч, чем аналогичная модель, производимая корпорацией «Абадар», но все знают, что на полках ближайшего гипермаркета корпорации «Абадар», вероятнее всего, можно найти обе.</p><p>Корпорация «Абадар» преследует те же цели, что и церковь Абадара: распространение цивилизации и закона, обеспечение свободной торговли и получение прибыли. Не являясь органом правопорядка сама по себе, эта корпорация обеспечивает оснащение, финансирование и обучение и Блюстителей, и более мелких планетарных сил охраны порядка. Корпорация активно спонсирует колониальные экспедиции и предоставляет всё, от финансовой помощи до колониальных кораблей, строительного оборудования, припасов и даже готовых для сборки зданий (включая, разумеется, набитый до отказа магазин корпорации «Абадар» — отличное франшизное предложение). Кроме того, корпорация сыграла значительную роль, содействуя правительству миров Соглашения при установлении кредитного стандарта, а её функции по валютному регулированию и осуществлению межпланетных денежных переводов фактически делают её частью казначейства, что устраивает далеко не всех граждан.</p><p>Корпорация имеет стандартную для крупной компании организационную структуру, сохраняя в то же время религиозные функции. Все директора и управляющие корпорации — священнослужители Абадара. Президент корпорации является первосвященником, называемым исполнительным архидиректором. Деятельность корпорации на каждой планете (или значимом объекте, например на станции «Абессалом») контролирует отдельный архидиректор, входящий в совет директоров корпорации. Более низкие по должности директора управляют крупными активами компании, а менеджеры и администраторы несут ответственность за более мелкие владения и офисы. Помимо менеджеров-священнослужителей, в корпорации «Абадар» трудятся многочисленные светские работники, занятые в самых разных областях, включая поиск новых рынков сбыта и ресурсов, пилотирование, исследовательскую работу, безопасность, транспорт и многое другое. Несмотря на то, что у корпорации достаточно лояльных постоянных сотрудников, она не стесняется привлекать людей со стороны, если для выполнения задачи требуются особые навыки или готовность рискнуть жизнью.</p>',6)])])}const f=_(r,[["render",o]]);export{m as __pageData,f as default};
