import{_ as e,c as t,o as r,ah as n}from"./chunks/framework.MgE-7MWY.js";const h=JSON.parse('{"title":"КСЕНОСТРАЖИ","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Фракции и организации/Ксеностражи.md","filePath":"content/Миры игры/Фракции и организации/Ксеностражи.md"}'),_={name:"content/Миры игры/Фракции и организации/Ксеностражи.md"};function o(p,a,s,c,i,d){return r(),t("div",null,[...a[0]||(a[0]=[n('<h1 id="ксеностражи" tabindex="-1">КСЕНОСТРАЖИ <a class="header-anchor" href="#ксеностражи" aria-label="Permalink to “КСЕНОСТРАЖИ”">​</a></h1><h2 id="хранители-окружающеи-среды" tabindex="-1">ХРАНИТЕЛИ ОКРУЖАЮЩЕЙ СРЕДЫ <a class="header-anchor" href="#хранители-окружающеи-среды" aria-label="Permalink to “ХРАНИТЕЛИ ОКРУЖАЮЩЕЙ СРЕДЫ”">​</a></h2><p><strong>Руководство:</strong> глас природы Алька Габрио<br><strong>Штаб-квартира:</strong> ковчег «Эквилибриум»</p><hr><p>После открытия дешёвой технологии межзвёздных путешествий были исследованы и освоены буквально миллиарды новых миров, но не все считают это благом.</p><p>По мнению Ксеностражей, каждый мир, на котором теплится искра жизни, уникален: это и научная загадка, которую необходимо разгадать, и священное чудо, достойное благоговения. На их взгляд, бесконтрольная экспансия и освоение галактики в погоне за быстрым обогащением или с целью дальнейшего разрастания городов является ужасным кощунством, которому необходимо противостоять.</p><p>Считается, что Ксеностражи появились как орден друидов на Кастровеле на заре эры космических полётов и сейчас являются собой странную смесь существ, объединённых общей целью, которых направляет Круг Хранителей. Многие из Ксеностражей — мистики, черпающие силу от самой природы ради защиты священной неприкосновенности диких мест, другие — учёные, которые видят в изучении инопланетных форм жизни шанс значительно продвинуть науку вперёд и совершить новые открытия в области медицины и опасаются экологических последствий бездумной разработки недр и операций по сбору биологических материалов. Есть среди них и профессиональные солдаты — борцы за сохранение окружающей среды, готовые отдать жизнь, чтобы заставить цивилизацию гармонично сосуществовать с хрупкими экосистемами. Ксеностражи патрулируют космическое пространство на живых кораблях-ковчегах, скорее выращенных, чем построенных, и спроектированных так, чтобы сократить использование невозобновляемых ресурсов. Более того, ковчеги частично разумны и поэтому способны осознанно выполнять поступающие от пилотов приказы.</p><p>Ксеностражи не пренебрегают вооружёнными столкновениями — их элитные отряды, Зелёные стражи и Рыцари природы, крайне опасны: симбиотические истребители и биологические силовые доспехи заставляют с ними считаться. Вместе с тем они осознают, что цивилизация является естественной ступенью эволюции и частью природного баланса. Политическое влияние Ксеностражей обусловлено в большей степени экономическими причинами. Их исследования в области биотехнологий и возобновляемых источников энергии, от созданных на заказ бактерий до электростанций на основе морских водорослей, сопоставимы разве что с разработками бреседиан, а их прорывы в области медицины и других наук, сделанные благодаря изучению биологии иных планет, приносят пользу всем мирам Соглашения.</p><p>Разумеется, Ксеностражи не могут отследить каждый корабль, проходящий через Поток, но они часто используют политическое влияние и внедряют своих членов в корпорации в качестве советников или наблюдателей. Кроме того, они посылают представителей к недавно открытым мирам, чтобы проследить за их развитием и убедиться, что колонисты аккуратно относятся к местным экосистемам.</p><p>Большинство Ксеностражей действует независимо, спокойно пресекая нарушения и убеждая неверующих в преимуществах жизни в гармонии с другими организмами. К сожалению, не все, кто разделяет идеалы Ксеностражей, так же благоразумны, как их правящий совет. Известная теория заговора обвиняет ксеностражей-отщепенцев в распространении звёздной чумы — смертельного патогена, занесённого первыми межзвёздными исследователями, который, предположительно, являлся частью плана по препятствию межзвёздной колонизации.</p><p>В организации Ксеностражей все равны, и даже недавно принятые члены вольны высказывать своё мнение. Их лидер, глас природы, выбирается путём всеобщего одобрения из числа капитанов кораблей-ковчегов, но далее его оставляют безоружным на пригодной для жизни, но совершенно дикой и ранее неизученной планете. В случае если он сможет там выжить в течение целого года без нанесения существенного вреда местной экосистеме, он займёт эту руководящую должность. На посту глас природы советуется не только с коллегами, но и с экстра-сенсорными созданиями, айхорами. Эти существа размером примерно с мышь и чуть умнее собак, однако наделены особым даром или проклятием — способностью отвечать на вопросы о будущем с обескураживающей честностью. Местоположение родного мира айхоров держится в строжайшем секрете, чтобы больше никто и никогда не смог их эксплуатировать.</p><hr>',12)])])}const m=e(_,[["render",o]]);export{h as __pageData,m as default};
