import{_,c as e,o as a,ah as r}from"./chunks/framework.MgE-7MWY.js";const h=JSON.parse('{"title":"РЫЦАРИ ГОЛАРИОНА","description":"","frontmatter":{},"headers":[],"relativePath":"content/Миры игры/Фракции и организации/Рыцари Голариона.md","filePath":"content/Миры игры/Фракции и организации/Рыцари Голариона.md"}'),n={name:"content/Миры игры/Фракции и организации/Рыцари Голариона.md"};function o(p,t,s,c,i,d){return a(),e("div",null,[...t[0]||(t[0]=[r('<h1 id="рыцари-голариона" tabindex="-1">РЫЦАРИ ГОЛАРИОНА <a class="header-anchor" href="#рыцари-голариона" aria-label="Permalink to “РЫЦАРИ ГОЛАРИОНА”">​</a></h1><p>**КРЕСТОНОСЦЫ</p><p><strong>Руководство:</strong> рыцарь-генерал Халайна Лимикин<br><strong>Штаб-квартира:</strong> флагман «Клинок отваги»</p><hr><p>Рыцари Голариона — это военная организация, прочно связанная с мирами Соглашения, церковью Иомедэй и выходцами с пропавшей планеты Голарион. Хотя поклонение богине Иомедэй не обязательно для вступления в ряды и некоторые члены общества даже почитают других богов, все Рыцари приносят священную клятву читать Кодекс Наследницы — обет, состоящий из 10 частей и требующий смелости, справедливого обращения с военнопленными, благородства и сдержанности в действиях. Им также следует постоянно изучать священный текст «Деяния Иомедэй».</p><p>Рыцари Голариона призваны нести справедливость и праведность во всех их проявлениях, распространяя дарованное богиней понятие морали по всем мирам Соглашения, поселения на дальних рубежах и беззаконным территориям. Для многих Рыцари являются классическими героями, готовыми пожертвовать собой ради спасения невинных, защищая их от беснующихся чудовищ или от посягательств бандитов и тиранов, воплощающими идеалы военного братства и готовыми умереть за правое дело. В отличие от Блюстителей, Рыцари не соблюдают местные законы, если не считают их справедливыми. Они следуют законам Небес, а любой, кто нарушает их, не заслуживает снисхождения. Они глубоко ценят закон и порядок, но не терпят тех, кто использует их для притеснения других или связывается со злом — например, дьяволами или нежитью. Из-за этого работать с ними бывает непросто, и хотя Рыцари не раз защищали миры Соглашения, Блюстители прилагают все усилия, чтобы направлять их в сражения, где политика не замешана. Они борются с Флотом мертвецов, вторжениями демонов или с проблемами в мирах за пределами системы.</p><p>У Рыцарей Голариона нет постоянной штаб-квартиры ни на одной из планет, они используют флот кораблей-соборов класса «Мститель» в качестве мобильных баз.</p><p>Рыцари Голариона тесно сотрудничают с церковью Иомедэй, в отличие от корпорации «Абадар», они чётко разделяют орден и церковь. Таким образом, Рыцари вольны бороться с возникающими угрозами быстро и тем способом, который считают наилучшим, без вовлечения руководителей церкви в принятие каждого решения. Связанные клятвой защищать всех невинных, Рыцари чтят свою богиню как покровительницу всех рас, бежавших с Голариона, особенно человечества. В качестве финансовой поддержки им поступают небольшие суммы от колоний, компаний и других организаций, которым Рыцари оказали помощь в прошлом, но это всего лишь дары, а не плата за службу — ведь Рыцари Голариона не служат никому.</p><p>Рыцари Голариона сочетают военную структуру и чины со старинными традициями рыцарства и верности сюзерену. Рядовые солдаты действуют под командованием рыцарей-офицеров, но рыцарям-лейтенантам и более высоким по званию часто доверяют самостоятельно сражаться против несправедливости, призвав под свои знамёна каждого, кто откликнется на зов.</p><hr>',10)])])}const m=_(n,[["render",o]]);export{h as __pageData,m as default};
